import axios from 'axios'; // Dodan axios za API pozive
import { useAuth } from '../contexts/AuthContext'; // Pretpostavka za dohvaćanje tokena
import 'bootstrap/dist/css/bootstrap.min.css'; // [cite: 1]
// // Ensure Bootstrap CSS is imported
import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:8081/api'; // Prilagodite ako je potrebno

function AdminDashboard() {
  const { token } = useAuth(); // Dohvaćanje tokena za autentifikaciju API poziva

  // Konfiguracija zaglavlja za axios koja uključuje token
  const authHeaders = {
    headers: {
      'Authorization': `Bearer ${token}`
    }
  };

  // State to manage the active tab in the dashboard
