            <button // [cite: 180]
              className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'doctors' ? 'active bg-secondary rounded' : ''}`} // [cite: 180]
              onClick={() => { setActiveTab('doctors'); setDoctorsCurrentPage(1); }} // [cite: 180, 181]
            >
              Doctors
            </button>
          </li>
          <li className="nav-item mb-2"> {/* [cite: 181] */}
            <button
              className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'patients' ? 'active bg-secondary rounded' : ''}`} // [cite: 182]
              onClick={() => { setActiveTab('patients'); setPatientsCurrentPage(1); }} // [cite: 182, 183]
            >
              Patients
            </button>
          </li>
          <li className="nav-item mb-2"> {/* [cite: 183] */}
            <button
              className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'appointments' ? 'active bg-secondary rounded' : ''}`} // [cite: 184]
              onClick={() => { setActiveTab('appointments'); setAppointmentsCurrentPage(1); }} // [cite: 184, 185]
            >
              Appointments
            </button>
          </li>
          <li className="nav-item mb-2"> {/* [cite: 185] */}
            <button
              className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'users' ? 'active bg-secondary rounded' : ''}`} // [cite: 186]
              onClick={() => { setActiveTab('users'); setUsersCurrentPage(1); }} // [cite: 186, 187]
            >
              User Management
            </button>
          </li>
          <li className="nav-item mb-2"> {/* [cite: 187] */}
            <button
              className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'content' ? 'active bg-secondary rounded' : ''}`} // [cite: 188]
              onClick={() => { setActiveTab('content'); setBlogPostsCurrentPage(1); }} // [cite: 188, 189]
            >
              Content Management
            </button>
          </li>
          <li className="nav-item mb-2"> {/* [cite: 189] */}
            <button
              className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'analytics' ? 'active bg-secondary rounded' : ''}`} // [cite: 190]
              onClick={() => setActiveTab('analytics')} // [cite: 190]
            >
              Analytics & Reports
            </button>
          </li>
          <li className="nav-item mb-2"> {/* [cite: 190] */}
            <button
              className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'settings' ? 'active bg-secondary rounded' : ''}`} // [cite: 191, 192]
              onClick={() => setActiveTab('settings')} // [cite: 192]
            >
              Settings
            </button>
          </li>
        </ul>
      </div>

      {/* Main Content Area */}
      <div className="flex-grow-1 p-4 bg-light"> {/* [cite: 193] */}
        {showAlert && ( /* [cite: 193] */
          <div className={`alert alert-${alertType} alert-dismissible fade show position-fixed top-0 start-50 translate-middle-x mt-3`} role="alert" style={{ zIndex: 1050 }}> {/* [cite: 193] */}
            {alertMessage} {/* [cite: 193] */}
            <button type="button" className="btn-close" onClick={() => setShowAlert(false)}></button> {/* [cite: 193] */}
          </div>
        )}

        <div className="d-flex justify-content-between align-items-center mb-4"> {/* [cite: 193] */}
          <h2 className="mb-0">Dashboard Overview</h2> {/* [cite: 194] */}
          {/* You can add user info or logout button here */}
          <button className="btn btn-outline-danger">Logout</button> {/* [cite: 194] */}
        </div>
        <hr /> {/* [cite: 194] */}
        {renderContent()} {/* Render content based on active tab */} {/* [cite: 194] */}
      </div>
    </div>
  ); // [cite: 195]
}

export default AdminDashboard; // [cite: 195]
