// frontend/src/components/LoginPage.jsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Dodaj useNavigate
import axios from 'axios'; // Importuj Axios
import 'bootstrap/dist/css/bootstrap.min.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginMessage, setLoginMessage] = useState('');
  const navigate = useNavigate(); // Inicijalizuj useNavigate

  const handleSubmit = async (e) => { // Promijeni u async funkciju
    e.preventDefault();
    setLoginMessage(''); // Očisti prethodnu poruku

    try {
      // Backend očekuje { email, password }
      const response = await axios.post('http://localhost:8081/auth/login', { // Koristi URL API Gatewaya
        email: email,
        password: password
      });

      if (response.status === 200) {
        const { token, email, fullName, roles } = response.data;
        // Uspješna prijava! Pohrani token i ostale podatke
        localStorage.setItem('jwtToken', token);
        localStorage.setItem('userEmail', email);
        localStorage.setItem('userFullName', fullName);
        localStorage.setItem('userRoles', JSON.stringify(roles)); // Pohrani uloge kao string

        setLoginMessage('Uspješna prijava! Preusmjeravam...');
        console.log('Login successful:', response.data);
        // Preusmjeri korisnika na dashboard (prilagodi putanju)
        navigate('/dashboard');
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setLoginMessage('Neispravan email ili lozinka.');
      } else {
        setLoginMessage('Došlo je do greške prilikom prijave. Pokušajte ponovo.');
      }
      console.error('Login error:', error);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center" style={{ minHeight: '80vh' }}>
      <div className="card shadow-lg p-4" style={{ maxWidth: '400px', width: '100%' }}>
        <div className="card-body">
          <h2 className="card-title text-center mb-4 text-danger">Login to WinxCare</h2>
          {loginMessage && (
            <div className={`alert ${loginMessage.includes('Uspješna') ? 'alert-success' : 'alert-danger'} text-center`} role="alert">
              {loginMessage}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="emailInput" className="form-label">Email address</label>
              <input
                type="email"
                className="form-control"
                id="emailInput"
                placeholder="name@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="passwordInput" className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                id="passwordInput"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="d-grid gap-2 mb-3">
              <button type="submit" className="btn btn-danger btn-lg">Login</button>
            </div>
          </form>
          <div className="text-center mt-3">
            <Link to="/forgot-password" className="text-muted text-decoration-none">Forgot Password?</Link>
          </div>
          <div className="text-center mt-2">
            <p className="text-muted">Don't have an account? <Link to="/sign-up" className="text-danger text-decoration-none">Sign Up</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
