import React, { createContext, useContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRoles, setUserRoles] = useState([]);
  const [userEmail, setUserEmail] = useState(null);
  const [fullName, setFullName] = useState(null);
  const [token, setToken] = useState(null);
  // User objekat koji sadrži sve relevantne ID-eve i podatke
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedToken = localStorage.getItem('jwtToken');
    const storedRoles = localStorage.getItem('userRoles');
    const storedEmail = localStorage.getItem('userEmail');
    const storedFullName = localStorage.getItem('fullName');
    const storedDoktorId = localStorage.getItem('doktorId'); // Dohvati doktorId iz localStorage
    const storedPacijentId = localStorage.getItem('pacijentId'); // Dohvati pacijentId iz localStorage

    if (storedToken) {
      try {
        const decodedToken = jwtDecode(storedToken);
        if (decodedToken.exp * 1000 < Date.now()) {
          console.warn("JWT token je istekao. Odjavljujem korisnika.");
          logoutUser();
        } else {
          setToken(storedToken);
          setIsAuthenticated(true);
          try {
            setUserRoles(JSON.parse(storedRoles));
          } catch (e) {
            console.error("Greška prilikom parsiranja userRoles iz localStorage-a", e);
            setUserRoles([]);
          }
          setUserEmail(storedEmail);
          setFullName(storedFullName);

          // Kreiramo user objekat prilikom učitavanja iz localStorage-a
          const userObj = {
            id: decodedToken.sub || null, // 'sub' je često ID korisnika u JWT-u
            email: storedEmail,
            fullName: storedFullName,
            // Parsiraj ID-eve kao brojeve (Integer na backendu = Number na frontendu)
            doktorId: storedDoktorId ? parseInt(storedDoktorId, 10) : null,
            pacijentId: storedPacijentId ? parseInt(storedPacijentId, 10) : null,
          };
          setUser(userObj);
        }
      } catch (error) {
        console.error("Greška prilikom dekodiranja JWT tokena ili učitavanja iz localStorage-a:", error);
        logoutUser();
      }
    }
    setLoading(false);
  }, []);

  const login = (jwtToken, roles, email, name, doktorID, pacijentID) => { // Sada prihvaća doktorID i pacijentID
    localStorage.setItem('jwtToken', jwtToken);
    localStorage.setItem('userRoles', JSON.stringify(roles));
    localStorage.setItem('userEmail', email);
    localStorage.setItem('fullName', name);
    // Spremi ID-eve u localStorage
    if (doktorID !== null && doktorID !== undefined) {
      localStorage.setItem('doktorId', doktorID.toString()); // Spremi kao string
    } else {
      localStorage.removeItem('doktorId');
    }
    if (pacijentID !== null && pacijentID !== undefined) {
      localStorage.setItem('pacijentId', pacijentID.toString()); // Spremi kao string
    } else {
      localStorage.removeItem('pacijentId');
    }

    setToken(jwtToken);
    setIsAuthenticated(true);
    setUserRoles(roles);
    setUserEmail(email);
    setFullName(name);

    // Kreiramo user objekat za React stanje
    const userObj = {
      id: jwtDecode(jwtToken).sub || null, // Koristi 'sub' iz JWT-a kao generički user ID
      email: email,
      fullName: name,
      doktorId: doktorID,
      pacijentId: pacijentID,
    };
    setUser(userObj);
  };

  const logout = () => {
    logoutUser();
  };

  const logoutUser = () => {
    localStorage.removeItem('jwtToken');
    localStorage.removeItem('userRoles');
    localStorage.removeItem('userEmail');
    localStorage.removeItem('fullName');
    localStorage.removeItem('doktorId'); // Ukloni i ove ID-eve
    localStorage.removeItem('pacijentId');
    setToken(null);
    setIsAuthenticated(false);
    setUserRoles([]);
    setUserEmail(null);
    setFullName(null);
    setUser(null);
  };

  const hasRole = (role) => {
    return userRoles.includes(role);
  };

  // Vraćamo `user` objekat
  return (
    <AuthContext.Provider value={{ isAuthenticated, userRoles, userEmail, fullName, token, user, login, logout, hasRole, loading }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  return useContext(AuthContext);
};
