import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect, useMemo } from 'react';
import { Modal, Button, Form, Alert, Spinner, Tab, Tabs } from 'react-bootstrap';
// No need for react-datepicker if your original version doesn't use it.

const GATEWAY_BASE_URL = 'http://localhost:8081';

// Defining API prefixes for each microservice - WE USE THESE PREFIXES!
const KORISNICI_DOKTORI_API_PREFIX = `${GATEWAY_BASE_URL}/api/korisnici-doktori`;
const TERMINI_PREGLEDI_API_PREFIX = `${GATEWAY_BASE_URL}/api/termini-pregledi`;
const OBAVIJESTI_DOKUMENTI_API_PREFIX = `${GATEWAY_BASE_URL}/api/obavijesti-dokumentacija`;
const AUTH_API_PREFIX = `${GATEWAY_BASE_URL}/auth`; // Assumed path for authentication, if used directly

function PatientDashboard() {
    // UPDATED: user object now contains authId (Long), id (email), patientId (Integer)
    const { token, user } = useAuth();
    console.log("Current user object from useAuth (PatientDashboard):", user);

    const authHeaders = useMemo(() => ({
        headers: {
            'Authorization': `Bearer ${token}`
        }
    }), [token]);

    const [activeTab, setActiveTab] = useState('overview');
    const [appointments, setAppointments] = useState([]);
    const [medicalHistory, setMedicalHistory] = useState([]);
    const [patientProfile, setPatientProfile] = useState({});
    const [doctors, setDoctors] = useState([]); // This doctor list should also contain userId
    const [allDoctorAvailableTerms, setAllDoctorAvailableTerms] = useState([]);
    // const [documents, setDocuments] = useState([]); // REMOVED: No longer needed
    const [bills, setBills] = useState([]);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState({ recipientId: '', subject: '', content: '' });
    const [replyMessage, setReplyMessage] = useState({ messageId: '', content: '' });
    const [selectedMessage, setSelectedMessage] = useState(null);

    const [showAlert, setShowAlert] = useState(false);
    const [alertMessage, setAlertMessage] = useState('');
    const [alertType, setAlertType] = useState('success');

    const [showAppointmentModal, setShowAppointmentModal] = useState(false);
    const [newAppointment, setNewAppointment] = useState({
        doctorId: '',
        appointmentDate: '',
        appointmentTime: '',
        terminId: '',
        reason: ''
    });
    const [editingAppointment, setEditingAppointment] = useState(null);

    const [loading, setLoading] = useState(true);

    // State for rating modal
    const [showRatingModal, setShowRatingModal] = useState(false);
    const [selectedDoctorForRating, setSelectedDoctorForRating] = useState(null);
    const [ratingData, setRatingData] = useState({ ocjena: 5, komentar: '' }); // Default rating 5

    // NEW: State for bill details modal
    const [showBillDetailsModal, setShowBillDetailsModal] = useState(false);
    const [selectedBillDetails, setSelectedBillDetails] = useState(null);


    // --- API Calls ---

    const fetchPatientAppointments = async () => {
        if (!user || !user.pacijentId) {
            console.warn("Patient ID not available for fetching appointments.");
            return;
        }
        try {
            const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/zakazani/${user.pacijentId}`, authHeaders);
            const transformedAppointments = response.data.map(app => ({
                id: app.pregledID,
                doktorID: app.doktorID,
                pacijentID: app.pacijentID,
                datumPregleda: app.datumPregleda,
                vrijemePregleda: app.vrijemePregleda,
                komentarPacijenta: app.komentarPacijenta,
                status: app.status,
                terminID: app.terminID,
                doktorIme: app.doktorIme
            }));
            setAppointments(transformedAppointments);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Error fetching patient appointments:', error.response?.data || error.message);
            setAlertMessage('Failed to fetch scheduled appointments.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchMedicalHistory = async () => {
        if (!user || !user.pacijentId) {
            console.warn("Patient ID not available for fetching medical history.");
            return;
        }
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/medicinska-historija/pacijent/${user.pacijentId}`, authHeaders);
            const transformedHistory = response.data.map(record => ({
                id: record.zapisID,
                patientId: record.pacijentID,
                doctorId: record.doktorID,
                doctorName: record.doktorIme,
                date: record.datumZapisivanja,
                diagnosis: record.dijagnoza,
                treatment: record.lijecenje,
                notes: record.napomene
            }));
            setMedicalHistory(transformedHistory);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Error fetching medical history:', error.response?.data || error.message);
            setAlertMessage('Failed to fetch medical history.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchPatientProfile = async () => {
        if (!user || !user.pacijentId) {
            console.warn("Patient ID not available for fetching patient profile.");
            return;
        }
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${user.pacijentId}`, authHeaders);
            setPatientProfile({
                id: response.data.pacijentID,
                firstName: response.data.ime,
                lastName: response.data.prezime,
                email: response.data.email,
                phone: response.data.telefon,
                datumRodjenja: response.data.datumRodjenja,
                adresa: response.data.adresa,
                userId: Number(response.data.userId) // Explicitly convert to Number
            });
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Error fetching patient profile:', error.response?.data || error.message);
            setAlertMessage('Failed to fetch profile data.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchDoctors = async () => {
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori`, authHeaders);
            const transformedDoctors = response.data.map(doc => ({
                id: doc.doktorID,
                userId: Number(doc.userId), // Explicitly convert to Number
                firstName: doc.ime,
                lastName: doc.prezime,
                specialty: Array.isArray(doc.specijalizacije) ? doc.specijalizacije.join(', ') : doc.specijalizacije,
                email: doc.email,
                phone: doc.telefon,
                ocjena: doc.ocjena
            }));
            setDoctors(transformedDoctors);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Error fetching doctors:', error.response?.data || error.message);
            setAlertMessage('Failed to fetch doctor list.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchDoctorAllAvailableTerms = async (doktorId) => {
        if (!doktorId) {
            setAllDoctorAvailableTerms([]);
            return;
        }
        setLoading(true);
        try {
            const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/termini/slobodni/${doktorId}`, authHeaders);
            const termsData = Array.isArray(response.data) ? response.data : [];
            const available = termsData
                .map(term => ({
                    terminID: term.terminID,
                    datum: term.datum,
                    vrijeme: term.vrijeme,
                    display: `${term.datum} ${term.vrijeme.substring(0, 5)}`
                }))
                .sort((a, b) => {
                    const dateTimeA = `${a.datum}T${a.vrijeme}`;
                    const dateTimeB = `${b.datum}T${b.vrijeme}`;
                    return new Date(dateTimeA) - new Date(dateTimeB);
                });
            setAllDoctorAvailableTerms(available);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Error fetching doctor availability:', error.response?.data || error.message);
            setAlertMessage('Failed to fetch doctor availability.');
            setAlertType('danger');
            setShowAlert(true);
            setAllDoctorAvailableTerms([]);
        } finally {
            setLoading(false);
        }
    };

    // REMOVED: fetchDocuments and handleDocumentUpload are no longer part of PatientDashboard
    // const fetchDocuments = async () => { ... }
    // const handleDocumentUpload = async (e) => { ... }

    const fetchBills = async () => {
        if (!user || !user.pacijentId) {
            console.warn("Patient ID not available for fetching bills.");
            return;
        }
        try {
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/racuni/pacijent/${user.pacijentId}`, authHeaders);
            const transformedBills = response.data.map(bill => ({
                id: bill.racunID,
                billDate: bill.datumIzdavanja,
                amount: bill.iznos,
                status: bill.status,
                dueDate: bill.datumDospijeca,
                opis: bill.opis,
                doktorIme: bill.doktorIme
            }));
            setBills(transformedBills);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Error fetching bills:', error.response?.data || error.message);
            setAlertMessage('Failed to fetch billing information.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchMessages = async () => {
        if (!user || !user.userId) { // Use user.userId to fetch messages
            console.warn("User ID not available for fetching messages.");
            return;
        }
        try {
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/konverzacija/${user.userId}?userType=PACIJENT`, authHeaders);
            const transformedMessages = response.data.map(msg => ({
                id: msg.porukaID,
                senderId: Number(msg.senderId), // Explicitly convert to Number
                senderType: msg.senderType,
                senderIme: msg.senderIme,
                receiverId: Number(msg.receiverId), // Explicitly convert to Number
                receiverType: msg.receiverType,
                receiverIme: msg.receiverIme,
                subject: msg.subject,
                content: msg.content,
                timestamp: msg.timestamp,
                replies: msg.replies || [] // Ensure replies is always an array
            }));
            setMessages(transformedMessages);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Error fetching messages:', error.response?.data || error.message);
            setAlertMessage('Failed to fetch messages.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    // --- useEffect Hooks ---
    useEffect(() => {
        const loadAllData = async () => {
            if (token && user) {
                setLoading(true);
                await Promise.all([
                    fetchPatientAppointments(),
                    fetchMedicalHistory(),
                    fetchPatientProfile(),
                    fetchDoctors(),
                    // fetchDocuments(), // REMOVED
                    fetchBills(),
                    fetchMessages()
                ]);
                setLoading(false);
            } else if (!token || (user && !user.pacijentId && !user.doktorId)) {
                setLoading(false);
                if (!token) {
                    console.warn("Token not available. User not logged in.");
                } else {
                    console.warn("User object available, but patientId nor doctorId are. User might be neither patient nor doctor.");
                }
            }
        };
        loadAllData();
    }, [token, user]);


    // --- Appointment Scheduling/Cancellation ---

    const openAppointmentModal = (appointment = null, doctorIdFromSearch = null) => {
        if (appointment) {
            setEditingAppointment(appointment);
            setNewAppointment({
                doctorId: appointment.doktorID,
                appointmentDate: appointment.datumPregleda,
                appointmentTime: appointment.vrijemePregleda,
                terminId: appointment.terminID,
                reason: appointment.komentarPacijenta
            });
            fetchDoctorAllAvailableTerms(appointment.doktorID);
        } else {
            setEditingAppointment(null);
            setNewAppointment({
                doctorId: doctorIdFromSearch || '',
                appointmentDate: '',
                appointmentTime: '',
                terminId: '',
                reason: ''
            });
            setAllDoctorAvailableTerms([]);
            if (doctorIdFromSearch) {
                fetchDoctorAllAvailableTerms(doctorIdFromSearch);
            }
        }
        setShowAppointmentModal(true);
    };

    const closeAppointmentModal = () => {
        setShowAppointmentModal(false);
        setEditingAppointment(null);
        setNewAppointment({
            doctorId: '',
            appointmentDate: '',
            appointmentTime: '',
            terminId: '',
            reason: ''
        });
        setAllDoctorAvailableTerms([]);
    };

    const handleNewAppointmentChange = async (e) => {
        const { name, value } = e.target;

        let updatedAppointment = { ...newAppointment, [name]: value };

        if (name === 'doctorId') {
            updatedAppointment.appointmentDate = '';
            updatedAppointment.appointmentTime = '';
            updatedAppointment.terminId = '';
            if (value) {
                await fetchDoctorAllAvailableTerms(value);
            } else {
                setAllDoctorAvailableTerms([]);
            }
        } else if (name === 'terminId') {
            const selectedTerm = allDoctorAvailableTerms.find(term => term.terminID === parseInt(value));
            if (selectedTerm) {
                updatedAppointment.terminId = selectedTerm.terminID;
                updatedAppointment.appointmentDate = selectedTerm.datum;
                updatedAppointment.appointmentTime = selectedTerm.vrijeme;
            } else {
                updatedAppointment.terminId = '';
                updatedAppointment.appointmentDate = '';
                updatedAppointment.appointmentTime = '';
            }
        }
        setNewAppointment(updatedAppointment);
    };


    const handleScheduleAppointment = async () => {
        if (!user || !user.pacijentId) {
            setAlertMessage('Patient ID not available. Cannot schedule appointment.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }

        if (!newAppointment.doctorId || !newAppointment.terminId || !newAppointment.reason) {
            setAlertMessage('Please select a doctor, an available slot, and provide a reason.');
            setAlertType('warning');
            setShowAlert(true);
            return;
        }

        const appointmentData = {
            pacijentID: user.pacijentId,
            doktorID: parseInt(newAppointment.doctorId),
            terminID: newAppointment.terminId,
            datumPregleda: newAppointment.appointmentDate,
            vrijemePregleda: newAppointment.appointmentTime,
            komentarPacijenta: newAppointment.reason,
            status: editingAppointment ? editingAppointment.status : 'zakazan',
        };

        try {
            await axios.post(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi`, appointmentData, authHeaders);

            setAlertMessage('Appointment scheduling request successfully accepted. You will be notified when it is processed.');
            setAlertType('info');
            setShowAlert(true);

            closeAppointmentModal();
        } catch (error) {
            console.error('Error scheduling/updating appointment:', error.response?.data || error.message);
            let errorMessage = 'Failed to schedule/update appointment. Please try again.';
            if (error.response?.data?.message) {
                errorMessage = error.response.data.message;
            } else if (error.message) {
                errorMessage = error.message;
            }
            setAlertMessage(errorMessage);
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const handleCancelAppointment = async (appointmentId) => {
        if (window.confirm('Are you sure you want to cancel this appointment?')) {
            if (!user || !user.pacijentId) {
                setAlertMessage('Patient ID not available for cancellation. Please try again.');
                setAlertType('danger');
                setShowAlert(true);
                return;
            }
            try {
                const existingApp = appointments.find(app => app.id === appointmentId);
                if (!existingApp) {
                    setAlertMessage("Appointment not found for cancellation.");
                    setAlertType('danger');
                    setShowAlert(true);
                    return;
                }

                const cancelPayload = {
                    pregledID: existingApp.id,
                    pacijentID: user.pacijentId,
                    doktorID: existingApp.doktorID,
                    terminID: existingApp.terminID,
                    datumPregleda: existingApp.datumPregleda,
                    vrijemePregleda: existingApp.vrijemePregleda,
                    komentarPacijenta: existingApp.komentarPacijenta,
                    status: 'otkazan'
                };

                await axios.put(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/${appointmentId}`, cancelPayload, authHeaders);
                setAlertMessage('Appointment successfully cancelled!');
                setAlertType('success');
                setShowAlert(true);
                fetchPatientAppointments();
            } catch (error) {
                console.error('Error cancelling appointment:', error.response?.data || error.message);
                setAlertMessage('Failed to cancel appointment. Please try again. ' + (error.response?.data?.message || error.message));
                setAlertType('danger');
                setShowAlert(true);
            }
        }
    };

    // --- Profile Update ---
    const handleProfileChange = (e) => {
        const { name, value } = e.target;
        setPatientProfile(prev => ({ ...prev, [name]: value }));
    };

    const handleSaveProfile = async () => {
        if (!user || !user.pacijentId || !user.userId) {
            setAlertMessage('Patient ID or User ID not available. Cannot save profile.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }

        try {
            const patientDetailsPayload = {
                pacijentID: user.pacijentId,
                ime: patientProfile.firstName,
                prezime: patientProfile.lastName,
                email: patientProfile.email,
                telefon: patientProfile.phone,
                datumRodjenja: patientProfile.datumRodjenja,
                adresa: patientProfile.adresa,
                userId: user.userId
            };
            await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${user.pacijentId}`, patientDetailsPayload, authHeaders);

            const authProfilePayload = {
                email: patientProfile.email,
                fullName: `${patientProfile.firstName} ${patientProfile.lastName}`,
                telefon: patientProfile.phone,
            };
            await axios.put(`${AUTH_API_PREFIX}/users/${user.userId}`, authProfilePayload, authHeaders);


            setAlertMessage('Profile successfully updated!');
            setAlertType('success');
            setShowAlert(true);
            fetchPatientProfile();
        } catch (error) {
            console.error('Error updating profile:', error.response?.data || error.message);
            setAlertMessage('Failed to update profile. Please try again. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    // --- Secure Messaging ---
    const handleNewMessageChange = (e) => {
        const { name, value } = e.target;
        setNewMessage(prev => ({ ...prev, [name]: value }));
    };

    const handleReplyMessageChange = (e) => {
        setReplyMessage(prev => ({ ...prev, content: e.target.value }));
    };

    const handleSendMessage = async () => {
        if (!user || !user.userId) { // Use user.userId to send message
            setAlertMessage('Your user ID is not available. Cannot send message.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }
        if (!newMessage.recipientId || !newMessage.subject || !newMessage.content) {
            setAlertMessage('Please fill in all message fields.');
            setAlertType('warning');
            setShowAlert(true);
            return;
        }

        try {
            const messageData = {
                senderId: user.userId, // SENDER is now user.userId (Long)
                senderType: 'PACIJENT',
                receiverId: Number(newMessage.recipientId), // Convert to Number
                receiverType: 'DOKTOR',
                subject: newMessage.subject,
                content: newMessage.content
            };
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/posalji`, messageData, authHeaders);
            setAlertMessage('Message successfully sent!');
            setAlertType('success');
            setShowAlert(true);
            setNewMessage({ recipientId: '', subject: '', content: '' });
            fetchMessages(); // Refresh message list
        } catch (error) {
            console.error('Error sending message:', error.response?.data || error.message);
            setAlertMessage('Failed to send message. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const handleReply = async (messageId) => {
        if (!user || !user.userId) { // Use user.userId to send reply
            setAlertMessage('Your user ID is not available. Cannot reply.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }
        if (!replyMessage.content) {
            setAlertMessage('Reply content cannot be empty.');
            setAlertType('warning');
            setShowAlert(true);
            return;
        }
        try {
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/${messageId}/odgovori`, {
                senderId: user.userId, // Important: Use user.userId here
                senderIme: user.fullName, // Use fullName from useAuth
                senderType: 'PACIJENT',
                content: replyMessage.content,
                timestamp: new Date().toISOString()
            }, authHeaders);
            setAlertMessage('Reply successfully sent!', 'success');
            setAlertType('success');
            setShowAlert(true);
            setReplyMessage({ messageId: '', content: '' });
            fetchMessages(); // Refresh message list
            // Update local state for current reply display
            setSelectedMessage(prev => {
                if (!prev) return null;
                return {
                    ...prev,
                    replies: [...(prev.replies || []), {
                        senderId: user.userId, // Important: Use user.userId here
                        senderIme: user.fullName, // Use fullName from useAuth
                        senderType: 'PACIJENT',
                        content: replyMessage.content,
                        timestamp: new Date().toISOString()
                    }]
                };
            });
        } catch (error) {
            console.error('Error sending reply:', error.response?.data || error.message);
            setAlertMessage('Failed to send reply. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const handleViewMessage = (message) => {
        setSelectedMessage(message);
    };

    const closeMessageModal = () => {
        setSelectedMessage(null);
        setReplyMessage({ messageId: '', content: '' });
    };

    // --- Rating Functions ---
    const openRatingModal = (doktorID, doktorIme) => {
        setSelectedDoctorForRating({ id: doktorID, name: doktorIme });
        setRatingData({ ocjena: 5, komentar: '' });
        setShowRatingModal(true);
    };

    const closeRatingModal = () => {
        setShowRatingModal(false);
        setSelectedDoctorForRating(null);
        setRatingData({ ocjena: 5, komentar: '' });
    };

    const handleRatingChange = (e) => {
        const { name, value } = e.target;
        setRatingData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmitRating = async () => {
        if (!user || !user.pacijentId) {
            setAlertMessage('Patient ID not available. Cannot submit rating.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }
        if (!selectedDoctorForRating || ratingData.ocjena === null) {
            setAlertMessage('Please select a rating.');
            setAlertType('warning');
            setShowAlert(true);
            return;
        }

        try {
            const ratingPayload = {
                pacijentID: user.pacijentId,
                doktorID: selectedDoctorForRating.id,
                ocjena: parseFloat(ratingData.ocjena),
                komentar: ratingData.komentar
            };

            await axios.post(`${KORISNICI_DOKTORI_API_PREFIX}/api/ocjene`, ratingPayload, authHeaders);

            setAlertMessage(`Rating successfully submitted for doctor ${selectedDoctorForRating.name}!`);
            setAlertType('success');
            setShowAlert(true);
            closeRatingModal();
            fetchPatientAppointments();
            fetchDoctors();
        } catch (error) {
            console.error('Error submitting rating:', error.response?.data || error.message);
            setAlertMessage('Failed to submit rating. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    // NEW: "Pay Now" functionality
    const handlePayNow = async (billId) => {
        if (window.confirm('Are you sure you want to mark this bill as paid?')) {
            try {
                // Call backend endpoint to update bill status
                await axios.put(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/racuni/${billId}/oznaci-kao-placeno`, {}, authHeaders);
                setAlertMessage('Bill successfully marked as paid!');
                setAlertType('success');
                setShowAlert(true);
                fetchBills(); // Refresh bill list
            } catch (error) {
                console.error('Error marking bill as paid:', error.response?.data || error.message);
                setAlertMessage('Failed to mark bill as paid. ' + (error.response?.data?.message || error.message));
                setAlertType('danger');
                setShowAlert(true);
            }
        }
    };

    // NEW: "View Details" functionality
    const handleViewBillDetails = (bill) => {
        setSelectedBillDetails(bill);
        setShowBillDetailsModal(true);
    };

    const closeBillDetailsModal = () => {
        setShowBillDetailsModal(false);
        setSelectedBillDetails(null);
    };


    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
                <Spinner animation="border" role="status">
                    <span className="visually-hidden">Loading...</span>
                </Spinner>
                <p className="ms-2">Loading dashboard data...</p>
            </div>
        );
    }

    const currentPatientUserId = user.userId;


    return (
        <div className="d-flex" style={{ minHeight: '100vh' }}>
            {/* Sidebar */}
            <div className="bg-dark text-white p-3" style={{ width: '250px' }}>
                <h4 className="mb-4">Patient Dashboard</h4>
                <ul className="nav flex-column">
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'overview' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('overview')}
                        >
                            Overview
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'myAppointments' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('myAppointments')}
                        >
                            My Appointments
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'medicalHistory' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('medicalHistory')}
                        >
                            Medical History
                        </button>
                    </li>
                    {/* REMOVED: My Documents tab */}
                    {/* <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'documents' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('documents')}
                        >
                            My Documents
                        </button>
                    </li> */}
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'billing' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('billing')}
                        >
                            Billing & Payments
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'messages' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('messages')}
                        >
                            Messages
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'doctorSearch' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('doctorSearch')}
                        >
                            Find a Doctor
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'profileSettings' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('profileSettings')}
                        >
                            Profile Settings
                        </button>
                    </li>
                </ul>
            </div>

            {/* Main Content Area */}
            <div className="flex-grow-1 p-4 bg-light">
                {showAlert && (
                    <Alert variant={alertType} onClose={() => setShowAlert(false)} dismissible className="position-fixed top-0 start-50 translate-middle-x mt-3" style={{ zIndex: 1050 }}>
                        {alertMessage}
                    </Alert>
                )}

                <div className="d-flex justify-content-between align-items-center mb-4">
                    <h2 className="mb-0">Patient Dashboard</h2>
                </div>

                {/* Content based on activeTab */}
                {activeTab === 'overview' && (
                    <div>
                        <h3>Welcome, {patientProfile.firstName || (user ? user.fullName : 'Patient')}!</h3>
                        <p>This is your patient overview. Here you can quickly see your upcoming appointments and recent activities.</p>
                        <div className="row mt-4">
                            <div className="col-md-6">
                                <div className="card">
                                    <div className="card-header">
                                        Upcoming Appointments
                                    </div>
                                    <ul className="list-group list-group-flush">
                                        {appointments.filter(app => app.status === 'zakazan' || app.status === 'potvren').length > 0 ? (
                                            appointments.filter(app => app.status === 'zakazan' || app.status === 'potvren').map(app => (
                                                <li key={app.id} className="list-group-item d-flex justify-content-between align-items-center">
                                                    <div>
                                                        <strong>{app.datumPregleda} at {app.vrijemePregleda}</strong>
                                                        <br />
                                                        <small>Reason: {app.komentarPacijenta}</small>
                                                        <br />
                                                        <small>Doctor: {app.doktorIme}</small>
                                                    </div>
                                                    <span className={`badge bg-${app.status === 'potvren' ? 'success' : 'warning'}`}>{app.status}</span>
                                                </li>
                                            ))
                                        ) : (
                                            <li className="list-group-item">No upcoming appointments.</li>
                                        )}
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {activeTab === 'myAppointments' && (
                    <div>
                        <h3>My Appointments</h3>
                        <Button variant="primary" className="mb-3" onClick={() => openAppointmentModal()}>
                            Schedule New Appointment
                        </Button>
                        {appointments.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Doctor</th>
                                        <th>Date</th>
                                        <th>Time</th>
                                        <th>Reason</th>
                                        <th>Status</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {appointments.map(app => (
                                        <tr key={app.id}>
                                            <td>{app.doktorIme}</td>
                                            <td>{app.datumPregleda}</td>
                                            <td>{app.vrijemePregleda}</td>
                                            <td>{app.komentarPacijenta}</td>
                                            <td><span className={`badge bg-${app.status === 'obavljen' ? 'success' : app.status === 'zakazan' ? 'warning' : 'danger'}`}>{app.status}</span></td>
                                            <td>
                                                {app.status === 'zakazan' || app.status === 'potvren' ? (
                                                    <Button variant="danger" size="sm" onClick={() => handleCancelAppointment(app.id)}>Cancel</Button>
                                                ) : app.status === 'obavljen' ? (
                                                    <Button variant="success" size="sm" onClick={() => openRatingModal(app.doktorID, app.doktorIme)}>Rate</Button>
                                                ) : (
                                                    <small>No available actions</small>
                                                )}
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>No appointment history found. Schedule your first appointment!</p>
                        )}
                    </div>
                )}

                {activeTab === 'medicalHistory' && (
                    <div>
                        <h3>My Medical History</h3>
                        {medicalHistory.length > 0 ? (
                            <ul className="list-group">
                                {medicalHistory.map((record, index) => (
                                    <li key={record.id} className="list-group-item">
                                        <strong>Date:</strong> {record.date} <br />
                                        <strong>Diagnosis:</strong> {record.diagnosis} <br />
                                        <strong>Treatment:</strong> {record.treatment} <br />
                                        <strong>Doctor:</strong> {record.doctorName} <br />
                                        <strong>Notes:</strong> {record.notes || 'N/A'}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>No medical history records found.</p>
                        )}
                    </div>
                )}

                {/* REMOVED: Documents tab content */}
                {/* {activeTab === 'documents' && (
                    <div>
                        <h3>My Documents</h3>
                        <Form.Group controlId="formFile" className="mb-3">
                            <Form.Label>Upload New Document</Form.Label>
                            <Form.Control type="file" onChange={handleDocumentUpload} />
                        </Form.Group>

                        {documents.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Document Name</th>
                                        <th>Type</th>
                                        <th>Upload Date</th>
                                        <th>Access</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {documents.map(doc => (
                                        <tr key={doc.id}>
                                            <td>{doc.fileName}</td>
                                            <td>{new Date(doc.uploadDate).toLocaleDateString()}</td>
                                            <td><span className={`badge bg-${doc.pristup === 'JAVNA' ? 'success' : 'secondary'}`}>{doc.pristup}</span></td>
                                            <td>
                                                <Button variant="success" size="sm" href={`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/download/${doc.id}`} target="_blank">Download</Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>No documents found.</p>
                        )}
                    </div>
                )} */}

                {activeTab === 'billing' && (
                    <div>
                        <h3>Billing & Payments</h3>
                        {bills.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Bill ID</th>
                                        <th>Issue Date</th>
                                        <th>Amount</th>
                                        <th>Status</th>
                                        <th>Description</th>
                                        <th>Issued By</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {bills.map(bill => (
                                        <tr key={bill.id}>
                                            <td>{bill.id}</td>
                                            <td>{new Date(bill.billDate).toLocaleDateString()}</td>
                                            <td>{bill.amount ? bill.amount.toFixed(2) : 'N/A'} KM</td>
                                            <td><span className={`badge bg-${bill.status === 'PLACEN' ? 'success' : 'warning'}`}>{bill.status}</span></td>
                                            <td>{bill.opis}</td>
                                            <td>{bill.doktorIme}</td>
                                            <td>
                                                {bill.status === 'NEPLACEN' && (
                                                    <Button variant="success" size="sm" className="me-2" onClick={() => handlePayNow(bill.id)}>Pay Now</Button>
                                                )}
                                                <Button variant="info" size="sm" onClick={() => handleViewBillDetails(bill)}>View Details</Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>No billing records found.</p>
                        )}
                    </div>
                )}

                {activeTab === 'messages' && (
                    <div>
                        <h3>My Messages</h3>
                        <Button variant="primary" className="mb-3" onClick={() => setNewMessage({ recipientId: '', subject: '', content: '' })}>Compose New Message</Button>

                        {messages.length > 0 ? (
                            <ul className="list-group">
                                {messages.map(msg => (
                                    <li key={msg.id} className="list-group-item d-flex justify-content-between align-items-center">
                                        <div>
                                            {/* Displays sender's name (Auth userId), checks if current user is patient or doctor */}
                                            <strong>From: {msg.senderId === currentPatientUserId ? 'Me' : (doctors.find(d => d.userId === msg.senderId)?.firstName + ' ' + doctors.find(d => d.userId === msg.senderId)?.lastName || `Doctor (${msg.senderId})`)}</strong> <br />
                                            <strong>Subject:</strong> {msg.subject} <br />
                                            <small>{new Date(msg.timestamp).toLocaleString()}</small>
                                        </div>
                                        <Button variant="info" size="sm" onClick={() => handleViewMessage(msg)}>View Message</Button>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>No messages found.</p>
                        )}

                        <hr className="my-4" />

                        <h4>Compose New Message</h4>
                        <Form>
                            <Form.Group className="mb-3">
                                <Form.Label>Recipient (Doctor)</Form.Label>
                                <Form.Control
                                    as="select"
                                    name="recipientId"
                                    value={newMessage.recipientId}
                                    onChange={handleNewMessageChange}
                                    required
                                >
                                    <option value="">-- Select a doctor --</option>
                                    {doctors.map(doctor => (
                                        <option key={doctor.userId} value={doctor.userId}>{doctor.firstName} {doctor.lastName}</option>
                                    ))}
                                </Form.Control>
                            </Form.Group>
                            <Form.Group className="mb-3">
                                <Form.Label>Subject</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="subject"
                                    value={newMessage.subject}
                                    onChange={handleNewMessageChange}
                                    required
                                />
                            </Form.Group>
                            <Form.Group className="mb-3">
                                <Form.Label>Content</Form.Label>
                                <Form.Control
                                    as="textarea"
                                    rows={3}
                                    name="content"
                                    value={newMessage.content}
                                    onChange={handleNewMessageChange}
                                    required
                                />
                            </Form.Group>
                            <Button variant="primary" onClick={handleSendMessage}>Send Message</Button>
                        </Form>
                    </div>
                )}

                {activeTab === 'doctorSearch' && (
                    <div>
                        <h3>Find a Doctor</h3>
                        <p>Search for doctors by name or specialty to find the right care for you.</p>
                        <Form.Group className="mb-3">
                            <Form.Control type="text" placeholder="Search by name or specialty..." />
                        </Form.Group>
                        {doctors.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Specialty</th>
                                        <th>Contact</th>
                                        <th>Rating</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {doctors.map(doctor => (
                                        <tr key={doctor.id}>
                                            <td>{doctor.firstName} {doctor.lastName}</td>
                                            <td>{doctor.specialty || 'General Practitioner'}</td>
                                            <td>{doctor.email} | {doctor.phone}</td>
                                            <td>{doctor.ocjena ? doctor.ocjena.toFixed(1) : 'N/A'}</td>
                                            <td>
                                                <Button variant="primary" size="sm" onClick={() => {
                                                    openAppointmentModal(null, doctor.id);
                                                }}>Schedule Appointment</Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>No doctors found.</p>
                        )}
                    </div>
                )}

                {activeTab === 'profileSettings' && (
                    <div>
                        <h3>Profile Settings</h3>
                        <Form>
                            <Form.Group className="mb-3" controlId="formFirstName">
                                <Form.Label>First Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="firstName"
                                    value={patientProfile.firstName || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formLastName">
                                <Form.Label>Last Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="lastName"
                                    value={patientProfile.lastName || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formEmail">
                                <Form.Label>Email</Form.Label>
                                <Form.Control
                                    type="email"
                                    name="email"
                                    value={patientProfile.email || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formPhone">
                                <Form.Label>Phone</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="phone"
                                    value={patientProfile.phone || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formAddress">
                                <Form.Label>Address</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="adresa"
                                    value={patientProfile.adresa || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formDatumRodjenja">
                                <Form.Label>Date of Birth</Form.Label>
                                <Form.Control
                                    type="date"
                                    name="datumRodjenja"
                                    value={patientProfile.datumRodjenja || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Button variant="primary" onClick={handleSaveProfile}>
                                Save Profile
                            </Button>
                        </Form>
                    </div>
                )}
            </div>

            {/* Appointment Modal */}
            <Modal show={showAppointmentModal} onHide={closeAppointmentModal}>
                <Modal.Header closeButton>
                    <Modal.Title>{editingAppointment ? 'Edit Appointment' : 'Schedule New Appointment'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3" controlId="formDoctor">
                            <Form.Label>Select Doctor</Form.Label>
                            <Form.Control
                                as="select"
                                name="doctorId"
                                value={newAppointment.doctorId}
                                onChange={handleNewAppointmentChange}
                                required
                            >
                                <option value="">-- Select a doctor --</option>
                                {doctors.map(doctor => (
                                    <option key={doctor.id} value={doctor.id}>{doctor.firstName} {doctor.lastName}</option>
                                ))}
                            </Form.Control>
                        </Form.Group>

                        {newAppointment.doctorId && (
                            <Form.Group className="mb-3" controlId="formAvailableTerm">
                                <Form.Label>Select Available Slot</Form.Label>
                                {loading ? (
                                    <Spinner animation="border" size="sm" />
                                ) : allDoctorAvailableTerms.length > 0 ? (
                                    <Form.Control
                                        as="select"
                                        name="terminId"
                                        value={newAppointment.terminId}
                                        onChange={handleNewAppointmentChange}
                                        required
                                    >
                                        <option value="">-- Select date and time --</option>
                                        {allDoctorAvailableTerms.map(term => (
                                            <option key={term.terminID} value={term.terminID}>{term.display}</option>
                                        ))}
                                    </Form.Control>
                                ) : (
                                    <Alert variant="info">No available slots for this doctor.</Alert>
                                )}
                            </Form.Group>
                        )}

                        {/* Fields for date and time that will be automatically populated after selecting a slot (can be display-only) */}
                        {newAppointment.terminId && (
                            <>
                                <Form.Group className="mb-3" controlId="formAppointmentDateDisplay">
                                    <Form.Label>Selected Date</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={newAppointment.appointmentDate}
                                        readOnly
                                        disabled
                                    />
                                </Form.Group>
                                <Form.Group className="mb-3" controlId="formAppointmentTimeDisplay">
                                    <Form.Label>Selected Time</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={newAppointment.appointmentTime ? newAppointment.appointmentTime.substring(0, 5) : ''}
                                        readOnly
                                        disabled
                                    />
                                </Form.Group>
                            </>
                        )}

                        <Form.Group className="mb-3" controlId="formReason">
                            <Form.Label>Reason for Appointment</Form.Label>
                            <Form.Control
                                as="textarea"
                                rows={3}
                                name="reason"
                                value={newAppointment.reason}
                                onChange={handleNewAppointmentChange}
                                placeholder="e.g., General check-up, specific symptoms..."
                                required
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeAppointmentModal}>
                        Close
                    </Button>
                    <Button
                        variant="primary"
                        onClick={handleScheduleAppointment}
                        disabled={!newAppointment.doctorId || !newAppointment.terminId || !newAppointment.reason || loading}
                    >
                        {editingAppointment ? 'Update Appointment' : 'Schedule Appointment'}
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* Message Detail Modal */}
            <Modal show={!!selectedMessage} onHide={closeMessageModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Message: {selectedMessage?.subject}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {selectedMessage && (
                        <>
                            {/* Display sender of the message (Auth userId) */}
                            <p><strong>From:</strong> {selectedMessage.senderId === currentPatientUserId ? 'Me' : (doctors.find(d => d.userId === selectedMessage.senderId)?.firstName + ' ' + doctors.find(d => d.userId === selectedMessage.senderId)?.lastName || `Doctor (${selectedMessage.senderId})`)}</p>
                            <p><strong>Date:</strong> {new Date(selectedMessage.timestamp).toLocaleString()}</p>
                            <hr />
                            <p>{selectedMessage.content}</p>

                            {selectedMessage.replies && selectedMessage.replies.length > 0 && (
                                <div className="mt-4">
                                    <h5>Replies:</h5>
                                    {selectedMessage.replies.map((reply, index) => (
                                        <div key={index} className="border p-2 mb-2 rounded bg-light">
                                            {/* Display sender of the reply (Auth userId) */}
                                            <strong>{reply.senderId === currentPatientUserId ? 'Me' : (doctors.find(d => d.userId === reply.senderId)?.firstName + ' ' + doctors.find(d => d.userId === reply.senderId)?.lastName || `Doctor (${reply.senderId})`)}:</strong> {reply.content}
                                            <br />
                                            <small>{new Date(reply.timestamp).toLocaleString()}</small>
                                        </div>
                                    ))}
                                </div>
                            )}

                            <Form.Group className="mt-4">
                                <Form.Label>Reply</Form.Label>
                                <Form.Control
                                    as="textarea"
                                    rows={2}
                                    value={replyMessage.content}
                                    onChange={handleReplyMessageChange}
                                    placeholder="Type your reply here..."
                                />
                            </Form.Group>
                            <Button variant="primary" className="mt-2" onClick={() => handleReply(selectedMessage.id)}>Send Reply</Button>
                        </>
                    )}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeMessageModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* Rating Modal */}
            <Modal show={showRatingModal} onHide={closeRatingModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Rate Doctor: {selectedDoctorForRating?.name}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3" controlId="formRating">
                            <Form.Label>Rating (1-5)</Form.Label>
                            <Form.Control
                                as="select"
                                name="ocjena"
                                value={ratingData.ocjena}
                                onChange={handleRatingChange}
                                required
                            >
                                <option value="1">1 - Very Poor</option>
                                <option value="2">2 - Poor</option>
                                <option value="3">3 - Average</option>
                                <option value="4">4 - Good</option>
                                <option value="5">5 - Excellent</option>
                            </Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formRatingComment">
                            <Form.Label>Comment (optional)</Form.Label>
                            <Form.Control
                                as="textarea"
                                rows={3}
                                name="komentar"
                                value={ratingData.komentar}
                                onChange={handleRatingChange}
                                placeholder="Write a comment about your experience..."
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeRatingModal}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleSubmitRating}>
                        Submit Rating
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* NEW: Bill Details Modal */}
            <Modal show={showBillDetailsModal} onHide={closeBillDetailsModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Bill Details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {selectedBillDetails && (
                        <div>
                            <p><strong>Bill ID:</strong> {selectedBillDetails.id}</p>
                            <p><strong>Issue Date:</strong> {new Date(selectedBillDetails.billDate).toLocaleDateString()}</p>
                            <p><strong>Amount:</strong> {selectedBillDetails.amount ? selectedBillDetails.amount.toFixed(2) : 'N/A'} KM</p>
                            <p><strong>Status:</strong> <span className={`badge bg-${selectedBillDetails.status === 'PLACEN' ? 'success' : 'warning'}`}>{selectedBillDetails.status}</span></p>
                            <p><strong>Description:</strong> {selectedBillDetails.opis}</p>
                            <p><strong>Issued By:</strong> {selectedBillDetails.doktorIme}</p>
                            {selectedBillDetails.dueDate && (
                                <p><strong>Due Date:</strong> {new Date(selectedBillDetails.dueDate).toLocaleDateString()}</p>
                            )}
                        </div>
                    )}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeBillDetailsModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>

        </div>
    );
}

export default PatientDashboard;
