import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
// Uklonjen direktan import Bootstrap CSS-a iz JS-a.
// Pretpostavlja se da će se Bootstrap CSS učitati putem CDN-a u public/index.html.
import { useAuth } from '../contexts/AuthContext'; // VAŽNO: Provjerite da li je putanja do AuthContext.js ispravna!

function DoctorDashboard() {
  const { id } = useParams(); // Dohvaća ID doktora iz URL-a (iz rute)
  // Dohvaćamo potrebne podatke iz AuthContext-a
  const { isAuthenticated, fullName, doktorId, userRoles } = useAuth();
  const navigate = useNavigate();

  const [scheduledAppointments, setScheduledAppointments] = useState([]);
  const [historyAppointments, setHistoryAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('scheduled'); // 'scheduled' ili 'history'

  // PRILAGODITE OVO: URL-ovi za backend API-je
  // const API_BASE_URL = 'http://localhost:8080/api/doktori'; // Vaš DoktorController je na 8080

  // Mock podaci za testiranje bez backenda
  const mockScheduledAppointments = [
    {
      pregledID: 101,
      datumVrijeme: new Date(new Date().setHours(10, 0, 0, 0) + 2 * 24 * 60 * 60 * 1000).toISOString(), // Za 2 dana od sada
      pacijentIme: 'Petar',
      pacijentPrezime: 'Petrović',
      pacijentID: 1, // Dodan mock pacijentID
      status: 'Zakazan',
      doktorID: 1 // Pretpostavljamo da je doktorId 1 za testiranje
    },
    {
      pregledID: 102,
      datumVrijeme: new Date(new Date().setHours(14, 30, 0, 0) + 5 * 24 * 60 * 60 * 1000).toISOString(), // Za 5 dana od sada
      pacijentIme: 'Ana',
      pacijentPrezime: 'Anić',
      pacijentID: 2, // Dodan mock pacijentID
      status: 'Zakazan',
      doktorID: 1
    },
    {
      pregledID: 103,
      datumVrijeme: new Date(new Date().setHours(9, 0, 0, 0) + 1 * 24 * 60 * 60 * 1000).toISOString(), // Za 1 dan od sada
      pacijentIme: 'Marko',
      pacijentPrezime: 'Marković',
      pacijentID: 3, // Dodan mock pacijentID
      status: 'Zakazan',
      doktorID: 1
    }
  ];

  const mockHistoryAppointments = [
    {
      pregledID: 201,
      datumVrijeme: new Date(new Date().setHours(11, 0, 0, 0) - 7 * 24 * 60 * 60 * 1000).toISOString(), // Prije 7 dana
      pacijentIme: 'Jelena',
      pacijentPrezime: 'Jelić',
      pacijentID: 4, // Dodan mock pacijentID
      status: 'Završen',
      doktorID: 1
    },
    {
      pregledID: 202,
      datumVrijeme: new Date(new Date().setHours(16, 0, 0, 0) - 14 * 24 * 60 * 60 * 1000).toISOString(), // Prije 14 dana
      pacijentIme: 'Ivan',
      pacijentPrezime: 'Ivanović',
      pacijentID: 5, // Dodan mock pacijentID
      status: 'Završen',
      doktorID: 1
    },
    {
      pregledID: 203,
      datumVrijeme: new Date(new Date().setHours(9, 0, 0, 0) - 3 * 24 * 60 * 60 * 1000).toISOString(), // Prije 3 dana
      pacijentIme: 'Milica',
      pacijentPrezime: 'Milić',
      pacijentID: 6, // Dodan mock pacijentID
      status: 'Otkazan',
      doktorID: 1
    }
  ];

  useEffect(() => {
    // Provjera autorizacije i ID-a
    // Za testiranje bez backenda, možete privremeno ukloniti ili pojednostaviti ove provjere
    // Npr. samo provjeriti da li je doktorId iz URL-a validan broj
    if (isNaN(parseInt(id))) {
      setError(new Error("Invalid doctor ID in URL."));
      setLoading(false);
      return;
    }

    // Simulacija dohvaćanja podataka s kašnjenjem (kao da dolazi s backenda)
    setTimeout(() => {
      // Filtrirajte mock podatke ako je potrebno na osnovu 'id'
      const filteredScheduled = mockScheduledAppointments.filter(app => app.doktorID === parseInt(id));
      const filteredHistory = mockHistoryAppointments.filter(app => app.doktorID === parseInt(id));

      setScheduledAppointments(filteredScheduled);
      setHistoryAppointments(filteredHistory);
      setLoading(false);
      setError(null); // Resetuj grešku ako je bilo
    }, 500); // Simulacija mrežnog kašnjenja

    // Ovdje su bili stvarni fetch pozivi, sada su zakomentarisani za mock testiranje:
    /*
    const fetchAppointments = async () => {
      try {
        // Dohvat zakazanih pregleda
        const scheduledResponse = await fetch(`${API_BASE_URL}/${doktorId}/pregledi/zakazani`);
        if (!scheduledResponse.ok) {
          throw new Error(`HTTP error! Status: ${scheduledResponse.status} for scheduled appointments`);
        }
        const scheduledData = await scheduledResponse.json();
        setScheduledAppointments(scheduledData);

        // Dohvat historije pregleda
        const historyResponse = await fetch(`${API_BASE_URL}/${doktorId}/pregledi/historija`);
        if (!historyResponse.ok) {
          throw new Error(`HTTP error! Status: ${historyResponse.status} for history appointments`);
        }
        const historyData = await historyResponse.json();
        setHistoryAppointments(historyData);

      } catch (err) {
        setError(err);
        console.error("Error fetching appointments:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
    */
  }, [id]); // Uklonjeni isAuthenticated, doktorId, navigate iz zavisnosti za jednostavno mock testiranje

  if (loading) {
    return (
      <div className="container text-center py-5">
        <p className="lead">Loading doctor panel...</p>
        <div className="spinner-border text-danger" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container text-center py-5">
        <p className="lead text-danger">Error: {error.message}</p>
        <p>Please ensure you are logged in as the correct doctor and the backend is running.</p>
      </div>
    );
  }

  return (
    <div className="container my-5">
      <div className="card shadow-lg p-4">
        <div className="card-body">
          <h2 className="card-title text-center mb-4 text-danger">Doctor Panel</h2>
          {/* Koristimo fullName iz konteksta za dobrodošlicu */}
          {/* Za mock testiranje, fullName možda neće biti dostupan ako nema login simulacije */}
          {isAuthenticated && fullName ? (
            <h3 className="text-center text-muted mb-5">Welcome, Dr. {fullName}</h3>
          ) : (
            <h3 className="text-center text-muted mb-5">Welcome, Doctor! (Mock Data)</h3>
          )}

          {/* Tabovi za navigaciju */}
          <ul className="nav nav-tabs nav-fill mb-4">
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === 'scheduled' ? 'active' : ''}`}
                onClick={() => setActiveTab('scheduled')}
              >
                Scheduled Appointments ({scheduledAppointments.length})
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === 'history' ? 'active' : ''}`}
                onClick={() => setActiveTab('history')}
              >
                Appointment History ({historyAppointments.length})
              </button>
            </li>
          </ul>

          {/* Prikaz sadržaja na osnovu aktivnog taba */}
          {activeTab === 'scheduled' && (
            <div>
              <h4 className="text-danger mb-3">Your Scheduled Appointments</h4>
              {scheduledAppointments.length > 0 ? (
                <ul className="list-group">
                  {scheduledAppointments.map(appointment => (
                    <li key={appointment.pregledID} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <strong>Date & Time:</strong> {new Date(appointment.datumVrijeme).toLocaleString()} <br />
                        {/* Dodan Link na pacijentov profil */}
                        <strong>Patient:</strong>
                        {appointment.pacijentID ? (
                          <Link to={`/doctor/patient-profile/${appointment.pacijentID}`} className="text-decoration-none text-danger ms-1">
                            {appointment.pacijentIme || 'N/A'} {appointment.pacijentPrezime || ''}
                          </Link>
                        ) : (
                          <span> {appointment.pacijentIme || 'N/A'} {appointment.pacijentPrezime || ''}</span>
                        )}
                        <br />
                        <strong>Status:</strong> <span className="badge bg-warning text-dark">{appointment.status}</span>
                      </div>
                      {/* Ovdje možete dodati dugmad za akcije, npr. "Complete", "Cancel" */}
                      <button className="btn btn-outline-danger btn-sm">Manage</button>
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="text-muted">No scheduled appointments found.</p>
              )}
            </div>
          )}

          {activeTab === 'history' && (
            <div>
              <h4 className="text-danger mb-3">Your Appointment History</h4>
              {historyAppointments.length > 0 ? (
                <ul className="list-group">
                  {historyAppointments.map(appointment => (
                    <li key={appointment.pregledID} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <strong>Date & Time:</strong> {new Date(appointment.datumVrijeme).toLocaleString()} <br />
                        <strong>Patient:</strong>
                        {appointment.pacijentID ? (
                          <Link to={`/doctor/patient-profile/${appointment.pacijentID}`} className="text-decoration-none text-danger ms-1">
                            {appointment.pacijentIme || 'N/A'} {appointment.pacijentPrezime || ''}
                          </Link>
                        ) : (
                          <span> {appointment.pacijentIme || 'N/A'} {appointment.pacijentPrezime || ''}</span>
                        )}
                        <br />
                        <strong>Status:</strong> <span className="badge bg-success">{appointment.status}</span>
                      </div>
                      {/* Ovdje možete dodati dugmad za akcije, npr. "View Details" */}
                      <button className="btn btn-outline-secondary btn-sm">View Details</button>
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="text-muted">No past appointments found in history.</p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default DoctorDashboard;
