import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate, Link } from 'react-router-dom'; // Import Link

function ResetPasswordPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [token, setToken] = useState(null);
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    // Izvlačenje tokena iz URL-a
    const params = new URLSearchParams(location.search);
    const receivedToken = params.get('token');
    if (receivedToken) {
      setToken(receivedToken);
    } else {
      setError("Token za resetovanje lozinke nije pronađen u URL-u.");
    }
  }, [location.search]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    if (!token) {
      setError("Token za resetovanje lozinke nedostaje.");
      return;
    }

    if (newPassword !== confirmPassword) {
      setError("Lozinke se ne podudaraju.");
      return;
    }

    try {
      // Slanje newPassword kao plain text u body
      const response = await axios.post(`http://localhost:8082/auth/reset-password?token=${token}`, newPassword, {
          headers: {
              'Content-Type': 'text/plain' // Važno: Reci backendu da šalješ plain text
          }
      });
      setMessage(response.data);
      // Opcionalno, preusmjeri korisnika na login stranicu nakon uspješnog reseta
      setTimeout(() => {
        navigate('/login');
      }, 3000);
    } catch (err) {
      if (err.response) {
        setError(err.response.data);
      } else {
        setError("Došlo je do greške prilikom resetovanja lozinke.");
      }
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header text-center bg-danger text-white">
              <h2>Resetovanje lozinke</h2>
            </div>
            <div className="card-body">
              {message && <div className="alert alert-success">{message}</div>}
              {error && <div className="alert alert-danger">{error}</div>}
              {token ? (
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label htmlFor="newPasswordInput" className="form-label">Nova lozinka</label>
                    <input
                      type="password"
                      className="form-control"
                      id="newPasswordInput"
                      value={newPassword}
                      onChange={(e) => setNewPassword(e.target.value)}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="confirmPasswordInput" className="form-label">Potvrdite novu lozinku</label>
                    <input
                      type="password"
                      className="form-control"
                      id="confirmPasswordInput"
                      value={confirmPassword}
                      onChange={(e) => setConfirmPassword(e.target.value)}
                      required
                    />
                  </div>
                  <div className="d-grid gap-2">
                    <button type="submit" className="btn btn-danger">Resetuj lozinku</button>
                  </div>
                </form>
              ) : (
                <p className="text-center">Molimo Vas da pratite link iz emaila za resetovanje lozinke.</p>
              )}
              <Link to="/login" className="text-center text-danger mt-3 d-block">Nazad na prijavu</Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ResetPasswordPage;
