import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Dodaj useNavigate
import axios from 'axios'; // Importuj Axios
import 'bootstrap/dist/css/bootstrap.min.css';

function SignUpPage() {
  const [fullName, setFullName] = useState(''); // Promijenjeno iz name u fullName
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [registrationMessage, setRegistrationMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => { // Promijeni u async funkciju
    e.preventDefault();
    setRegistrationMessage(''); // Očisti prethodnu poruku

    if (password !== confirmPassword) {
      setRegistrationMessage('Lozinke se ne podudaraju.');
      return;
    }

    try {
      // Backend očekuje { fullName, email, password }
      const response = await axios.post('http://localhost:8081/api/auth/register', { // Koristi URL API Gatewaya
        fullName: fullName, // Koristi fullName
        email: email,
        password: password
        // Roles se mogu poslati, ali za sada je backend podrazumijeva "ROLE_USER"
      });

      if (response.status === 201) { // 201 Created je očekivani status za uspješnu registraciju
        setRegistrationMessage('Registracija uspješna! Molimo prijavite se.');
        console.log('Registration successful:', response.data);
        // Nakon uspješne registracije, preusmjeri korisnika na login stranicu
        navigate('/login'); 
      }
    } catch (error) {
      if (error.response && error.response.status === 400 && error.response.data.includes('Email već postoji!')) {
         setRegistrationMessage('Email već postoji. Molimo koristite drugi email.');
      } else {
         setRegistrationMessage('Došlo je do greške prilikom registracije. Pokušajte ponovo.');
      }
      console.error('Registration error:', error);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center" style={{ minHeight: '80vh' }}>
      <div className="card shadow-lg p-4" style={{ maxWidth: '450px', width: '100%' }}>
        <div className="card-body">
          <h2 className="card-title text-center mb-4 text-danger">Sign Up for WinxCare</h2>
          {registrationMessage && (
            <div className={`alert ${registrationMessage.includes('uspješna') ? 'alert-success' : 'alert-danger'} text-center`} role="alert">
              {registrationMessage}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="nameInput" className="form-label">Full Name</label>
              <input
                type="text"
                className="form-control"
                id="nameInput"
                placeholder="Your Full Name"
                value={fullName} // Koristi fullName
                onChange={(e) => setFullName(e.target.value)} // Koristi setFullName
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="emailInput" className="form-label">Email address</label>
              <input
                type="email"
                className="form-control"
                id="emailInput"
                placeholder="name@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="passwordInput" className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                id="passwordInput"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="confirmPasswordInput" className="form-label">Confirm Password</label>
              <input
                type="password"
                className="form-control"
                id="confirmPasswordInput"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>
            <div className="d-grid gap-2 mb-3">
              <button type="submit" className="btn btn-danger btn-lg">Sign Up</button>
            </div>
          </form>
          <div className="text-center mt-3">
            <p className="text-muted">Already have an account? <Link to="/login" className="text-danger text-decoration-none">Login</Link></p>
          </div>
        </div>
        </div>
    </div>
  );
}

export default SignUpPage;
