import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode'; // Importuj jwtDecode

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // NOVO: Dodaj loading state

  useEffect(() => {
    const loadUserFromToken = () => {
      setLoading(true); // Postavi loading na true na početku
      if (token) {
        try {
          const decodedToken = jwtDecode(token); // Dekodiraj token
          // Provjeri istek tokena
          if (decodedToken.exp * 1000 < Date.now()) {
            console.warn("Token je istekao. Odjavljujem korisnika.");
            setToken(null);
            localStorage.removeItem('token');
            setUser(null);
          } else {
            // Pretpostavimo da su uloge u 'roles' polju tokena
            setUser({
              email: decodedToken.sub,
              roles: decodedToken.roles || [] // Osiguraj da su uloge niz
            });
          }
        } catch (error) {
          console.error("Greška prilikom dekodiranja tokena ili provjere:", error);
          setToken(null);
          localStorage.removeItem('token');
          setUser(null);
        }
      }
      setLoading(false); // Postavi loading na false nakon obrade tokena
    };

    loadUserFromToken();
  }, [token]); // Ovisnost o tokenu

  const login = async (email, password) => {
    try {
      const response = await axios.post('http://localhost:8081/auth/login', { email, password });
      const receivedToken = response.data.token;

      localStorage.setItem('token', receivedToken);
      setToken(receivedToken);

      const decodedToken = jwtDecode(receivedToken);
      setUser({
        email: decodedToken.sub,
        roles: decodedToken.roles || []
      });
      return response.data; // Vrati cijeli AuthResponse
    } catch (error) {
      console.error("Login failed:", error);
      throw error;
    }
  };

  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem('token');
  };

  return (
    <AuthContext.Provider value={{ token, user, login, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
