import React, { createContext, useContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode'; // Provjeri da li je jwt-decode instaliran: npm install jwt-decode

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // Jedan user objekt za sve korisničke podatke
  const [loading, setLoading] = useState(true); // Učitavanje stanja autentifikacije

  useEffect(() => {
    console.log("AuthContext useEffect: Provjeravam localStorage za korisničke podatke...");
    const storedUser = localStorage.getItem('user');

    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        console.log("AuthContext: Učitani korisnički podaci iz localStorage-a:", parsedUser);

        // Provjeri validnost tokena
        if (parsedUser.token) {
          const decodedToken = jwtDecode(parsedUser.token);
          if (decodedToken.exp * 1000 < Date.now()) {
            console.warn("AuthContext: JWT token je istekao. Odjavljujem korisnika.");
            logoutUser(); // Koristi internu funkciju za odjavu
          } else {
            // Token je validan, postavi korisnika
            setUser(parsedUser);
            console.log("AuthContext: Korisnik uspješno učitan i postavljen.");
          }
        } else {
          console.log("AuthContext: Token nije pronađen u pohranjenom korisničkom objektu.");
          logoutUser();
        }
      } catch (error) {
        console.error("AuthContext: Greška prilikom parsiranja korisničkih podataka ili dekodiranja tokena iz localStorage-a:", error);
        logoutUser(); // Odjavi ako je bilo greške
      }
    } else {
      console.log("AuthContext: Nema korisničkih podataka u localStorage-u.");
    }
    setLoading(false);
    console.log("AuthContext useEffect: Učitavanje završeno.");
  }, []);

  // Funkcija za prijavu
  // Prima cijeli AuthResponse objekt od backenda
  const login = (authResponseData) => {
    console.log("AuthContext: Login funkcija pozvana sa podacima:", authResponseData);

    // Destrukturiraj sve potrebne podatke iz AuthResponse
    const {
      token,
      email,
      fullName,
      roles,
      userId,        // Long ID iz Auth servisa
      doktorID,      // Integer ID doktorskog profila
      pacijentID,    // Integer ID pacijentskog profila
      grad,
      specijalizacije,
      ocjena,
      iskustvo,
      radnoVrijeme
    } = authResponseData;

    // Kreiraj kompletan user objekt
    const newUser = {
      token: token,
      email: email,
      fullName: fullName,
      roles: Array.isArray(roles) ? roles : [], // Osiguraj da su role uvijek array
      authId: userId, // Koristi authId za ID iz Auth servisa
      doktorId: doktorID,
      pacijentId: pacijentID,
      grad: grad,
      specijalizacije: specijalizacije,
      ocjena: ocjena,
      iskustvo: iskustvo,
      radnoVrijeme: radnoVrijeme
    };

    setUser(newUser);
    localStorage.setItem('user', JSON.stringify(newUser)); // Pohrani cijeli objekt
    console.log("AuthContext: Korisničko stanje postavljeno nakon prijave:", newUser);
  };

  // Funkcija za odjavu (izložena vani)
  const logout = () => {
    console.log("AuthContext: Logout pozvan.");
    logoutUser();
  };

  // Interna funkcija za odjavu i čišćenje stanja
  const logoutUser = () => {
    localStorage.removeItem('user'); // Ukloni samo jedan ključ
    setUser(null);
    console.log("AuthContext: Korisnik odjavljen i stanje očišćeno.");
  };

  // Funkcija za provjeru role
  const hasRole = (role) => {
    // Provjeri da li user postoji i da li ima role array, pa onda provjeri uključivanje
    return user && Array.isArray(user.roles) && user.roles.includes(role);
  };

  // Izloži potrebne vrijednosti
  const contextValue = useMemo(() => ({
    user, // Izloži cijeli user objekt
    isAuthenticated: !!user && !!user.token, // Izvedeno iz user objekta
    token: user ? user.token : null,         // Izvedeno iz user objekta
    userRoles: user ? user.roles : [],       // Izvedeno iz user objekta
    userEmail: user ? user.email : null,     // Izvedeno iz user objekta
    fullName: user ? user.fullName : null,   // Izvedeno iz user objekta
    login,
    logout,
    hasRole,
    loading,
  }), [user, loading]); // Ovisnosti za useMemo

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  return useContext(AuthContext);
};
