import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
// Uklonjen direktan import Bootstrap CSS-a iz JS-a.
// Pretpostavlja se da će se Bootstrap CSS i JS učitati putem CDN-a u public/index.html.

import HomePage from './pages/HomePage';
import ServicePage from './pages/ServicePage';
import DoctorProfilePage from './pages/DoctorProfilePage';
import LoginPage from './pages/LoginPage';
import SignUpPage from './pages/SignUpPage';
import AdminDashboard from './pages/AdminDashboard';
import DoctorDashboard from './pages/DoctorDashboard';
import PatientDashboard from './pages/PatientDashboard';
import PatientProfileForDoctorPage from './pages/PatientProfileForDoctorPage';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import ForgotPasswordRequestPage from './pages/ForgotPasswordRequestPage';
import ResetPasswordPage from './pages/ResetPasswordPage';
import ContactUsPage from './pages/ContactUsPage';
import HelpPage from './pages/HelpPage';
import BlogsPage from './pages/BlogsPage';

// Komponenta za zaštićene rute (Protected Route)
const ProtectedRoute = ({ children, requiredRoles }) => {
  const { isAuthenticated, hasRole } = useAuth();

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  if (requiredRoles && requiredRoles.length > 0) {
    const hasRequiredRole = requiredRoles.some(role => hasRole(role));
    if (!hasRequiredRole) {
      // Ako korisnik nema potrebnu ulogu, preusmjeri ga na početnu stranicu
      return <Navigate to="/" replace />;
    }
  }

  return children;
};

// Komponenta za Header (izdvojena radi preglednosti)
function AppHeader() {
  // Dohvaćamo potrebne podatke iz AuthContext-a
  const { isAuthenticated, hasRole, logout, userEmail } = useAuth(); // Koristimo userEmail za prikaz

  return (
    <header className="d-flex justify-content-between align-items-center py-3 px-4 bg-white shadow-sm sticky-header">
      <h1 className="h3 mb-0 text-danger">WinxCare</h1>
      <nav>
        <ul className="nav">
          <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
          <li className="nav-item"><Link className="nav-link" to="/service">Service</Link></li>
          <li className="nav-item"><Link className="nav-link" to="/contact-us">Contact Us</Link></li>
          <li className="nav-item"><Link className="nav-link" to="/help">Help</Link></li>
          <li className="nav-item"><Link className="nav-link" to="/blogs">Blogs</Link></li>
          {/* Prikazati linkove na dashboard na osnovu uloge */}
          {isAuthenticated && hasRole('ROLE_ADMIN') && (
            <li className="nav-item"><Link className="nav-link" to="/admin-dashboard">Admin Panel</Link></li>
          )}
          {isAuthenticated && hasRole('ROLE_DOCTOR') && (
            <li className="nav-item"><Link className="nav-link" to="/doctor-dashboard">Doktor Panel</Link></li>
          )}
          {isAuthenticated && hasRole('ROLE_PATIENT') && (
            <li className="nav-item"><Link className="nav-link" to="/patient-dashboard">Pacijent Panel</Link></li>
          )}
        </ul>
      </nav>
      <div className="d-flex">
        {!isAuthenticated ? (
          <>
            <Link to="/sign-up" className="btn btn-outline-danger me-2">Sign Up</Link>
            <Link to="/login" className="btn btn-danger">Log In</Link>
          </>
        ) : (
          <>
            <span className="align-self-center me-3">Dobrodošao, {userEmail}!</span>
            <button onClick={logout} className="btn btn-danger">Log Out</button>
          </>
        )}
      </div>
    </header>
  );
}

function App() {
  return (
    <Router>
      {/* Globalni stilovi za navigaciju, ako su potrebni */}
      <style>
        {`
        .nav-link { color: black !important; }
        .nav-link.active { color: #e91e63 !important; font-weight: bold; }
        .nav-link:hover { color: #e91e63 !important; }
        /* Stil za sticky header */
        .sticky-header {
          position: sticky;
          top: 0;
          z-index: 1020;
          width: 100%;
          background-color: white;
        }
        `}
      </style>
      <AuthProvider> {/* Omotajte cijelu aplikaciju s AuthProviderom */}
        <div className="App">
          <AppHeader /> {/* Koristimo izdvojenu komponentu za header */}

          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/service" element={<ServicePage />} />
            <Route path="/doctor/:id" element={<DoctorProfilePage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/sign-up" element={<SignUpPage />} />
            <Route path="/forgot-password" element={<ForgotPasswordRequestPage />} />
            <Route path="/reset-password" element={<ResetPasswordPage />} />

            {/* Zaštićene rute za dashboarde */}
            <Route
              path="/admin-dashboard"
              element={
                <ProtectedRoute requiredRoles={['ROLE_ADMIN']}>
                  <AdminDashboard />
                </ProtectedRoute>
              }
            />
            <Route
              path="/doctor-dashboard"
              element={
                <ProtectedRoute requiredRoles={['ROLE_DOCTOR']}>
                  <DoctorDashboard />
                </ProtectedRoute>
              }
            />
            <Route
              path="/patient-dashboard"
              element={
                <ProtectedRoute requiredRoles={['ROLE_PATIENT']}>
                  <PatientDashboard />
                </ProtectedRoute>
              }
            />
            {/* Zaštićena ruta za pacijentov profil (za doktore) */}
            <Route
              path="/doctor/patient-profile/:patientId"
              element={
                <ProtectedRoute requiredRoles={['ROLE_DOCTOR']}>
                  <PatientProfileForDoctorPage />
                </ProtectedRoute>
              }
            />

            {/* Ostale javne rute */}
            <Route path="/contact-us" element={<ContactUsPage />} />
            <Route path="/help" element={<HelpPage />} />
            <Route path="/blogs" element={<BlogsPage />} />
          </Routes>
        </div>
      </AuthProvider>
    </Router>
  );
}

export default App;
