  // DataModal komponenta unutar AdminDashboard-a
  const DataModal = ({ show, onClose, data, onSave, fields, title, idPrefix, authHeaders }) => {
    const prevTerminIDRef = useRef(null);
    const [shouldFetchTermDetails, setShouldFetchTermDetails] = useState(false);
    const [formData, setFormData] = useState({});

    const [localValidationErrors, setLocalValidationErrors] = useState({}); // Za klijentsku validaciju (ispod polja)
    const [apiErrorMessage, setApiErrorMessage] = useState(''); // Za backend API greške (na vrhu modala)

    useEffect(() => {
        console.log(`[DataModal ${idPrefix}] useEffect 1 (data/fields/idPrefix change): data prop changed:`, data);
        const newFormData = {};
        fields.forEach(field => {
            if (data === null || data[field.name] === undefined || data[field.name] === null) {
                if (field.name === 'specijalizacije') {
                    newFormData[field.name] = '';
                } else if (field.type === 'number') {
                    newFormData[field.name] = null;
                } else if (field.type === 'checkbox') {
                    newFormData[field.name] = false;
                } else {
                    newFormData[field.name] = '';
                }
            } else {
                let value = data[field.name];
                if (field.type === 'date' && typeof value === 'string' && value.includes('T')) {
                    value = value.split('T')[0];
                } else if (field.type === 'time' && typeof value === 'string' && value.length >= 5) {
                    value = value.substring(0, 5);
                } else if (field.name === 'specijalizacije' && Array.isArray(value)) {
                    value = value[0] || '';
                } else if (field.type === 'checkbox') {
                    value = Boolean(value);
                }
                newFormData[field.name] = value;
            }
        });
        setFormData(newFormData);
        prevTerminIDRef.current = (idPrefix === 'examination' && data && typeof data.terminID === 'number' && data.terminID > 0) ? data.terminID : null;
        setShouldFetchTermDetails(false);
        setLocalValidationErrors({}); // Očisti greške prilikom otvaranja/promjene podataka
        setApiErrorMessage('');      // Očisti API greške prilikom otvaranja/promjene podataka
        console.log(`[DataModal ${idPrefix}] useEffect 1: Set formData to:`, newFormData);
        console.log(`[DataModal ${idPrefix}] useEffect 1: prevTerminIDRef.current set to:`, prevTerminIDRef.current);
    }, [data, fields, idPrefix]);

    const handleTerminIDBlur = () => {
        if (idPrefix === 'examination') {
            const currentTerminID = formData.terminID;
            const isNumericAndValidTerminID = typeof currentTerminID === 'number' && currentTerminID > 0;

            console.log(`[DataModal ${idPrefix}] handleTerminIDBlur: currentTerminID: ${currentTerminID}, isNumericAndValidTerminID: ${isNumericAndValidTerminID}, prevTerminIDRef.current: ${prevTerminIDRef.current}`);

            if (isNumericAndValidTerminID && currentTerminID !== prevTerminIDRef.current) {
                console.log(`[DataModal ${idPrefix}] handleTerminIDBlur: Valid NEW Termin ID detected. Setting shouldFetchTermDetails to true.`);
                setShouldFetchTermDetails(true);
            } else if (!isNumericAndValidTerminID && prevTerminIDRef.current !== null) {
                console.log(`[DataModal ${idPrefix}] handleTerminIDBlur: Termin ID became invalid (${currentTerminID}) after being valid. Resetting associated fields.`);
                setFormData(prev => ({
                    ...prev,
                    doktorID: null,
                    datumPregleda: '',
                    vrijemePregleda: '',
                }));
                prevTerminIDRef.current = null;
                setShouldFetchTermDetails(false);
            } else if (currentTerminID === null || currentTerminID === 0 || currentTerminID === '') {
                console.log(`[DataModal ${idPrefix}] handleTerminIDBlur: Termin ID is null/0/empty. Resetting associated fields if they were populated by a previous term.`);
                if (formData.doktorID !== null || formData.datumPregleda !== '' || formData.vrijemePregleda !== '') {
                    setFormData(prev => ({
                        ...prev,
                        doktorID: null,
                        datumPregleda: '',
                        vrijemePregleda: '',
                    }));
                }
                prevTerminIDRef.current = null;
                setShouldFetchTermDetails(false);
            }
        }
    };


    useEffect(() => {
        if (shouldFetchTermDetails && idPrefix === 'examination') {
            const terminIDToFetch = formData.terminID;
            if (!(typeof terminIDToFetch === 'number' && terminIDToFetch > 0)) {
                console.warn(`[DataModal ${idPrefix}] useEffect Fetcher: Invalid terminID for fetching:`, terminIDToFetch);
                setShouldFetchTermDetails(false);
                return;
            }

            const fetchTermDetails = async () => {
                console.log(`[DataModal ${idPrefix}] useEffect Fetcher: Attempting to fetch details for ID: ${terminIDToFetch}`);
                try {
                    const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/termini/${terminIDToFetch}`, authHeaders);
                    const fetchedTerm = response.data;
                    console.log(`[DataModal ${idPrefix}] useEffect Fetcher SUCCESS: Term details fetched:`, fetchedTerm);

                    setFormData(prev => {
                        const newState = {
                            ...prev,
                            doktorID: fetchedTerm.doktorID,
                            datumPregleda: fetchedTerm.datum,
                            vrijemePregleda: fetchedTerm.vrijeme.substring(0, 5), // Only HH:mm
                        };
                        console.log(`[DataModal ${idPrefix}] useEffect Fetcher SUCCESS: Setting formData to:`, newState);
                        return newState;
                    });
                    prevTerminIDRef.current = terminIDToFetch;
                } catch (error) {
                    console.error(`[DataModal ${idPrefix}] useEffect Fetcher ERROR: Greška prilikom dohvaćanja detalja termina:`, error.response?.data || error.message);
                    setApiErrorMessage('Greška prilikom dohvaćanja detalja termina. Provjerite ID termina.'); // Prikaz greške unutar modala
                    setFormData(prev => ({
                        ...prev,
                        doktorID: null,
                        datumPregleda: '',
                        vrijemePregleda: '',
                    }));
                    prevTerminIDRef.current = null;
                } finally {
                    setShouldFetchTermDetails(false);
                }
            };
            fetchTermDetails();
        }
    }, [shouldFetchTermDetails, idPrefix, authHeaders, TERMINI_PREGLEDI_API_PREFIX]);

    const handleChange = (e) => {
      const { name, value, type, checked } = e.target;
      console.log(`[DataModal ${idPrefix}] handleChange: Field: ${name}, Value: "${value}", Type: ${type}`);
      console.log(`[DataModal ${idPrefix}] handleChange: Current formData BEFORE update for ${name}:`, formData);

      // Očisti specifičnu grešku polja kada korisnik počne kucati
      setLocalValidationErrors(prev => {
          const newState = { ...prev };
          delete newState[name];
          return newState;
      });
      setApiErrorMessage(''); // Očisti API grešku na bilo koju promjenu

      setFormData(prev => {
        let newValue = value;

        if (name === 'specijalizacije') {
            newValue = value;
        } else if (type === 'number') {
            newValue = value === '' ? null : Number(value);
            if (isNaN(newValue)) {
                newValue = null;
            }
        } else if (type === 'checkbox') {
            newValue = checked;
        }
        const newState = { ...prev, [name]: newValue };
        console.log(`[DataModal ${idPrefix}] handleChange: New formData state for ${name}:`, newState);
        return newState;
      });
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      setLocalValidationErrors({}); // Očisti prethodne lokalne greške
      setApiErrorMessage('');      // Očisti prethodne API greške

      const finalFormData = { ...formData };

      if (idPrefix === 'doctor' && typeof finalFormData.specijalizacije === 'string') {
          finalFormData.specijalizacije = finalFormData.specijalizacije ? [finalFormData.specijalizacije] : [];
      } else if (idPrefix === 'doctor' && finalFormData.specijalizacije === null) {
          finalFormData.specijalizacije = [];
      }


      let currentValidationErrors = {}; // Koristimo objekt za greške specifične za polja
      fields.forEach(field => {
        if (field.required) {
          if (field.type === 'text' || field.type === 'textarea' || field.type === 'date' || field.type === 'time' || field.type === 'select') {
            if (!finalFormData[field.name] || finalFormData[field.name].toString().trim() === '') {
              currentValidationErrors[field.name] = `${field.label} je obavezno polje.`;
            }
          } else if (field.type === 'number') {
            if (finalFormData[field.name] === null || finalFormData[field.name] === undefined || typeof finalFormData[field.name] !== 'number') {
                currentValidationErrors[field.name] = `${field.label} je obavezno polje i mora biti validan broj.`;
            } else if (field.name === 'iskustvo' && finalFormData[field.name] < 0) {
                 currentValidationErrors[field.name] = `Godine iskustva ne mogu biti negativne.`;
            } else if (field.name === 'ocjena' && (finalFormData[field.name] < 0 || finalFormData[field.name] > 5)) { // Pretpostavka: ocjena je 0-5
                 currentValidationErrors[field.name] = `Ocjena mora biti između 0 i 5.`;
            }
          } else if (field.type === 'email') {
              if (!finalFormData[field.name] || !/\S+@\S+\.\S+/.test(finalFormData[field.name])) {
                  currentValidationErrors[field.name] = `Email adresa mora biti validna.`;
              }
          } else if (field.name === 'password') {
             if (!finalFormData[field.name] || finalFormData[field.name].toString().length < 6) {
                 currentValidationErrors[field.name] = `Lozinka mora imati barem 6 karaktera.`;
             }
          }
        }
        // Dodatna validacija za format emaila ako nije obavezno, ali je uneseno
        if (field.type === 'email' && finalFormData[field.name] && finalFormData[field.name].trim() !== '' && !/\S+@\S+\.\S+/.test(finalFormData[field.name])) {
            currentValidationErrors[field.name] = `Email adresa mora biti validna.`;
        }
      });

      setLocalValidationErrors(currentValidationErrors);

      if (Object.keys(currentValidationErrors).length > 0) {
        // showNotification('Molimo ispravite greške u formi.', 'danger'); // NE TREBA GLOBALNA NOTIFIKACIJA ZA OVO
        setApiErrorMessage('Molimo ispravite greške u formi.'); // Prikazi generičku poruku u modalu
        return;
      }

      fields.forEach(field => {
          if (field.type === 'number' && (finalFormData[field.name] === null || finalFormData[field.name] === undefined || finalFormData[field.name] === '')) {
              if (field.name === 'iskustvo' || field.name === 'ocjena') {
                  finalFormData[field.name] = 0;
              } else {
                  finalFormData[field.name] = null;
              }
          }
      });


      if (idPrefix === 'term' && finalFormData.vrijeme) {
          if (finalFormData.vrijeme.length === 5) {
              finalFormData.vrijeme += ':00';
          }
      } else if (idPrefix === 'examination' && finalFormData.vrijemePregleda) {
          if (finalFormData.vrijemePregleda.length === 5) {
              finalFormData.vrijemePregleda += ':00';
          }
      }

      console.log(`[DataModal ${idPrefix}] handleSubmit: Final payload for submission:`, finalFormData);

      // Pozivamo onSave i očekujemo rezultat objekt
      const result = await onSave(finalFormData); // onSave sada vraća { success: boolean, message: string }
      if (!result.success) {
          setApiErrorMessage(result.message); // Prikazi API grešku direktno unutar modala
          // showNotification('Došlo je do greške prilikom spremanja.', 'danger'); // NE TREBA GLOBALNA NOTIFIKACIJA ZA OVO
      } else {
           showNotification(result.message, 'success'); // Prikazi uspješnu poruku kao globalnu notifikaciju
           onClose(); // Zatvori modal na uspjeh
      }
    };

    return (
      <Modal show={show} onHide={onClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {apiErrorMessage && ( // Prikaz API greške na vrhu tijela modala
              <Alert variant="danger" className="mb-3">
                {apiErrorMessage}
              </Alert>
            )}
            {fields.map(field => (
              <Form.Group className="mb-3" controlId={`${idPrefix}-${field.name}`} key={field.name}>
                <Form.Label>{field.label}</Form.Label>
                {field.type === 'select' ? (
                  <Form.Select
                    name={field.name}
                    value={formData[field.name] === null ? '' : formData[field.name]}
                    onChange={handleChange}
                    required={field.required}
                    isInvalid={!!localValidationErrors[field.name]} // Primijeni Bootstrap stil za nevalidnost
                  >
                    <option value="">Odaberi {field.label}</option>
                    {field.options.map(option => (
                      <option key={option} value={option}>{option}</option>
                    ))}
                  </Form.Select>
                ) : field.type === 'textarea' ? (
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                    isInvalid={!!localValidationErrors[field.name]}
                  />
                ) : field.type === 'checkbox' ? (
                    <Form.Check
                        type="checkbox"
                        name={field.name}
                        checked={formData[field.name] || false}
                        onChange={handleChange}
                        label={field.label}
                    />
                ) : (
                  <Form.Control
                    type={field.type || 'text'}
                    name={field.name}
                    value={
                        (formData[field.name] === null || formData[field.name] === undefined || formData[field.name] === 'N/A')
                        ? ''
                        : formData[field.name]
                    }
                    onChange={handleChange}
                    required={field.required}
                    readOnly={field.readOnly || (idPrefix === 'examination' && (field.name === 'doktorID' || field.name === 'datumPregleda' || field.name === 'vrijemePregleda'))}
                    onBlur={field.name === 'terminID' && idPrefix === 'examination' ? handleTerminIDBlur : undefined}
                    isInvalid={!!localValidationErrors[field.name]} // Primijeni Bootstrap stil za nevalidnost
                  />
                )}
                {localValidationErrors[field.name] && ( // Prikaz poruke o lokalnoj validacijskoj grešci ispod polja
                  <Form.Control.Feedback type="invalid">
                    {localValidationErrors[field.name]}
                  </Form.Control.Feedback>
                )}
              </Form.Group>
            ))}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={onClose}>
              Zatvori
            </Button>
            <Button variant="primary" type="submit">
              Sačuvaj promjene
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    );
  };

  const Pagination = ({ itemsPerPage, totalItems, currentPage, paginate }) => {
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
      pageNumbers.push(i);
    }

    return (
      <nav>
        <ul className="pagination justify-content-center mt-4">
          {pageNumbers.map(number => (
            <li key={number} className={`page-item ${currentPage === number ? 'active' : ''}`}>
              <button onClick={() => paginate(number)} className="page-link">
                {number}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    );
  };


  const renderContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div>
            <h4 className="mb-4">Pregled nadzorne ploče</h4>
            <div className="row g-4">
              <div className="col-md-3">
                <div className="card text-white bg-primary mb-3">
                  <div className="card-header">Ukupno doktora</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{doctors.length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="card text-white bg-success mb-3">
                  <div className="card-header">Ukupno pacijenata</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{patients.length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="card text-white bg-info mb-3">
                  <div className="card-header">Dostupni termini</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{terms.filter(t => t.dostupnost === true).length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="card text-white bg-warning mb-3">
                  <div className="card-header">Pregledi na čekanju</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{examinations.filter(exam => exam.status === 'zakazan').length}</h5>
                  </div>
                </div>
              </div>
            </div>
            <div className="mt-5">
              <h5>Brze akcije</h5>
              <div className="d-flex gap-2 flex-wrap">
                <button className="btn btn-outline-primary" onClick={() => openDoctorModal()}>Dodaj novog doktora</button>
                <button className="btn btn-outline-success" onClick={() => openPatientModal()}>Dodaj novog pacijenta</button>
                <button className="btn btn-outline-info" onClick={() => openTermModal()}>Dodaj novi termin</button>
                {/* UKLONJENO: Dugme za dodavanje novog pregleda */}
              </div>
            </div>
          </div>
        );
      case 'doctors':
        return (
          <div>
            <h4 className="mb-4">Upravljanje doktorima</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openDoctorModal()}>Dodaj novog doktora</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Pretraži doktore..."
                value={doctorSearchTerm}
                onChange={(e) => setDoctorSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Ime</th>
                    <th>Specijalnost</th>
                    <th>Grad</th>
                    <th>Email</th>
                    <th>Telefon</th>
                    <th>Radno Vrijeme</th>
                    <th>Iskustvo</th>
                    <th>Ocjena</th>
                    <th>Akcije</th>
                  </tr>
                </thead>
                <tbody>
                  {currentDoctors.length > 0 ? (
                    currentDoctors.map(doctor => (
                      <tr key={doctor.id}>
                        <td>{doctor.id}</td>
                        <td>{doctor.name}</td>
                        <td>{doctor.specialty}</td>
                        <td>{doctor.city}</td>
                        <td>{doctor.email}</td>
                        <td>{doctor.telefon}</td>
                        <td>{doctor.radnoVrijeme}</td>
                        <td>{doctor.iskustvo}</td>
                        <td>{doctor.ocjena}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openDoctorModal(doctor)}>Uredi</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteDoctor(doctor.id)}>Obriši</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="10" className="text-center">Nema pronađenih doktora.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredDoctors.length}
              currentPage={doctorsCurrentPage}
              paginate={setDoctorsCurrentPage}
            />
          </div>
        );
      case 'patients':
        return (
          <div>
            <h4 className="mb-4">Upravljanje pacijentima</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openPatientModal()}>Dodaj novog pacijenta</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Pretraži pacijente..."
                value={patientSearchTerm}
                onChange={(e) => setPatientSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Ime</th>
                    <th>Email</th>
                    <th>Telefon</th>
                    <th>Akcije</th>
                  </tr>
                </thead>
                <tbody>
                  {currentPatients.length > 0 ? (
                    currentPatients.map(patient => (
                      <tr key={patient.id}>
                        <td>{patient.id}</td>
                        <td>{patient.name}</td>
                        <td>{patient.email}</td>
                        <td>{patient.telefon}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openPatientModal(patient)}>Uredi</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeletePatient(patient.id)}>Obriši</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center">Nema pronađenih pacijenata.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredPatients.length}
              currentPage={patientsCurrentPage}
              paginate={setPatientsCurrentPage}
            />
          </div>
        );
      case 'terms':
        return (
          <div>
            <h4 className="mb-4">Upravljanje terminima</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openTermModal()}>Dodaj novi termin</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Pretraži termine..."
                value={termSearchTerm}
                onChange={(e) => setTermSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>ID Doktora</th>
                    <th>Datum</th>
                    <th>Vrijeme</th>
                    <th>Dostupnost</th>
                    <th>Akcije</th>
                  </tr>
                </thead>
                <tbody>
                  {currentTerms.length > 0 ? (
                    currentTerms.map(term => (
                      <tr key={term.id}>
                        <td>{term.id}</td>
                        <td>{term.doktorID}</td>
                        <td>{term.datum}</td>
                        <td>{term.vrijeme}</td>
                        <td>{term.dostupnost ? 'Dostupan' : 'Zauzet'}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openTermModal(term)}>Uredi</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteTerm(term.id)}>Obriši</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6" className="text-center">Nema pronađenih termina.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredTerms.length}
              currentPage={termsCurrentPage}
              paginate={setTermsCurrentPage}
            />
          </div>
        );
      case 'examinations':
        return (
          <div>
            <h4 className="mb-4">Upravljanje pregledima</h4>
            <div className="d-flex justify-content-between mb-3">
              {/* UKLONJENO: Dugme za dodavanje novog pregleda u Examinations tabu */}
              <input
                type="text"
                className="form-control w-25"
                placeholder="Pretraži preglede..."
                value={examinationSearchTerm}
                onChange={(e) => setExaminationSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Pacijent</th>
                    <th>Doktor</th>
                    <th>ID Termina</th>
                    <th>Datum</th>
                    <th>Vrijeme</th>
                    <th>Status</th>
                    <th>Komentar Pacijenta</th>
                    <th>Ocjena Doktora</th>
                    <th>Akcije</th>
                  </tr>
                </thead>
                <tbody>
                  {currentExaminations.length > 0 ? (
                    currentExaminations.map(examination => (
                      <tr key={examination.id}>
                        <td>{examination.id}</td>
                        <td>{examination.patientName}</td>
                        <td>{examination.doctorName}</td>
                        <td>{examination.terminID}</td>
                        <td>{examination.datumPregleda}</td>
                        <td>{examination.vrijemePregleda}</td>
                        <td>
                          <select
                            className="form-select"
                            value={examination.status}
                            onChange={(e) => handleUpdateExamination(
                                {
                                  ...examination,
                                  status: e.target.value,
                                  datumPregleda: examination.datumPregleda,
                                  vrijemePregleda: examination.vrijemePregleda
                                }
                            )}
                          >
                            <option value="zakazan">Zakazan</option>
                            <option value="obavljen">Obavljen</option>
                            <option value="otkazan">Otkazan</option>
                          </select>
                        </td>
                        <td>{examination.komentarPacijenta}</td>
                        <td>{examination.ocjenaDoktora}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openExaminationModal(examination)}>Uredi</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteExamination(examination.id)}>Obriši</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="10" className="text-center">Nema pronađenih pregleda.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredExaminations.length}
              currentPage={examinationsCurrentPage.current}
              paginate={(page) => { examinationsCurrentPage.current = page; /* Force re-render if needed or paginate table differently */ }}
            />
          </div>
        );
      default:
        return <p>Odaberite karticu za prikaz sadržaja.</p>;
    }
  };


  if (loadingInitial) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Učitavanje administratorskog panela...</span>
        </div>
      </div>
    );
  }

  if (!loadingInitial && !token && isAuthChecked) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
        <div className="text-center">
          <h2 className="text-danger">Pristup odbijen</h2>
          <p className="lead">Niste autorizovani za pristup ovoj stranici. Molimo prijavite se kao administrator.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      {showAlert && (
        <Alert variant={alertType} onClose={() => setShowAlert(false)} dismissible className="position-fixed top-0 start-50 translate-middle-x mt-3" style={{ zIndex: 1050 }}>
          {alertMessage}
        </Alert>
      )}

      <Modal show={showConfirmModal} onHide={() => setShowConfirmModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>{confirmTitle}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {confirmMessage}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowConfirmModal(false)}>
            Poništi
          </Button>
          <Button variant="danger" onClick={handleConfirmAction}>
            Potvrdi
          </Button>
        </Modal.Footer>
      </Modal>

      {showDoctorModal && (
        <DataModal
          show={showDoctorModal}
          onClose={() => {setShowDoctorModal(false); setEditingDoctor(null);}}
          data={editingDoctor}
          onSave={editingDoctor ? handleUpdateDoctor : handleAddDoctor}
          title={editingDoctor ? 'Uredi Doktora' : 'Dodaj Novog Doktora'}
          idPrefix="doctor"
          fields={editingDoctor ? editDoctorFields : addDoctorFields}
          authHeaders={authHeaders}
          // showNotification={showNotification} // Uklonjeno: DataModal sam upravlja svojim greškama
          key={editingDoctor ? `edit-doctor-${editingDoctor.id}` : 'add-doctor'}
          onSuccess={() => fetchDoctors()} // Dodano: Callback za osvježavanje liste doktora
        />
      )}

      {showPatientModal && (
        <DataModal
          show={showPatientModal}
          onClose={() => {setShowPatientModal(false); setEditingPatient(null);}}
          data={editingPatient}
          onSave={editingPatient ? handleUpdatePatient : handleAddPatient}
          title={editingPatient ? 'Uredi Pacijenta' : 'Dodaj Novog Pacijenta'}
          idPrefix="patient"
          fields={editingPatient ? editPatientFields : addPatientFields}
          authHeaders={authHeaders}
          // showNotification={showNotification} // Uklonjeno
          key={editingPatient ? `edit-patient-${editingPatient.id}` : 'add-patient'}
          onSuccess={() => fetchPatients()} // Dodano: Callback za osvježavanje liste pacijenata
        />
      )}

      {showTermModal && (
        <DataModal
          show={showTermModal}
          onClose={() => {setShowTermModal(false); setEditingTerm(null);}}
          data={editingTerm}
          onSave={editingTerm ? handleUpdateTerm : handleAddTerm}
          title={editingTerm ? 'Uredi Termin' : 'Dodaj Novi Termin'}
          idPrefix="term"
          fields={editingTerm ? editTermFields : addTermFields}
          authHeaders={authHeaders}
          // showNotification={showNotification} // Uklonjeno
          key={editingTerm ? `edit-term-${editingTerm.id}` : 'add-term'}
          onSuccess={() => fetchTerms()} // Dodano: Callback za osvježavanje liste termina
        />
      )}

      {showExaminationModal && (
        <DataModal
          show={showExaminationModal}
          onClose={() => {setShowExaminationModal(false); setEditingExamination(null);}}
          data={editingExamination} // Uvijek se proslijeđuju podaci za uređivanje
          onSave={handleUpdateExamination} // Samo funkcija za update
          title={'Uredi Pregled'} // Uvijek "Uredi Pregled"
          idPrefix="examination"
          fields={editExaminationFields} // Uvijek polja za uređivanje
          authHeaders={authHeaders}
          // showNotification={showNotification} // Uklonjeno
          key={editingExamination ? `edit-examination-${editingExamination.id}` : 'examination-modal'}
          onSuccess={() => fetchExaminations()} // Dodano: Callback za osvježavanje liste pregleda
        />
      )}

      <div className="row">
        <nav className="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse" style={{ minHeight: '100vh' }}>
          <div className="position-sticky pt-3">
            <ul className="nav flex-column">
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'overview' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('overview')}>
                  Pregled
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'doctors' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('doctors')}>
                  Doktori
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'patients' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('patients')}>
                  Pacijenti
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'terms' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('terms')}>
                  Termini
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'examinations' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('examinations')}>
                  Pregledi
                </a>
              </li>
            </ul>
          </div>
        </nav>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4 py-4">
          {renderContent()}
        </main>
      </div>
    </div>
  );
}

export default AdminDashboard;
