import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link

function ForgotPasswordRequestPage() {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');
    try {
          const response = await axios.post(
            'http://localhost:8082/auth/forgot-password-request',
            { email: email }
          );
          setMessage(response.data);
    } catch (err) {
      if (err.response) {
        setError(err.response.data);
      } else {
        setError("Došlo je do greške prilikom slanja zahtjeva.");
      }
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header text-center bg-danger text-white">
              <h2>Zaboravili ste lozinku?</h2>
            </div>
            <div className="card-body">
              {message && <div className="alert alert-success">{message}</div>}
              {error && <div className="alert alert-danger">{error}</div>}
              <p className="text-center">Unesite Vašu email adresu i poslat ćemo Vam link za resetovanje lozinke.</p>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="emailInput" className="form-label">Email address</label>
                  <input
                    type="email"
                    className="form-control"
                    id="emailInput"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className="d-grid gap-2">
                  <button type="submit" className="btn btn-danger">Pošalji zahtjev za resetovanje</button>
                  <Link to="/login" className="text-center text-danger mt-2">Nazad na prijavu</Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ForgotPasswordRequestPage;
