import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect, useMemo } from 'react';
import { Modal, Button, Form, Alert, Spinner } from 'react-bootstrap';

const GATEWAY_BASE_URL = 'http://localhost:8081';

const KORISNICI_DOKTORI_API_PREFIX = `${GATEWAY_BASE_URL}/api/korisnici-doktori`;
const TERMINI_PREGLEDI_API_PREFIX = `${GATEWAY_BASE_URL}/api/termini-pregledi`;
const OBAVIJESTI_DOKUMENTI_API_PREFIX = `${GATEWAY_BASE_URL}/api/obavijesti-dokumentacija`;

function DoctorDashboard() {
    const [activeTab, setActiveTab] = useState('overview');

    const [doctors, setDoctors] = useState([]);
    const [patients, setPatients] = useState([]);
    const [examinations, setExaminations] = useState([]);
    const [terms, setTerms] = useState([]);
    const [activityLog, setActivityLog] = useState([]);

    const [currentDoctorProfile, setCurrentDoctorProfile] = useState(null);
    const [isEditingProfile, setIsEditingProfile] = useState(false);

    const [editIme, setEditIme] = useState('');
    const [editPrezime, setEditPrezime] = useState('');
    const [editEmail, setEditEmail] = useState('');
    const [editTelefon, setEditTelefon] = useState('');
    const [editSpecijalizacije, setEditSpecijalizacije] = useState('');
    const [editGrad, setEditGrad] = useState('');
    const [editRadnoVrijeme, setEditRadnoVrijeme] = useState('');
    const [editIskustvo, setEditIskustvo] = useState('');
    const [editOcjena, setEditOcjena] = useState('');
    const [profileImageFile, setProfileImageFile] = useState(null);
    const [profileImagePreview, setProfileImagePreview] = useState(null);

    const [documents, setDocuments] = useState([]);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState({ recipientId: '', subject: '', content: '' });
    const [replyMessage, setReplyMessage] = useState({ messageId: '', content: '' });
    const [selectedMessage, setSelectedMessage] = useState(null);

    const [showAlert, setShowAlert] = useState(false);
    const [alertMessage, setAlertMessage] = useState('');
    const [alertType, setAlertType] = useState('success');

    const [patientSearchTerm, setPatientSearchTerm] = useState('');
    const [examinationSearchTerm, setExaminationSearchTerm] = useState('');
    const [examinationFilterStatus, setExaminationFilterStatus] = useState('All');

    const [myPatientsCurrentPage, setMyPatientsCurrentPage] = useState(1);
    const [myExaminationsCurrentPage, setMyExaminationsCurrentPage] = useState(1);
    const itemsPerPage = 5;

    const [showPatientDetailModal, setShowPatientDetailModal] = useState(false);
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [patientNotes, setPatientNotes] = useState('');

    const { token, user, loading: authLoading } = useAuth(); // 'logout' button removed, so logout is not needed here

    const authHeaders = useMemo(() => ({
        headers: {
            'Authorization': `Bearer ${token}`
        }
    }), [token]);

    const showNotification = (message, type = 'success') => {
        setAlertMessage(message);
        setAlertType(type);
        setShowAlert(true);
        setTimeout(() => {
            setShowAlert(false);
            setAlertMessage('');
        }, 3000);
    };

    const fetchDoctors = async () => {
        if (!token) return;
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori`, authHeaders);
            setDoctors(response.data.map(doc => ({
                id: doc.doktorID,
                userId: doc.userId,
                name: `${doc.ime} ${doc.prezime}`,
                ime: doc.ime,
                prezime: doc.prezime,
                specialty: Array.isArray(doc.specijalizacije) && doc.specijalizacije.length > 0
                           ? doc.specijalizacije.join(', ')
                           : 'N/A',
                grad: doc.grad,
                email: doc.email,
                telefon: doc.telefon,
                radnoVrijeme: doc.radnoVrijeme,
                iskustvo: doc.iskustvo,
                ocjena: doc.ocjena,
                profileImageBase64: doc.profileImageBase64,
            })));
        } catch (error) {
            console.error("Error fetching all doctors:", error.response?.data || error.message);
            showNotification("Error fetching doctors.", "danger");
        }
    };

    const fetchPatients = async () => {
        if (!token) return;
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti`, authHeaders);
            setPatients(response.data.map(pat => ({
                id: pat.pacijentID,
                userId: pat.userId,
                name: `${pat.ime} ${pat.prezime}`,
                ime: pat.ime,
                prezime: pat.prezime,
                email: pat.email,
                phone: pat.telefon,
            })));
        } catch (error) {
            console.error("Error fetching all patients:", error.response?.data || error.message);
            showNotification("Error fetching patients.", "danger");
        }
    };

    const fetchTerms = async () => {
        if (!token) return;
        try {
            const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/termini`, authHeaders);
            setTerms(response.data.map(term => ({
                id: term.terminID,
                doktorId: term.doktorID,
                datum: term.datum,
                vrijeme: term.vrijeme,
                dostupnost: term.dostupnost,
            })));
        } catch (error) {
            console.error("Error fetching all terms:", error.response?.data || error.message);
            showNotification("Error fetching terms.", "danger");
        }
    };

    const fetchExaminations = async () => {
        if (!token) return;
        try {
            const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi`, authHeaders);
            setExaminations(response.data.map(exam => ({
                id: exam.pregledID,
                pacijentId: exam.pacijentID,
                doktorId: exam.doktorID,
                patientName: exam.pacijentIme || 'N/A',
                doctorName: exam.doktorIme || 'N/A',
                date: exam.datumPregleda,
                time: exam.vrijemePregleda,
                status: exam.status,
                komentarPacijenta: exam.komentarPacijenta || '',
                ocjenaDoktora: exam.ocjenaDoktora || null,
                terminID: exam.terminID, // <--- AŽURIRANO OVDJE: direktno dohvati terminID
            })));
        } catch (error) {
            console.error("Error fetching all examinations:", error.response?.data || error.message);
            showNotification("Error fetching examinations.", "danger");
        }
    };

    const fetchActivityLog = async () => {
        if (!token) return;
        try {
            console.warn("Activity Log endpoint is not defined, skipping fetching activity log.");
            setActivityLog([]);
        } catch (error) {
            console.error("Error fetching activity log:", error.response?.data || error.message);
            showNotification("Error fetching activity log.", "danger");
        }
    };

    const fetchPatientNotes = async (patientId) => {
        if (!token) return;
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${patientId}/notes`, authHeaders);
            setPatientNotes(response.data.notes || '');
        } catch (error) {
            console.error("Error fetching patient notes:", error.response?.data || error.message);
            setPatientNotes('');
            showNotification("Error fetching patient notes.", "danger");
        }
    };

    const handleSavePatientNotes = async () => {
        if (!token || !selectedPatient) return;
        try {
            await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${selectedPatient.id}/notes`, { notes: patientNotes }, authHeaders);
            showNotification("Notes successfully saved!");
            fetchActivityLog();
            setShowPatientDetailModal(false);
        }
        catch (error) {
            console.error("Error saving notes:", error.response?.data || error.message);
            showNotification(error.response?.data?.message || 'Error saving notes.', 'danger');
        }
    };

    const openPatientDetailModal = async (patient) => {
        setSelectedPatient(patient);
        await fetchPatientNotes(patient.id);
        setShowPatientDetailModal(true);
    };

    const closePatientDetailModal = () => {
        setShowPatientDetailModal(false);
        setSelectedPatient(null);
        setPatientNotes('');
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setProfileImageFile(file);
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfileImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        } else {
            setProfileImageFile(null);
            setProfileImagePreview(null);
        }
    };

    const fetchCurrentDoctorProfile = async () => {
        if (!token || !user || user.doktorId === null || user.doktorId === undefined) {
            console.warn("Cannot fetch current doctor profile: doktorId is missing or auth not ready.");
            return;
        }
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori/${user.doktorId}`, authHeaders);
            const profileData = response.data;
            setCurrentDoctorProfile(profileData);
            setEditIme(profileData.ime || '');
            setEditPrezime(profileData.prezime || '');
            setEditEmail(profileData.email || '');
            setEditTelefon(profileData.telefon || '');
            setEditSpecijalizacije(Array.isArray(profileData.specijalizacije) ? profileData.specijalizacije.join(', ') : '');
            setEditGrad(profileData.grad || '');
            setEditRadnoVrijeme(profileData.radnoVrijeme || '');
            setEditIskustvo(profileData.iskustvo || '');
            setEditOcjena(profileData.ocjena || '');
            setProfileImagePreview(profileData.profileImageBase64 || null);
        } catch (error) {
            console.error("Error fetching doctor profile:", error.response?.data || error.message);
            showNotification("Error fetching doctor profile.", "danger");
        }
    };

    const handleUpdateDoctorProfile = async (e) => {
        e.preventDefault();
        if (!token || !user || user.doktorId === null || user.doktorId === undefined) {
            showNotification("You are not authorized or doctor ID is missing.", "danger");
            return;
        }

        const updatedProfile = {
            doktorID: user.doktorId,
            userId: currentDoctorProfile.userId,
            ime: editIme,
            prezime: editPrezime,
            email: editEmail,
            telefon: editTelefon,
            specijalizacije: editSpecijalizacije.split(',').map(s => s.trim()).filter(s => s !== ''),
            grad: editGrad,
            radnoVrijeme: editRadnoVrijeme,
            iskustvo: parseInt(editIskustvo, 10),
            ocjena: parseFloat(editOcjena) || 0.0,
            profileImageBase64: profileImagePreview,
        };

        try {
            const response = await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori/${user.doktorId}`, updatedProfile, authHeaders);
            setCurrentDoctorProfile(response.data);
            setIsEditingProfile(false);
            showNotification("Profile successfully updated!");
            fetchDoctors();
        } catch (error) {
            console.error("Error updating profile:", error.response?.data || error.message);
            showNotification(error.response?.data?.message || 'Error updating profile.', 'danger');
        }
    };

    const fetchDocuments = async () => {
        if (!token || !user || user.doktorId === null || user.doktorId === undefined) {
            console.warn("Cannot fetch documents: doktorId is missing or auth not ready.");
            return;
        }
        try {
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/doktor/${user.doktorId}`, authHeaders);
            const transformedDocuments = response.data.map(doc => ({
                id: doc.dokumentID,
                fileName: doc.nazivDokumenta,
                documentType: doc.tipDokumenta,
                uploadDate: doc.datumKreiranja,
                pristup: doc.pristup,
                pacijentIme: doc.pacijentIme
            }));
            setDocuments(transformedDocuments);
            showNotification("Documents successfully fetched!", "success");
        } catch (error) {
            console.error('Error fetching documents for doctor:', error.response?.data || error.message);
            showNotification('Failed to fetch documents.', "danger");
        }
    };

    const handleDocumentUpload = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        if (!user || user.doktorId === null || user.doktorId === undefined) {
            showNotification("Doctor ID is not available for document upload.", "danger");
            return;
        }

        const formData = new FormData();
        formData.append('file', file);
        formData.append('doktorID', user.doktorId);
        formData.append('tipDokumenta', 'Doctor Upload');
        formData.append('pristup', 'PRIVATNA');

        try {
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/upload`, formData, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data'
                }
            });
            showNotification('Document successfully uploaded!', 'success');
            fetchDocuments();
        } catch (error) {
            console.error('Error uploading document:', error.response?.data || error.message);
            showNotification('Document upload failed. ' + (error.response?.data?.message || error.message), "danger");
        }
    };

    const fetchMessages = async () => {
        if (!token || !user || user.doktorId === null || user.doktorId === undefined) {
            console.warn("Cannot fetch messages: doktorId is missing or auth not ready.");
            return;
        }
        try {
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/primatelj/${user.doktorId}?receiverType=DOKTOR`, authHeaders);
            const transformedMessages = response.data.map(msg => ({
                id: msg.porukaID,
                senderId: msg.senderId,
                senderType: msg.senderType,
                senderIme: msg.senderIme,
                receiverId: msg.receiverId,
                receiverType: msg.receiverType,
                receiverIme: msg.receiverIme,
                subject: msg.subject,
                content: msg.content,
                timestamp: msg.timestamp,
                replies: msg.replies || []
            }));
            setMessages(transformedMessages);
            showNotification("Messages successfully fetched!", "success");
        } catch (error) {
            console.error('Error fetching messages for doctor:', error.response?.data || error.message);
            showNotification('Failed to fetch messages.', "danger");
        }
    };

    const handleNewMessageChange = (e) => {
        const { name, value } = e.target;
        setNewMessage(prev => ({ ...prev, [name]: value }));
    };

    const handleSendMessage = async () => {
        if (!user || user.doktorId === null || user.doktorId === undefined) {
            showNotification('Doctor ID is not available. Cannot send message.', 'danger');
            return;
        }
        if (!newMessage.recipientId || !newMessage.subject || !newMessage.content) {
            showNotification('Please fill in all message fields.', 'warning');
            return;
        }

        try {
            const messageData = {
                senderId: user.doktorId,
                senderType: 'DOKTOR',
                receiverId: parseInt(newMessage.recipientId),
                receiverType: 'PACIJENT',
                subject: newMessage.subject,
                content: newMessage.content
            };
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/posalji`, messageData, authHeaders);
            showNotification('Message successfully sent!', 'success');
            setNewMessage({ recipientId: '', subject: '', content: '' });
            fetchMessages();
        } catch (error) {
            console.error('Error sending message:', error.response?.data || error.message);
            showNotification('Failed to send message. ' + (error.response?.data?.message || error.message), "danger");
        }
    };

    const handleReplyMessageChange = (e) => {
        setReplyMessage(prev => ({ ...prev, content: e.target.value }));
    };

    const handleReply = async (messageId) => {
        if (!user || user.doktorId === null || user.doktorId === undefined) {
            showNotification('Doctor ID is not available. Cannot reply.', 'danger');
            return;
        }
        if (!replyMessage.content) {
            showNotification('Reply content cannot be empty.', 'warning');
            return;
        }
        try {
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/${messageId}/odgovori`, {
                senderId: user.doktorId,
                senderType: 'DOKTOR',
                content: replyMessage.content
            }, authHeaders);
            showNotification('Reply successfully sent!', 'success');
            setReplyMessage({ messageId: '', content: '' });
            fetchMessages();
            setSelectedMessage(prev => ({
                ...prev,
                replies: [...(prev.replies || []), {
                    senderId: user.doktorId,
                    senderIme: `${user.firstName} ${user.lastName}`,
                    senderType: 'DOKTOR',
                    content: replyMessage.content,
                    timestamp: new Date().toISOString()
                }]
            }));
        } catch (error) {
            console.error('Error sending reply:', error.response?.data || error.message);
            showNotification('Failed to send reply. ' + (error.response?.data?.message || error.message), "danger");
        }
    };

    const handleViewMessage = (message) => {
        setSelectedMessage(message);
    };

    const closeMessageModal = () => {
        setSelectedMessage(null);
        setReplyMessage({ messageId: '', content: '' });
    };

    useEffect(() => {
        console.log("DoctorDashboard useEffect: token=", token, "user=", user, "authLoading=", authLoading);
        if (!authLoading && token && user && user.doktorId) {
            console.log("DoctorDashboard useEffect: Initiating data fetching for doktorId:", user.doktorId);
            fetchDoctors();
            fetchPatients();
            fetchTerms();
            fetchExaminations();
            fetchActivityLog();
            fetchCurrentDoctorProfile();
            fetchDocuments();
            fetchMessages();
        } else if (!authLoading) {
            console.log("DoctorDashboard useEffect: Data fetching skipped because condition not met.", { token, user, doktorId: user?.doktorId, authLoading });
        }
    }, [token, user, authLoading]);

    console.log("DoctorDashboard render check: token=", token, "user=", user, "user.doktorId=", user?.doktorId, "authLoading=", authLoading);
    if (authLoading) {
        console.log("DoctorDashboard: Still loading authentication...");
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
                <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
                <p className="ms-3">Loading authentication...</p>
            </div>
        );
    }
    if (!token || !user || user.doktorId === null || user.doktorId === undefined) {
        console.log("DoctorDashboard: Not authorized as doctor or incomplete data. Conditions:", { token: !!token, user: !!user, doktorIdDefined: user?.doktorId !== null && user?.doktorId !== undefined });
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
                <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
                <p className="ms-3">Not authorized as a doctor or incomplete data.</p>
            </div>
        );
    }

    const currentDoctorId = user.doktorId;
    console.log("DoctorDashboard: Current doctor ID for filtering (after initial checks):", currentDoctorId);

    const doctorsExaminations = examinations.filter(
        (examination) => examination.doktorId === currentDoctorId
    );

    const patientIdsWithExaminations = new Set(
        doctorsExaminations.map((exam) => exam.pacijentId)
    );
    const myPatients = patients.filter((patient) =>
        patientIdsWithExaminations.has(patient.id)
    );

    const handleUpdateExaminationStatus = async (id, newStatus) => {
        try {
            const existingExamination = examinations.find(exam => exam.id === id);
            if (!existingExamination) {
                showNotification('Examination not found for status update.', 'danger');
                return;
            }

            console.log("Existing Examination object before update:", existingExamination);
            const terminIdToSend = existingExamination.terminID; // DIREKTNO KORIŠTENJE terminID
            console.log("Termin ID to send:", terminIdToSend);

            if (terminIdToSend === null || terminIdToSend === undefined) {
                showNotification('Error: Term ID is missing for this examination. Please ensure all examinations have a valid term.', 'danger');
                return;
            }

            const ratingToSend = newStatus === 'obavljen' ? (existingExamination.ocjenaDoktora || 5.0) : existingExamination.ocjenaDoktora;


            const examinationPayload = {
                pregledID: existingExamination.id,
                pacijentID: existingExamination.pacijentId,
                doktorID: existingExamination.doktorId,
                datumPregleda: existingExamination.date.split('T')[0],
                vrijemePregleda: existingExamination.time.split('.')[0],
                status: newStatus,
                komentarPacijenta: existingExamination.komentarPacijenta,
                ocjenaDoktora: ratingToSend,
                terminID: terminIdToSend,
            };

            const response = await axios.put(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/${id}`, examinationPayload, authHeaders);
            const updatedExam = {
                id: response.data.pregledID,
                pacijentId: response.data.pacijentID,
                doktorId: response.data.doktorID,
                patientName: response.data.pacijentIme || 'N/A',
                doctorName: response.data.doktorIme || 'N/A',
                date: response.data.datumPregleda,
                time: response.data.vrijemePregleda,
                status: response.data.status,
                komentarPacijenta: response.data.komentarPacijenta || '',
                ocjenaDoktora: response.data.ocjenaDoktora || null,
                terminID: response.data.terminID, // Update the state with the received terminID
            };
            setExaminations(examinations.map(exam => (exam.id === id ? updatedExam : exam)));
            fetchActivityLog();
            showNotification(`Examination status updated to ${newStatus}!`, 'info');
        } catch (error) {
            console.error("Error updating examination status:", error.response?.data || error.message);
            showNotification(error.response?.data?.message || 'Error updating examination status.', 'danger');
        }
    };

    const filteredMyPatients = myPatients.filter(patient =>
        (patient.name && patient.name.toLowerCase().includes(patientSearchTerm.toLowerCase())) ||
        (patient.email && patient.email.toLowerCase().includes(patientSearchTerm.toLowerCase())) ||
        (patient.phone && patient.phone.toLowerCase().includes(patientSearchTerm.toLowerCase()))
    );

    const indexOfLastMyPatient = myPatientsCurrentPage * itemsPerPage;
    const indexOfFirstMyPatient = indexOfLastMyPatient - itemsPerPage;
    const currentMyPatients = filteredMyPatients.slice(indexOfFirstMyPatient, indexOfLastMyPatient);
    const totalMyPatientPages = Math.ceil(filteredMyPatients.length / itemsPerPage);

    const filteredMyExaminations = doctorsExaminations.filter(examination => {
        const matchesSearch = (examination.patientName && examination.patientName.toLowerCase().includes(examinationSearchTerm.toLowerCase())) ||
            (examination.status && examination.status.toLowerCase().includes(examinationSearchTerm.toLowerCase())) ||
            (examination.date && new Date(examination.date).toLocaleDateString().includes(examinationSearchTerm));

        const matchesFilterStatus = examinationFilterStatus === 'All' || examination.status === examinationFilterStatus;

        return matchesSearch && matchesFilterStatus;
    });

    const indexOfLastMyExaminations = myExaminationsCurrentPage * itemsPerPage;
    const indexOfFirstMyExaminations = indexOfLastMyExaminations - itemsPerPage;
    const currentMyExaminations = filteredMyExaminations.slice(indexOfFirstMyExaminations, indexOfLastMyExaminations);
    const totalMyExaminationsPages = Math.ceil(filteredMyExaminations.length / itemsPerPage);


    const Pagination = ({ itemsPerPage, totalItems, currentPage, paginate }) => {
        const pageNumbers = [];
        for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
            pageNumbers.push(i);
        }

        return (
            <nav>
                <ul className="pagination justify-content-center mt-4">
                    {pageNumbers.map(number => (
                        <li key={number} className={`page-item ${currentPage === number ? 'active' : ''}`}>
                            <button onClick={() => paginate(number)} className="page-link">
                                {number}
                            </button>
                        </li>
                    ))}
                </ul>
            </nav>
        );
    };


    const renderContent = () => {
        switch (activeTab) {
            case 'overview':
                return (
                    <div>
                        <h4 className="mb-4">Doctor Dashboard</h4>
                        <div className="row g-4">
                            <div className="col-md-6">
                                <div className="card text-white bg-info mb-3">
                                    <div className="card-header">Total patients under care</div>
                                    <div className="card-body">
                                        <h5 className="card-title display-4">{myPatients.length}</h5>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-6">
                                <div className="card text-white bg-warning mb-3">
                                    <div className="card-header">Pending examinations</div>
                                    <div className="card-body">
                                        <h5 className="card-title display-4">{doctorsExaminations.filter(exam => exam.status === 'zakazan').length}</h5>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="mt-5">
                            <h5>Quick Actions</h5>
                            <div className="d-flex gap-2">
                                <button className="btn btn-outline-info" onClick={() => setActiveTab('my-examinations')}>View My Examinations</button>
                                <button className="btn btn-outline-success" onClick={() => setActiveTab('my-patients')}>View My Patients</button>
                                <button className="btn btn-outline-primary" onClick={() => setActiveTab('documents')}>View Documents</button>
                                <button className="btn btn-outline-secondary" onClick={() => setActiveTab('messages')}>View Messages</button>
                            </div>
                        </div>
                        <div className="mt-5">
                            <h5>Recent Activities</h5>
                            <ul className="list-group">
                                {activityLog.length > 0 ? (
                                    activityLog
                                        .filter(log => log.doktorId === currentDoctorId)
                                        .slice(-5).reverse().map(log => (
                                            <li key={log.id || Math.random()} className="list-group-item d-flex justify-content-between align-items-center">
                                                {log.action}
                                                <span className="badge bg-light text-dark">{new Date(log.timestamp).toLocaleString()}</span>
                                            </li>
                                        ))
                                ) : (
                                    <li className="list-group-item text-center text-muted">No recent activities.</li>
                                )}
                            </ul>
                        </div>
                    </div>
                );
            case 'my-examinations':
                return (
                    <div>
                        <h4 className="mb-4">Manage My Examinations</h4>
                        <div className="d-flex justify-content-between mb-3">
                            <input
                                type="text"
                                className="form-control w-25 me-2"
                                placeholder="Search examinations (patient name, status, date)..."
                                value={examinationSearchTerm}
                                onChange={(e) => setExaminationSearchTerm(e.target.value)}
                            />
                            <Form.Select
                                className="w-auto"
                                value={examinationFilterStatus}
                                onChange={(e) => setExaminationFilterStatus(e.target.value)}
                            >
                                <option value="All">Filter by status (All)</option>
                                <option value="zakazan">Scheduled</option>
                                <option value="obavljen">Completed</option>
                                <option value="otkazan">Cancelled</option>
                            </Form.Select>
                        </div>
                        <div className="table-responsive">
                            <table className="table table-striped table-hover">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Patient</th>
                                        <th>Date</th>
                                        <th>Time</th>
                                        <th>Status</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {currentMyExaminations.length > 0 ? (
                                        currentMyExaminations.map(examination => {
                                            const patient = patients.find(p => p.id === examination.pacijentId);
                                            return (
                                                <tr key={examination.id}>
                                                    <td>{examination.id}</td>
                                                    <td>{patient ? patient.name : 'N/A'}</td>
                                                    <td>{new Date(examination.date).toLocaleDateString()}</td>
                                                    <td>{examination.time}</td>
                                                    <td>
                                                        <span className={`badge ${
                                                            examination.status === 'zakazan' ? 'bg-warning text-dark' :
                                                            examination.status === 'obavljen' ? 'bg-success' :
                                                            examination.status === 'otkazan' ? 'bg-danger' :
                                                            'bg-secondary'
                                                        }`}>
                                                            {examination.status}
                                                        </span>
                                                    </td>
                                                    <td>
                                                        {examination.status === 'zakazan' && (
                                                            <>
                                                                <button
                                                                    className="btn btn-success btn-sm me-2"
                                                                    onClick={() => handleUpdateExaminationStatus(examination.id, 'obavljen')}
                                                                >
                                                                    Mark as Completed
                                                                </button>
                                                                <button
                                                                    className="btn btn-danger btn-sm"
                                                                    onClick={() => handleUpdateExaminationStatus(examination.id, 'otkazan')}
                                                                >
                                                                    Cancel
                                                                </button>
                                                            </>
                                                        )}
                                                        {examination.status === 'obavljen' && (
                                                            <span className="text-muted">Completed</span>
                                                        )}
                                                        {examination.status === 'otkazan' && (
                                                            <span className="text-muted">Cancelled</span>
                                                        )}
                                                    </td>
                                                </tr>
                                            );
                                        })
                                    ) : (
                                        <tr>
                                            <td colSpan="6" className="text-center">No examinations found.</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <Pagination
                            itemsPerPage={itemsPerPage}
                            totalItems={filteredMyExaminations.length}
                            currentPage={myExaminationsCurrentPage}
                            paginate={setMyExaminationsCurrentPage}
                        />
                    </div>
                );
            case 'my-patients':
                return (
                    <div>
                        <h4 className="mb-4">My Patients</h4>
                        <div className="d-flex justify-content-between mb-3">
                            <input
                                type="text"
                                className="form-control w-25"
                                placeholder="Search patients..."
                                value={patientSearchTerm}
                                onChange={(e) => setPatientSearchTerm(e.target.value)}
                            />
                        </div>
                        <div className="table-responsive">
                            <table className="table table-striped table-hover">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Phone</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {currentMyPatients.length > 0 ? (
                                        currentMyPatients.map(patient => (
                                            <tr key={patient.id}>
                                                <td>{patient.id}</td>
                                                <td>{patient.name}</td>
                                                <td>{patient.email}</td>
                                                <td>{patient.phone}</td>
                                                <td>
                                                    <button
                                                        className="btn btn-primary btn-sm me-2"
                                                        onClick={() => openPatientDetailModal(patient)}
                                                    >
                                                        Examination Details
                                                    </button>
                                                </td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr>
                                            <td colSpan="5" className="text-center">No patients found.</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <Pagination
                            itemsPerPage={itemsPerPage}
                            totalItems={filteredMyPatients.length}
                            currentPage={myPatientsCurrentPage}
                            paginate={setMyPatientsCurrentPage}
                        />
                    </div>
                );
            case 'documents':
                return (
                    <div>
                        <h4 className="mb-4">My Documents</h4>
                        <Form.Group controlId="formFile" className="mb-3">
                            <Form.Label>Upload new document</Form.Label>
                            <Form.Control type="file" onChange={handleDocumentUpload} />
                        </Form.Group>

                        {documents.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Document Name</th>
                                        <th>Type</th>
                                        <th>Upload Date</th>
                                        <th>Access</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {documents.map(doc => (
                                        <tr key={doc.id}>
                                            <td>{doc.fileName}</td>
                                            <td>{doc.documentType}</td>
                                            <td>{new Date(doc.uploadDate).toLocaleDateString()}</td>
                                            <td><span className={`badge bg-${doc.pristup === 'JAVNA' ? 'success' : 'secondary'}`}>{doc.pristup}</span></td>
                                            <td>
                                                <Button variant="success" size="sm" href={`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/download/${doc.id}`} target="_blank">Download</Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>No documents found.</p>
                        )}
                    </div>
                );
            case 'messages':
                return (
                    <div>
                        <h4 className="mb-4">My Messages</h4>
                        <Button variant="primary" className="mb-3" onClick={() => setNewMessage({ recipientId: '', subject: '', content: '' })}>Compose new message</Button>

                        {messages.length > 0 ? (
                            <ul className="list-group">
                                {messages.map(msg => (
                                    <li key={msg.id} className="list-group-item d-flex justify-content-between align-items-center">
                                        <div>
                                            <strong>From: {msg.senderType === 'PACIJENT' && patients.find(p => p.id === msg.senderId) ? patients.find(p => p.id === msg.senderId).name : 'Me'}</strong> <br />
                                            <strong>Subject:</strong> {msg.subject} <br />
                                            <small>{new Date(msg.timestamp).toLocaleString()}</small>
                                        </div>
                                        <Button variant="info" size="sm" onClick={() => handleViewMessage(msg)}>View Message</Button>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>No messages found.</p>
                        )}

                        <hr className="my-4" />

                        <h4>Compose new message</h4>
                        <Form>
                            <Form.Group className="mb-3">
                                <Form.Label>Recipient (Patient)</Form.Label>
                                <Form.Control
                                    as="select"
                                    name="recipientId"
                                    value={newMessage.recipientId}
                                    onChange={handleNewMessageChange}
                                    required
                                >
                                    <option value="">-- Select patient --</option>
                                    {myPatients.map(patient => (
                                        <option key={patient.id} value={patient.id}>{patient.name}</option>
                                    ))}
                                </Form.Control>
                            </Form.Group>
                            <Form.Group className="mb-3">
                                <Form.Label>Subject</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="subject"
                                    value={newMessage.subject}
                                    onChange={handleNewMessageChange}
                                    required
                                />
                            </Form.Group>
                            <Form.Group className="mb-3">
                                <Form.Label>Content</Form.Label>
                                <Form.Control
                                    as="textarea"
                                    rows={3}
                                    name="content"
                                    value={newMessage.content}
                                    onChange={handleNewMessageChange}
                                    required
                                />
                            </Form.Group>
                            <Button variant="primary" onClick={handleSendMessage}>Send Message</Button>
                        </Form>
                    </div>
                );
            case 'settings':
                return (
                    <div className="container my-4">
                        <h4 className="mb-4">Doctor Profile Settings</h4>
                        {!currentDoctorProfile ? (
                            <p>Loading doctor profile...</p>
                        ) : (
                            isEditingProfile ? (
                                <Form onSubmit={handleUpdateDoctorProfile}>
                                    <div className="row mb-3">
                                        <Form.Group className="col-md-6 mb-3">
                                            <Form.Label>First Name</Form.Label>
                                            <Form.Control type="text" value={editIme} onChange={(e) => setEditIme(e.target.value)} required />
                                        </Form.Group>
                                        <Form.Group className="col-md-6 mb-3">
                                            <Form.Label>Last Name</Form.Label>
                                            <Form.Control type="text" value={editPrezime} onChange={(e) => setEditPrezime(e.target.value)} required />
                                        </Form.Group>
                                    </div>
                                    <div className="row mb-3">
                                        <Form.Group className="col-md-6 mb-3">
                                            <Form.Label>Email</Form.Label>
                                            <Form.Control type="email" value={editEmail} onChange={(e) => setEditEmail(e.target.value)} required />
                                        </Form.Group>
                                        <Form.Group className="col-md-6 mb-3">
                                            <Form.Label>Phone</Form.Label>
                                            <Form.Control type="text" value={editTelefon} onChange={(e) => setEditTelefon(e.target.value)} required />
                                        </Form.Group>
                                    </div>
                                    <Form.Group className="mb-3">
                                        <Form.Label>Specialties (comma separated)</Form.Label>
                                        <Form.Control type="text" value={editSpecijalizacije} onChange={(e) => setEditSpecijalizacije(e.target.value)} />
                                    </Form.Group>
                                    <div className="row mb-3">
                                        <Form.Group className="col-md-6 mb-3">
                                            <Form.Label>City</Form.Label>
                                            <Form.Control type="text" value={editGrad} onChange={(e) => setEditGrad(e.target.value)} />
                                        </Form.Group>
                                        <Form.Group className="col-md-6 mb-3">
                                            <Form.Label>Working Hours</Form.Label>
                                            <Form.Control type="text" value={editRadnoVrijeme} onChange={(e) => setEditRadnoVrijeme(e.target.value)} />
                                        </Form.Group>
                                    </div>
                                    <Form.Group className="mb-3">
                                        <Form.Label>Experience (years)</Form.Label>
                                        <Form.Control type="number" value={editIskustvo} onChange={(e) => setEditIskustvo(e.target.value)} />
                                    </Form.Group>
                                    <Form.Group className="mb-3">
                                        <Form.Label>Profile Image</Form.Label>
                                        <Form.Control
                                            type="file"
                                            accept="image/*"
                                            onChange={handleImageChange}
                                        />
                                        {profileImagePreview && (
                                            <div className="mt-2">
                                                <img
                                                    src={profileImagePreview}
                                                    alt="Profile Image Preview"
                                                    style={{ maxWidth: '100px', maxHeight: '100px', borderRadius: '50%', objectFit: 'cover' }}
                                                    className="border border-secondary"
                                                />
                                            </div>
                                        )}
                                    </Form.Group>
                                    <div className="d-flex justify-content-end">
                                        <Button variant="secondary" className="me-2" onClick={() => setIsEditingProfile(false)}>Cancel</Button>
                                        <Button variant="primary" type="submit">Save Changes</Button>
                                    </div>
                                </Form>
                            ) : (
                                <div>
                                    <p>
                                        {currentDoctorProfile.profileImageBase64 ? (
                                            <img
                                                src={currentDoctorProfile.profileImageBase64}
                                                alt="Doctor Profile"
                                                style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover' }}
                                                className="mb-3 border border-secondary"
                                            />
                                        ) : (
                                            <img
                                                src="https://placehold.co/100x100/CCCCCC/000000?text=No+image"
                                                alt="No profile image"
                                                style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover' }}
                                                className="mb-3 border border-secondary"
                                            />
                                        )}
                                    </p>
                                    <p><strong>First Name:</strong> {currentDoctorProfile.ime}</p>
                                    <p><strong>Last Name:</strong> {currentDoctorProfile.prezime}</p>
                                    <p><strong>Email:</strong> {currentDoctorProfile.email}</p>
                                    <p><strong>Phone:</strong> {currentDoctorProfile.telefon}</p>
                                    <p><strong>Specialties:</strong> {Array.isArray(currentDoctorProfile.specijalizacije) ? currentDoctorProfile.specijalizacije.join(', ') : 'N/A'}</p>
                                    <p><strong>City:</strong> {currentDoctorProfile.grad}</p>
                                    <p><strong>Working Hours:</strong> {currentDoctorProfile.radnoVrijeme}</p>
                                    <p><strong>Experience:</strong> {currentDoctorProfile.iskustvo} years</p>
                                    <p><strong>Rating:</strong> {currentDoctorProfile.ocjena}</p>
                                    <Button variant="primary" onClick={() => setIsEditingProfile(true)}>Edit Profile</Button>
                                </div>
                            )
                        )}
                    </div>
                );
            default:
                return <div>Select a tab from the sidebar.</div>;
        }
    };

    return (
        <div className="d-flex" style={{ minHeight: '100vh' }}>
            <div className="bg-dark text-white p-4" style={{ width: '250px' }}>
                <h3 className="mb-4">Doctor Panel</h3>
                <ul className="nav flex-column">
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'overview' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('overview')}
                        >
                            Overview
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'my-examinations' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('my-examinations')}
                        >
                            My Examinations
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'my-patients' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('my-patients')}
                        >
                            My Patients
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'documents' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('documents')}
                        >
                            Documents
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'messages' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('messages')}
                        >
                            Messages
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'settings' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('settings')}
                        >
                            Settings
                        </button>
                    </li>
                </ul>
            </div>

            <div className="flex-grow-1 p-4 bg-light">
                {showAlert && (
                    <div className={`alert alert-${alertType} alert-dismissible fade show position-fixed top-0 start-50 translate-middle-x mt-3`} role="alert" style={{ zIndex: 1050 }}>
                        {alertMessage}
                        <button type="button" className="btn-close" onClick={() => setShowAlert(false)}></button>
                    </div>
                )}

                <div className="d-flex justify-content-between align-items-center mb-4">
                    <h2 className="mb-0">Doctor Dashboard</h2>
                    {/* Removed Logout button here */}
                </div>

                {renderContent()}
            </div>

            <Modal show={showPatientDetailModal} onHide={closePatientDetailModal} size="lg">
                <Modal.Header closeButton>
                    <Modal.Title>Patient Details: {selectedPatient?.name}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {selectedPatient && (
                        <div>
                            <p><strong>Email:</strong> {selectedPatient.email}</p>
                            <p><strong>Phone:</strong> {selectedPatient.phone}</p>
                            <hr />
                            <h5>Examination History for {selectedPatient.name}</h5>
                            {doctorsExaminations.filter(exam => exam.pacijentId === selectedPatient.id).length > 0 ? (
                                <ul className="list-group mb-3">
                                    {doctorsExaminations
                                        .filter(exam => exam.pacijentId === selectedPatient.id)
                                        .sort((a, b) => new Date(b.date) - new Date(a.date))
                                        .map(exam => (
                                            <li key={exam.id} className="list-group-item">
                                                <strong>Date:</strong> {new Date(exam.date).toLocaleDateString()}, {' '}
                                                <strong>Time:</strong> {exam.time}, {' '}
                                                <strong>Status:</strong> <span className={`badge ${
                                                    exam.status === 'zakazan' ? 'bg-warning text-dark' :
                                                    exam.status === 'obavljen' ? 'bg-success' :
                                                    exam.status === 'otkazan' ? 'bg-danger' :
                                                    'bg-secondary'
                                                }`}>{exam.status}</span>
                                            </li>
                                        ))}
                                </ul>
                            ) : (
                                <p>No examination history found for this patient.</p>
                            )}
                            <hr />
                            <h5>Patient Notes</h5>
                            <Form.Group className="mb-3">
                                <Form.Control
                                    as="textarea"
                                    rows={5}
                                    value={patientNotes}
                                    onChange={(e) => setPatientNotes(e.target.value)}
                                    placeholder="Add or edit notes for this patient..."
                                />
                            </Form.Group>
                        </div>
                    )}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closePatientDetailModal}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleSavePatientNotes}>
                        Save Notes
                    </Button>
                </Modal.Footer>
            </Modal>

            <Modal show={!!selectedMessage} onHide={closeMessageModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Message: {selectedMessage?.subject}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {selectedMessage && (
                        <>
                            <p><strong>From:</strong> {selectedMessage.senderIme || (selectedMessage.senderType === 'PACIJENT' ? `Patient (${selectedMessage.senderId})` : 'Me')}</p>
                            <p><strong>Date:</strong> {new Date(selectedMessage.timestamp).toLocaleString()}</p>
                            <hr />
                            <p>{selectedMessage.content}</p>

                            {selectedMessage.replies && selectedMessage.replies.length > 0 && (
                                <div className="mt-4">
                                    <h5>Replies:</h5>
                                    {selectedMessage.replies.map((reply, index) => (
                                        <div key={index} className="border p-2 mb-2 rounded bg-light">
                                            <strong>{reply.senderIme || (reply.senderType === 'PACIJENT' ? `Patient (${reply.senderId})` : 'Me')}:</strong> {reply.content}
                                            <br />
                                            <small>{new Date(reply.timestamp).toLocaleString()}</small>
                                        </div>
                                    ))}
                                </div>
                            )}

                            <Form.Group className="mt-4">
                                <Form.Label>Reply</Form.Label>
                                <Form.Control
                                    as="textarea"
                                    rows={2}
                                    value={replyMessage.content}
                                    onChange={handleReplyMessageChange}
                                    placeholder="Type your reply here..."
                                />
                            </Form.Group>
                            <Button variant="primary" className="mt-2" onClick={() => handleReply(selectedMessage.id)}>Send Reply</Button>
                        </>
                    )}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeMessageModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default DoctorDashboard;
