import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [token, setToken] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        console.log("AuthContext useEffect: Provjeravam localStorage za korisničke podatke...");
        const storedUser = localStorage.getItem('user');
        const storedToken = localStorage.getItem('token');

        if (storedUser && storedToken) {
            try {
                const parsedUser = JSON.parse(storedUser);
                setUser(parsedUser);
                setToken(storedToken);
                axios.defaults.headers.common['Authorization'] = `Bearer ${storedToken}`;
                console.log("AuthContext: Korisnički podaci i token pronađeni u localStorage-u.");
            } catch (e) {
                console.error("AuthContext: Greška pri parsiranju korisničkih podataka iz localStorage-a:", e);
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                setUser(null);
                setToken(null);
                delete axios.defaults.headers.common['Authorization'];
            }
        } else {
            console.log("AuthContext: Nema korisničkih podataka u localStorage-u.");
        }
        setLoading(false);
        console.log("AuthContext useEffect: Učitavanje završeno.");
    }, []);

    // AŽURIRANO: Login funkcija sada vraća cijeli AuthResponse objekat
    const login = async (email, password) => {
        try {
            setLoading(true);
            const response = await axios.post('http://localhost:8081/auth/login', { email, password });
            const { token: receivedToken, email: userEmail, roles, fullName, patientId, doktorId, grad, specijalizacije, ocjena, iskustvo, radnoVrijeme, userId } = response.data;

            const userData = {
                email: userEmail,
                fullName: fullName, // Dodano
                roles: roles || [],
                patientId: patientId || null,
                doktorId: doktorId || null,
                grad: grad || null, // Dodano
                specijalizacije: specijalizacije || [], // Dodano
                ocjena: ocjena || null, // Dodano
                iskustvo: iskustvo || null, // Dodano
                radnoVrijeme: radnoVrijeme || null, // Dodano
                userId: userId || null, // Dodano
            };

            setUser(userData);
            setToken(receivedToken);
            localStorage.setItem('user', JSON.stringify(userData));
            localStorage.setItem('token', receivedToken);
            axios.defaults.headers.common['Authorization'] = `Bearer ${receivedToken}`;
            setLoading(false);
            return response.data; // Vrati cijeli odgovor od backend-a
        } catch (error) {
            setLoading(false);
            console.error('Login error in AuthContext:', error.response?.data || error.message);
            throw error; // Proslijedi grešku dalje
        }
    };

    const logout = () => {
        setUser(null);
        setToken(null);
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        delete axios.defaults.headers.common['Authorization'];
    };

    return (
        <AuthContext.Provider value={{ user, token, loading, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
