import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import homeVilaImage from '../images/homevila.jpg';

function HomePage() {
  // State za pretragu i filtere na HomePage-u
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSpecialty, setSelectedSpecialty] = useState('');
  const [selectedCity, setSelectedCity] = useState(''); // Novi state za grad

  // Mock podaci za specijalnosti i gradove na HomePage-u
  // Ovi podaci su hardkodirani jer HomePage ne dohvaća doktore s backenda
  const mockSpecialties = ['Cardiologist', 'Pediatrician', 'Dermatologist', 'Neurologist', 'Orthopedic Surgeon', 'General Practitioner'];
  const mockCities = ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla'];

  // Filtriranje doktora na osnovu unesenih kriterijuma
  // Napomena: Ovo filtriranje trenutno nema efekta jer nema liste doktora na HomePage-u.
  // Služi samo za demonstraciju funkcionalnosti inputa i selecta.
  const filteredDoctors = []; // HomePage ne prikazuje listu doktora, pa je ovo prazan niz

  return (
    <div className="container"> {/* Koristimo container za sadržaj stranice */}
      <section className="hero py-5">
        <div className="row align-items-center">
          <div className="col-md-6">
            <h2 className="display-4">Providing Excellent <strong>Healthcare</strong> For <br className="d-none d-lg-block" /> A <span className="text-success">Healthier</span> And <span className="text-danger">Brighter</span> Future.</h2>
            <p className="lead">Lorem Ipsum Dolor Sit Amet, Consectetur Adipiscing Elit. Nulla Facilisi. Suspendisse Potenti. Proin Euismod, Justo Ac Cursus Volutpat. Nunc Lorem Fermentum Neque, Eget Efficitur Libero Mauris Nec Neque. Integer Vehicula, Lectus Et Fermentum Aliquet. Ex Ligula Auctor Purus, Sit Amet Congue Justo Purus Nec Purus.</p>
          </div>
          <div className="col-md-6 position-relative">
            {/* Krugovi sa z-indexom 1 da budu iza slike */}
            <div className="bg-danger rounded-circle position-absolute" style={{ width: '150px', height: '150px', opacity: 0.6, top: '-20px', right: '80px', zIndex: 1}}></div>
            <div className="bg-success rounded-circle position-absolute" style={{ width: '120px', height: '120px', opacity: 0.6, bottom: '30px', left: '90px', zIndex: 1 }}></div>
            {/* Omotač za sliku sa z-indexom 2 da bude ispred krugova i poravnanje udesno */}
            <div className="d-flex justify-content-end" style={{ position: 'relative', zIndex: 2, left:'-70px'}}>
              <img src={homeVilaImage} alt="Healthcare Illustration" className="rounded" style={{ maxWidth: '500px', height: 'auto'}} />
            </div>
          </div>
        </div>
      </section>



      {/* Dodatni prostor ispod search sekcije */}
    </div>
  );
}

export default HomePage;
