import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Form, Alert } from 'react-bootstrap';

const GATEWAY_BASE_URL = 'http://localhost:8081';

const KORISNICI_DOKTORI_API_PREFIX = `${GATEWAY_BASE_URL}/api/korisnici-doktori`;
const TERMINI_PREGLEDI_API_PREFIX = `${GATEWAY_BASE_URL}/api`;
const OBAVIJESTI_DOKUMENTACIJA_API_PREFIX = `${GATEWAY_BASE_URL}/api`;
const AUTH_API_PREFIX = `${GATEWAY_BASE_URL}/auth`;

function AdminDashboard() {
  const { token } = useAuth();

  const authHeaders = {
    headers: {
      'Authorization': `Bearer ${token}`
    }
  };

  const [activeTab, setActiveTab] = useState('overview');
  const [doctors, setDoctors] = useState([]);
  const [patients, setPatients] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [users, setUsers] = useState([]);
  const [blogPosts, setBlogPosts] = useState([]);
  const [activityLog, setActivityLog] = useState([]);

  const [showDoctorModal, setShowDoctorModal] = useState(false);
  const [editingDoctor, setEditingDoctor] = useState(null);

  const [showPatientModal, setShowPatientModal] = useState(false);
  const [editingPatient, setEditingPatient] = useState(null);

  const [showUserModal, setShowUserModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [showBlogPostModal, setShowBlogPostModal] = useState(false);
  const [editingBlogPost, setEditingBlogPost] = useState(null);

  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('success');

  const [doctorSearchTerm, setDoctorSearchTerm] = useState('');
  const [patientSearchTerm, setPatientSearchTerm] = useState('');

  const [doctorsCurrentPage, setDoctorsCurrentPage] = useState(1);
  const [patientsCurrentPage, setPatientsCurrentPage] = useState(1);
  const [appointmentsCurrentPage, setAppointmentsCurrentPage] = useState(1);
  const [usersCurrentPage, setUsersCurrentPage] = useState(1);
  const [blogPostsCurrentPage, setBlogPostsCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const showNotification = (message, type = 'success') => {
    setAlertMessage(message);
    setAlertType(type);
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
      setAlertMessage('');
    }, 3000);
  };

  const fetchDoctors = async () => {
    try {
      //frontend šalje: http://localhost:8081/api/korisnici-doktori/api/doktori
      const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori`, authHeaders);
      const transformedDoctors = response.data.map(doc => ({
        id: doc.doktorID,
        name: `${doc.ime} ${doc.prezime}`,
        specialty: doc.specijalizacija || 'N/A',
        city: doc.grad || 'N/A',
        email: doc.email || 'N/A',
      }));
      setDoctors(transformedDoctors);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja doktora:", error);
      showNotification("Greška prilikom dohvaćanja doktora sa servera.", "danger");
    }
  };

  const fetchPatients = async () => {
    try {
      //frontend šalje: http://localhost:8081/api/korisnici-doktori/api/pacijenti
      const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti`, authHeaders);
      const transformedPatients = response.data.map(pat => ({
        id: pat.pacijentID,
        name: `${pat.ime} ${pat.prezime}`,
        email: pat.email,
        phone: pat.telefon || 'N/A',
      }));
      setPatients(transformedPatients);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja pacijenata:", error);
      showNotification("Greška prilikom dohvaćanja pacijenata sa servera.", "danger");
    }
  };

  const fetchAppointments = async () => {
    try {
      const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/appointments`, authHeaders);
      const transformedAppointments = response.data.map(app => ({
        id: app.terminID,
        patientName: app.pacijentIme || 'N/A',
        doctorName: app.doktorIme || 'N/A',
        date: app.datum,
        time: app.vrijeme,
        status: app.status,
      }));
      setAppointments(transformedAppointments);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja termina:", error);
      showNotification("Greška prilikom dohvaćanja termina sa servera.", "danger");
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get(`${AUTH_API_PREFIX}/users`, authHeaders);
      const transformedUsers = response.data.map(user => ({
        id: user.id,
        name: user.fullName,
        email: user.email,
        roles: user.roles.join(', '),
      }));
      setUsers(transformedUsers);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja korisnika:", error);
      showNotification("Greška prilikom dohvaćanja korisnika sa servera.", "danger");
    }
  };

  const fetchBlogPosts = async () => {
    try {
      const response = await axios.get(`${OBAVIJESTI_DOKUMENTACIJA_API_PREFIX}/blog-posts`, authHeaders);
      const transformedPosts = response.data.map(post => ({
        id: post.postID,
        title: post.naslov,
        author: post.autor,
        date: post.datumKreiranja,
      }));
      setBlogPosts(transformedPosts);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja blog postova:", error);
      showNotification("Greška prilikom dohvaćanja blog postova sa servera.", "danger");
    }
  };

  const fetchActivityLog = async () => {
    try {
      const response = await axios.get(`${OBAVIJESTI_DOKUMENTACIJA_API_PREFIX}/activity-log`, authHeaders);
      const transformedLog = response.data.map(log => ({
        id: log.logID,
        timestamp: log.timestamp,
        action: log.akcija,
      }));
      setActivityLog(transformedLog);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja loga aktivnosti:", error);
      showNotification("Greška prilikom dohvaćanja loga aktivnosti.", "danger");
    }
  };

  useEffect(() => {
    if (!token) {
      console.warn("Nema tokena, preskačem dohvaćanje podataka.");
      return;
    }

    fetchDoctors();
    fetchPatients();
    fetchAppointments();
    fetchUsers();
    fetchBlogPosts();
    fetchActivityLog();

  }, [token]);

  // --- Doctor Management Functions ---
  const handleAddDoctor = async (newDoctor) => {
    try {
      const response = await axios.post(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori`, newDoctor, authHeaders);
      setDoctors([...doctors, response.data]);
      fetchActivityLog();
      setShowDoctorModal(false);
      showNotification('Doktor uspješno dodan!');
    } catch (error) {
      console.error("Greška prilikom dodavanja doktora:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja doktora.', 'danger');
    }
  };

  const handleUpdateDoctor = async (updatedDoctor) => {
    try {
      const response = await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori/${updatedDoctor.id}`, updatedDoctor, authHeaders);
      setDoctors(doctors.map(doc => (doc.id === updatedDoctor.id ? response.data : doc)));
      fetchActivityLog();
      setShowDoctorModal(false);
      setEditingDoctor(null);
      showNotification('Doktor uspješno ažuriran!');
    } catch (error) {
      console.error("Greška prilikom ažuriranja doktora:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja doktora.', 'danger');
    }
  };

  const handleDeleteDoctor = async (id) => {
    const confirmed = window.confirm('Jeste li sigurni da želite obrisati ovog doktora?');
    if (!confirmed) {
      return;
    }
    try {
      await axios.delete(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori/${id}`, authHeaders);
      setDoctors(doctors.filter(doc => doc.id !== id));
      fetchActivityLog();
      showNotification('Doktor uspješno obrisan!', 'danger');
    } catch (error) {
      console.error("Greška prilikom brisanja doktora:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom brisanja doktora.', 'danger');
    }
  };

  const openDoctorModal = (doctor = null) => {
    setEditingDoctor(doctor);
    setShowDoctorModal(true);
  };

  const filteredDoctors = doctors.filter(doctor =>
    doctor.name.toLowerCase().includes(doctorSearchTerm.toLowerCase()) ||
    doctor.specialty.toLowerCase().includes(doctorSearchTerm.toLowerCase()) ||
    doctor.city.toLowerCase().includes(doctorSearchTerm.toLowerCase())
  );
  const indexOfLastDoctor = doctorsCurrentPage * itemsPerPage;
  const indexOfFirstDoctor = indexOfLastDoctor - itemsPerPage;
  const currentDoctors = filteredDoctors.slice(indexOfFirstDoctor, indexOfLastDoctor);
  const totalDoctorPages = Math.ceil(filteredDoctors.length / itemsPerPage);

  // --- Patient Management Functions ---
  const handleAddPatient = async (newPatient) => {
    try {
      const response = await axios.post(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti`, newPatient, authHeaders);
      setPatients([...patients, response.data]);
      fetchActivityLog();
      setShowPatientModal(false);
      showNotification('Pacijent uspješno dodan!');
    } catch (error) {
      console.error("Greška prilikom dodavanja pacijenta:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja pacijenta.', 'danger');
    }
  };

  const handleUpdatePatient = async (updatedPatient) => {
    try {
      const response = await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${updatedPatient.id}`, updatedPatient, authHeaders);
      setPatients(patients.map(pat => (pat.id === updatedPatient.id ? response.data : pat)));
      fetchActivityLog();
      setShowPatientModal(false);
      setEditingPatient(null);
      showNotification('Pacijent uspješno ažuriran!');
    } catch (error) {
      console.error("Greška prilikom ažuriranja pacijenta:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja pacijenta.', 'danger');
    }
  };

  const handleDeletePatient = async (id) => {
    const confirmed = window.confirm('Jeste li sigurni da želite obrisati ovog pacijenta?');
    if (!confirmed) {
      return;
    }
    try {
      await axios.delete(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${id}`, authHeaders);
      setPatients(patients.filter(pat => pat.id !== id));
      fetchActivityLog();
      showNotification('Pacijent uspješno obrisan!', 'danger');
    } catch (error) {
      console.error("Greška prilikom brisanja pacijenta:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom brisanja pacijenta.', 'danger');
    }
  };

  const openPatientModal = (patient = null) => {
    setEditingPatient(patient);
    setShowPatientModal(true);
  };

  const filteredPatients = patients.filter(patient =>
    patient.name.toLowerCase().includes(patientSearchTerm.toLowerCase()) ||
    patient.email.toLowerCase().includes(patientSearchTerm.toLowerCase()) ||
    (patient.phone && patient.phone.toLowerCase().includes(patientSearchTerm.toLowerCase()))
  );
  const indexOfLastPatient = patientsCurrentPage * itemsPerPage;
  const indexOfFirstPatient = indexOfLastPatient - itemsPerPage;
  const currentPatients = filteredPatients.slice(indexOfFirstPatient, indexOfLastPatient);
  const totalPatientPages = Math.ceil(filteredPatients.length / itemsPerPage);

  // --- Appointment Management Functions ---
  const handleUpdateAppointmentStatus = async (id, newStatus) => {
    try {
      const response = await axios.put(`${TERMINI_PREGLEDI_API_PREFIX}/appointments/${id}/status`, { status: newStatus }, authHeaders);
      setAppointments(appointments.map(app => (app.id === id ? response.data : app)));
      fetchActivityLog();
      showNotification(`Status termina ažuriran na ${newStatus}!`, 'info');
    } catch (error) {
      console.error("Greška prilikom ažuriranja statusa termina:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja statusa termina.', 'danger');
    }
  };

  const indexOfLastAppointment = appointmentsCurrentPage * itemsPerPage;
  const indexOfFirstAppointment = indexOfLastAppointment - itemsPerPage;
  const currentAppointments = appointments.slice(indexOfFirstAppointment, indexOfLastAppointment);
  const totalAppointmentPages = Math.ceil(appointments.length / itemsPerPage);

  // --- User Management Functions ---
  const handleAddUser = async (newUser) => {
    try {
      const response = await axios.post(`${AUTH_API_PREFIX}/users`, newUser, authHeaders);
      setUsers([...users, response.data]);
      fetchActivityLog();
      setShowUserModal(false);
      showNotification('Korisnik uspješno dodan!');
    } catch (error) {
      console.error("Greška prilikom dodavanja korisnika:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja korisnika.', 'danger');
    }
  };

  const handleUpdateUser = async (updatedUser) => {
    try {
      const response = await axios.put(`${AUTH_API_PREFIX}/users/${updatedUser.id}`, updatedUser, authHeaders);
      setUsers(users.map(user => (user.id === updatedUser.id ? response.data : user)));
      fetchActivityLog();
      setShowUserModal(false);
      setEditingUser(null);
      showNotification('Korisnik uspješno ažuriran!');
    } catch (error) {
      console.error("Greška prilikom ažuriranja korisnika:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja korisnika.', 'danger');
    }
  };

  const handleDeleteUser = async (id) => {
    const confirmed = window.confirm('Jeste li sigurni da želite obrisati ovog korisnika?');
    if (!confirmed) {
      return;
    }
    try {
      await axios.delete(`${AUTH_API_PREFIX}/users/${id}`, authHeaders);
      setUsers(users.filter(user => user.id !== id));
      fetchActivityLog();
      showNotification('Korisnik uspješno obrisan!', 'danger');
    } catch (error) {
      console.error("Greška prilikom brisanja korisnika:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom brisanja korisnika.', 'danger');
    }
  };

  const openUserModal = (user = null) => {
    setEditingUser(user);
    setShowUserModal(true);
  };

  const indexOfLastUser = usersCurrentPage * itemsPerPage;
  const indexOfFirstUser = indexOfLastUser - itemsPerPage;
  const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);
  const totalUserPages = Math.ceil(users.length / itemsPerPage);

  // --- Blog Post Management Functions ---
  const handleAddBlogPost = async (newPost) => {
    try {
      const response = await axios.post(`${OBAVIJESTI_DOKUMENTACIJA_API_PREFIX}/blog-posts`, newPost, authHeaders);
      setBlogPosts([...blogPosts, response.data]);
      fetchActivityLog();
      setShowBlogPostModal(false);
      showNotification('Blog post uspješno dodan!');
    } catch (error) {
      console.error("Greška prilikom dodavanja blog posta:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja blog posta.', 'danger');
    }
  };

  const handleUpdateBlogPost = async (updatedPost) => {
    try {
      const response = await axios.put(`${OBAVIJESTI_DOKUMENTACIJA_API_PREFIX}/blog-posts/${updatedPost.id}`, updatedPost, authHeaders);
      setBlogPosts(blogPosts.map(post => (post.id === updatedPost.id ? response.data : post)));
      fetchActivityLog();
      setShowBlogPostModal(false);
      setEditingBlogPost(null);
      showNotification('Blog post uspješno ažuriran!');
    } catch (error) {
      console.error("Greška prilikom ažuriranja blog posta:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja blog posta.', 'danger');
    }
  };

  const handleDeleteBlogPost = async (id) => {
    const confirmed = window.confirm('Jeste li sigurni da želite obrisati ovaj blog post?');
    if (!confirmed) {
      return;
    }
    try {
      await axios.delete(`${OBAVIJESTI_DOKUMENTACIJA_API_PREFIX}/blog-posts/${id}`, authHeaders);
      setBlogPosts(blogPosts.filter(post => post.id !== id));
      fetchActivityLog();
      showNotification('Blog post uspješno obrisan!', 'danger');
    } catch (error) {
      console.error("Greška prilikom brisanja blog posta:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom brisanja blog posta.', 'danger');
    }
  };

  const openBlogPostModal = (post = null) => {
    setEditingBlogPost(post);
    setShowBlogPostModal(true);
  };

  const indexOfLastBlogPost = blogPostsCurrentPage * itemsPerPage;
  const indexOfFirstBlogPost = indexOfLastBlogPost - itemsPerPage;
  const currentBlogPosts = blogPosts.slice(indexOfFirstBlogPost, indexOfLastBlogPost);
  const totalBlogPostPages = Math.ceil(blogPosts.length / itemsPerPage);

  const DataModal = ({ show, onClose, data, onSave, fields, title, idPrefix }) => {
    const [formData, setFormData] = useState(data || {});
    useEffect(() => {
      setFormData(data || {});
    }, [data]);

    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      onSave(formData);
    };

    return (
      <Modal show={show} onHide={onClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {fields.map(field => (
              <Form.Group className="mb-3" controlId={`${idPrefix}-${field.name}`} key={field.name}>
                <Form.Label>{field.label}</Form.Label>
                {field.type === 'select' ? (
                  <Form.Select
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  >
                    <option value="">Select {field.label}</option>
                    {field.options.map(option => (
                      <option key={option} value={option}>{option}</option>
                    ))}
                  </Form.Select>
                ) : field.type === 'textarea' ? (
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  />
                ) : (
                  <Form.Control
                    type={field.type || 'text'}
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  />
                )}
              </Form.Group>
            ))}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={onClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Save changes
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    );
  };

  const Pagination = ({ itemsPerPage, totalItems, currentPage, paginate }) => {
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
      pageNumbers.push(i);
    }

    return (
      <nav>
        <ul className="pagination justify-content-center mt-4">
          {pageNumbers.map(number => (
            <li key={number} className={`page-item ${currentPage === number ? 'active' : ''}`}>
              <button onClick={() => paginate(number)} className="page-link">
                {number}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    );
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div>
            <h4 className="mb-4">Dashboard Overview</h4>
            <div className="row g-4">
              <div className="col-md-4">
                <div className="card text-white bg-primary mb-3">
                  <div className="card-header">Total Doctors</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{doctors.length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card text-white bg-success mb-3">
                  <div className="card-header">Total Patients</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{patients.length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card text-white bg-warning mb-3">
                  <div className="card-header">Pending Appointments</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{appointments.filter(app => app.status === 'Pending').length}</h5>
                  </div>
                </div>
              </div>
            </div>
            <div className="mt-5">
              <h5>Quick Actions</h5>
              <div className="d-flex gap-2">
                <button className="btn btn-outline-primary" onClick={() => openDoctorModal()}>Add New Doctor</button>
                <button className="btn btn-outline-success" onClick={() => openPatientModal()}>Add New Patient</button>
                <button className="btn btn-outline-info" onClick={() => setActiveTab('appointments')}>View Appointments</button>
              </div>
            </div>
            <div className="mt-5">
              <h5>Recent Activity</h5>
              <ul className="list-group">
                {activityLog.slice(-5).reverse().map(log => (
                  <li key={log.id} className="list-group-item d-flex justify-content-between align-items-center">
                    {log.action}
                    <span className="badge bg-light text-dark">{new Date(log.timestamp).toLocaleString()}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        );
      case 'doctors':
        return (
          <div>
            <h4 className="mb-4">Manage Doctors</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openDoctorModal()}>Add New Doctor</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Search doctors..."
                value={doctorSearchTerm}
                onChange={(e) => setDoctorSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Specialty</th>
                    <th>City</th>
                    <th>Email</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentDoctors.length > 0 ? (
                    currentDoctors.map(doctor => (
                      <tr key={doctor.id}>
                        <td>{doctor.id}</td>
                        <td>{doctor.name}</td>
                        <td>{doctor.specialty}</td>
                        <td>{doctor.city}</td>
                        <td>{doctor.email}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openDoctorModal(doctor)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteDoctor(doctor.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6" className="text-center">No doctors found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredDoctors.length}
              currentPage={doctorsCurrentPage}
              paginate={setDoctorsCurrentPage}
            />
            <DataModal
              show={showDoctorModal}
              onClose={() => setShowDoctorModal(false)}
              data={editingDoctor}
              onSave={editingDoctor ? handleUpdateDoctor : handleAddDoctor}
              title={editingDoctor ? 'Edit Doctor' : 'Add New Doctor'}
              idPrefix="doctor"
              fields={[
                { name: 'ime', label: 'First Name', type: 'text', required: true },
                { name: 'prezime', label: 'Last Name', type: 'text', required: true },
                { name: 'specijalizacija', label: 'Specialty', type: 'select', required: true, options: ['Cardiologist', 'Pediatrician', 'Dermatologist', 'Neurologist', 'Orthopedic Surgeon', 'General Practitioner'] },
                { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla'] },
                { name: 'email', label: 'Email', type: 'email', required: true },
              ]}
            />
          </div>
        );
      case 'patients':
        return (
          <div>
            <h4 className="mb-4">Manage Patients</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openPatientModal()}>Add New Patient</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Search patients..."
                value={patientSearchTerm}
                onChange={(e) => setPatientSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentPatients.length > 0 ? (
                    currentPatients.map(patient => (
                      <tr key={patient.id}>
                        <td>{patient.id}</td>
                        <td>{patient.name}</td>
                        <td>{patient.email}</td>
                        <td>{patient.phone}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openPatientModal(patient)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeletePatient(patient.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center">No patients found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredPatients.length}
              currentPage={patientsCurrentPage}
              paginate={setPatientsCurrentPage}
            />
            <DataModal
              show={showPatientModal}
              onClose={() => setShowPatientModal(false)}
              data={editingPatient}
              onSave={editingPatient ? handleUpdatePatient : handleAddPatient}
              title={editingPatient ? 'Edit Patient' : 'Add New Patient'}
              idPrefix="patient"
              fields={[
                { name: 'ime', label: 'First Name', type: 'text', required: true },
                { name: 'prezime', label: 'Last Name', type: 'text', required: true },
                { name: 'email', label: 'Email', type: 'email', required: true },
                { name: 'telefon', label: 'Phone', type: 'text', required: false },
              ]}
            />
          </div>
        );
      case 'appointments':
        return (
          <div>
            <h4 className="mb-4">Manage Appointments</h4>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Patient</th>
                    <th>Doctor</th>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentAppointments.length > 0 ? (
                    currentAppointments.map(appointment => (
                      <tr key={appointment.id}>
                        <td>{appointment.id}</td>
                        <td>{appointment.patientName}</td>
                        <td>{appointment.doctorName}</td>
                        <td>{appointment.date}</td>
                        <td>{appointment.time}</td>
                        <td>
                          <select
                            className="form-select"
                            value={appointment.status}
                            onChange={(e) => handleUpdateAppointmentStatus(appointment.id, e.target.value)}
                          >
                            <option value="Pending">Pending</option>
                            <option value="Confirmed">Confirmed</option>
                            <option value="Completed">Completed</option>
                            <option value="Cancelled">Cancelled</option>
                          </select>
                        </td>
                        <td>
                          <button className="btn btn-danger btn-sm" onClick={() => showNotification('Brisanje termina nije implementirano.', 'warning')}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="7" className="text-center">No appointments found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={appointments.length}
              currentPage={appointmentsCurrentPage}
              paginate={setAppointmentsCurrentPage}
            />
          </div>
        );
      case 'users':
        return (
          <div>
            <h4 className="mb-4">Manage Users</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openUserModal()}>Add New User</button>
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Email</th>
                    <th>Full Name</th>
                    <th>Roles</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentUsers.length > 0 ? (
                    currentUsers.map(user => (
                      <tr key={user.id}>
                        <td>{user.id}</td>
                        <td>{user.email}</td>
                        <td>{user.name}</td>
                        <td>{user.roles}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openUserModal(user)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteUser(user.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center">No users found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={users.length}
              currentPage={usersCurrentPage}
              paginate={setUsersCurrentPage}
            />
            <DataModal
              show={showUserModal}
              onClose={() => setShowUserModal(false)}
              data={editingUser}
              onSave={editingUser ? handleUpdateUser : handleAddUser}
              title={editingUser ? 'Edit User' : 'Add New User'}
              idPrefix="user"
              fields={[
                { name: 'email', label: 'Email', type: 'email', required: true },
                { name: 'fullName', label: 'Full Name', type: 'text', required: true },
                { name: 'password', label: 'Password', type: 'password', required: !editingUser },
                { name: 'roles', label: 'Roles (comma-separated)', type: 'text', required: true },
              ]}
            />
          </div>
        );
      case 'blog-posts':
        return (
          <div>
            <h4 className="mb-4">Manage Blog Posts</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openBlogPostModal()}>Add New Blog Post</button>
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>Author</th>
                    <th>Date</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentBlogPosts.length > 0 ? (
                    currentBlogPosts.map(post => (
                      <tr key={post.id}>
                        <td>{post.id}</td>
                        <td>{post.title}</td>
                        <td>{post.author}</td>
                        <td>{post.date}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openBlogPostModal(post)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteBlogPost(post.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center">No blog posts found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={blogPosts.length}
              currentPage={blogPostsCurrentPage}
              paginate={setBlogPostsCurrentPage}
            />
            <DataModal
              show={showBlogPostModal}
              onClose={() => setShowBlogPostModal(false)}
              data={editingBlogPost}
              onSave={editingBlogPost ? handleUpdateBlogPost : handleAddBlogPost}
              title={editingBlogPost ? 'Edit Blog Post' : 'Add New Blog Post'}
              idPrefix="blogPost"
              fields={[
                { name: 'naslov', label: 'Title', type: 'text', required: true },
                { name: 'sadrzaj', label: 'Content', type: 'textarea', required: true },
                { name: 'autor', label: 'Author', type: 'text', required: true },
              ]}
            />
          </div>
        );
      default:
        return <p>Select a tab to view content.</p>;
    }
  };

  return (
    <div className="container-fluid">
      {showAlert && (
        <Alert variant={alertType} onClose={() => setShowAlert(false)} dismissible className="position-fixed top-0 start-50 translate-middle-x mt-3" style={{ zIndex: 1050 }}>
          {alertMessage}
        </Alert>
      )}

      <div className="row">
        <nav className="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse" style={{ minHeight: '100vh' }}>
          <div className="position-sticky pt-3">
            <ul className="nav flex-column">
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'overview' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('overview')}>
                  Overview
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'doctors' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('doctors')}>
                  Doctors
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'patients' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('patients')}>
                  Patients
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'appointments' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('appointments')}>
                  Appointments
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'users' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('users')}>
                  Users
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'blog-posts' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('blog-posts')}>
                  Blog Posts
                </a>
              </li>
            </ul>
          </div>
        </nav>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4 py-4">
          {renderContent()}
        </main>
      </div>
    </div>
  );
}

export default AdminDashboard;
