import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Form, Alert } from 'react-bootstrap';

const GATEWAY_BASE_URL = 'http://localhost:8081';

const KORISNICI_DOKTORI_API_PREFIX = `${GATEWAY_BASE_URL}/api/korisnici-doktori`;
const TERMINI_PREGLEDI_API_PREFIX = `${GATEWAY_BASE_URL}/termini-pregledi`;
const AUTH_API_PREFIX = `${GATEWAY_BASE_URL}/auth`;

function AdminDashboard() {
  const { token } = useAuth();

  const authHeaders = {
    headers: {
      'Authorization': `Bearer ${token}`
    }
  };

  const [activeTab, setActiveTab] = useState(() => {
    const savedTab = localStorage.getItem('adminActiveTab');
    return savedTab || 'overview';
  });

  const [doctors, setDoctors] = useState([]);
  const [patients, setPatients] = useState([]);
  const [appointments, setAppointments] = useState([]);

  const [showDoctorModal, setShowDoctorModal] = useState(false);
  const [editingDoctor, setEditingDoctor] = useState(null);
  const [showPatientModal, setShowPatientModal] = useState(false);
  const [editingPatient, setEditingPatient] = useState(null);

  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('success');

  const [doctorSearchTerm, setDoctorSearchTerm] = useState('');
  const [patientSearchTerm, setPatientSearchTerm] = useState('');

  const [doctorsCurrentPage, setDoctorsCurrentPage] = useState(1);
  const [patientsCurrentPage, setPatientsCurrentPage] = useState(1);
  const [appointmentsCurrentPage, setAppointmentsCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const showNotification = (message, type = 'success') => {
    setAlertMessage(message);
    setAlertType(type);
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
      setAlertMessage('');
    }, 3000);
  };

  const fetchDoctors = async () => {
    try {
      const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/doktori`, authHeaders);
      const transformedDoctors = response.data.map(doc => ({
        id: doc.doktorID,
        userId: doc.userId,
        name: `${doc.ime} ${doc.prezime}`,
        ime: doc.ime,
        prezime: doc.prezime,
        specialty: Array.isArray(doc.specijalizacije) && doc.specijalizacije.length > 0
                   ? doc.specijalizacije.join(', ')
                   : 'N/A',
        specijalizacije: doc.specijalizacije,
        city: doc.grad || 'N/A',
        grad: doc.grad,
        email: doc.email || 'N/A',
        telefon: doc.telefon || 'N/A',
        radnoVrijeme: doc.radnoVrijeme || 'N/A', // Ovdje se 'N/A' koristi samo za prikaz
        iskustvo: doc.iskustvo || 0, // Ovdje se '0' koristi samo za prikaz
        ocjena: doc.ocjena || 0.0,   // Ovdje se '0.0' koristi samo za prikaz
      }));
      setDoctors(transformedDoctors);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja doktora:", error);
      showNotification("Greška prilikom dohvaćanja doktora sa servera.", "danger");
    }
  };

  const fetchPatients = async () => {
    try {
      const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/pacijenti`, authHeaders);
      const transformedPatients = response.data.map(pat => ({
        id: pat.pacijentID,
        userId: pat.userId,
        name: `${pat.ime} ${pat.prezime}`,
        ime: pat.ime,
        prezime: pat.prezime,
        email: pat.email || 'N/A',
        phone: pat.telefon || 'N/A',
        telefon: pat.telefon,
      }));
      setPatients(transformedPatients);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja pacijenata:", error);
      showNotification("Greška prilikom dohvaćanja pacijenata sa servera.", "danger");
    }
  };

  const fetchAppointments = async () => {
    try {
      const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi`, authHeaders);
      const transformedAppointments = response.data.map(app => ({
        id: app.terminID,
        patientName: app.pacijentIme || 'N/A',
        doctorName: app.doktorIme || 'N/A',
        date: app.datum,
        time: app.vrijeme,
        status: app.status,
      }));
      setAppointments(transformedAppointments);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja termina:", error);
      showNotification("Greška prilikom dohvaćanja termina sa servera.", "danger");
    }
  };

  useEffect(() => {
    if (!token) {
      console.warn("Nema tokena, preskačem dohvaćanje podataka.");
      return;
    }
    fetchDoctors();
    fetchPatients();
    fetchAppointments();
  }, [token]);

  useEffect(() => {
    localStorage.setItem('adminActiveTab', activeTab);
  }, [activeTab]);


  const handleAddDoctor = async (newDoctorData) => {
    try {
      const registerPayload = {
        email: newDoctorData.email,
        password: newDoctorData.password,
        fullName: `${newDoctorData.ime} ${newDoctorData.prezime}`,
        telefon: newDoctorData.telefon,
        roles: ["ROLE_DOKTOR"],
        grad: newDoctorData.grad,
        specijalizacije: newDoctorData.specijalizacije,
      };

      await axios.post(`${AUTH_API_PREFIX}/register`, registerPayload);
      fetchDoctors();
      setShowDoctorModal(false);
      showNotification('Novi doktor uspješno registrovan i dodan!');
    } catch (error) {
      console.error("Greška prilikom dodavanja doktora (registracije):", error);
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja doktora.', 'danger');
    }
  };

  const handleUpdateDoctor = async (updatedDoctor) => {
    try {
      const doctorUpdatePayload = {
        ime: updatedDoctor.ime,
        prezime: updatedDoctor.prezime,
        email: updatedDoctor.email,
        telefon: updatedDoctor.telefon,
        grad: updatedDoctor.grad,
        specijalizacije: updatedDoctor.specijalizacije,
        radnoVrijeme: updatedDoctor.radnoVrijeme,
        iskustvo: updatedDoctor.iskustvo,
        ocjena: updatedDoctor.ocjena,
      };
      await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/doktori/${updatedDoctor.id}`, doctorUpdatePayload, authHeaders);

      const userUpdatePayload = {
        email: updatedDoctor.email,
        fullName: `${updatedDoctor.ime} ${updatedDoctor.prezime}`,
        telefon: updatedDoctor.telefon,
      };
      if (updatedDoctor.userId) {
          await axios.put(`${AUTH_API_PREFIX}/users/${updatedDoctor.userId}`, userUpdatePayload, authHeaders);
      } else {
          console.warn("Nije pronađen userId za ažuriranje korisnika u Auth servisu.");
      }

      fetchDoctors();
      setShowDoctorModal(false);
      setEditingDoctor(null);
      showNotification('Doktor uspješno ažuriran!');
    } catch (error) {
      console.error("Greška prilikom ažuriranja doktora:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja doktora.', 'danger');
    }
  };

  const handleDeleteDoctor = async (id) => {
    const confirmed = window.confirm('Jeste li sigurni da želite obrisati ovog doktora? Ovom akcijom će se obrisati i korisnički nalog.');
    if (!confirmed) {
      return;
    }
    try {
      const doctorToDelete = doctors.find(doc => doc.id === id);
      if (!doctorToDelete || !doctorToDelete.userId) {
        showNotification('Nije pronađen korisnički ID za brisanje doktora.', 'danger');
        return;
      }

      await axios.delete(`${KORISNICI_DOKTORI_API_PREFIX}/doktori/${id}`, authHeaders);
      await axios.delete(`${AUTH_API_PREFIX}/users/${doctorToDelete.userId}`, authHeaders);

      fetchDoctors();
      showNotification('Doktor uspješno obrisan!', 'danger');
    } catch (error) {
      console.error("Greška prilikom brisanja doktora:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom brisanja doktora.', 'danger');
    }
  };

  const openDoctorModal = (doctor = null) => {
    setEditingDoctor(doctor);
    setShowDoctorModal(true);
  };

  const filteredDoctors = doctors.filter(doctor =>
    doctor.name.toLowerCase().includes(doctorSearchTerm.toLowerCase()) ||
    doctor.specialty.toLowerCase().includes(doctorSearchTerm.toLowerCase()) ||
    doctor.city.toLowerCase().includes(doctorSearchTerm.toLowerCase())
  );
  const indexOfLastDoctor = doctorsCurrentPage * itemsPerPage;
  const indexOfFirstDoctor = indexOfLastDoctor - itemsPerPage;
  const currentDoctors = filteredDoctors.slice(indexOfFirstDoctor, indexOfLastDoctor);
  const totalDoctorPages = Math.ceil(filteredDoctors.length / itemsPerPage);

  const handleAddPatient = async (newPatient) => {
    try {
      const registerPayload = {
        email: newPatient.email,
        password: newPatient.password,
        fullName: `${newPatient.ime} ${newPatient.prezime}`,
        telefon: newPatient.telefon,
        roles: ["ROLE_PACIJENT"],
        grad: null,
        specijalizacije: null,
      };
      await axios.post(`${AUTH_API_PREFIX}/register`, registerPayload);
      fetchPatients();
      setShowPatientModal(false);
      showNotification('Novi pacijent uspješno registrovan i dodan!');
    } catch (error) {
      console.error("Greška prilikom dodavanja pacijenta (registracije):", error);
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja pacijenta.', 'danger');
    }
  };

  const handleUpdatePatient = async (updatedPatient) => {
    try {
      const patientUpdatePayload = {
        ime: updatedPatient.ime,
        prezime: updatedPatient.prezime,
        email: updatedPatient.email,
        telefon: updatedPatient.telefon,
      };
      await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/pacijenti/${updatedPatient.id}`, patientUpdatePayload, authHeaders);

      const userUpdatePayload = {
        email: updatedPatient.email,
        fullName: `${updatedPatient.ime} ${updatedPatient.prezime}`,
        telefon: updatedPatient.telefon,
      };
      if (updatedPatient.userId) {
          await axios.put(`${AUTH_API_PREFIX}/users/${updatedPatient.userId}`, userUpdatePayload, authHeaders);
      } else {
          console.warn("Nije pronađen userId za ažuriranje korisnika u Auth servisu.");
      }

      fetchPatients();
      setShowPatientModal(false);
      setEditingPatient(null);
      showNotification('Pacijent uspješno ažuriran!');
    } catch (error) {
      console.error("Greška prilikom ažuriranja pacijenta:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja pacijenta.', 'danger');
    }
  };

  const handleDeletePatient = async (id) => {
    const confirmed = window.confirm('Jeste li sigurni da želite obrisati ovog pacijenta? Ovom akcijom će se obrisati i korisnički nalog.');
    if (!confirmed) {
      return;
    }
    try {
      const patientToDelete = patients.find(pat => pat.id === id);
      if (!patientToDelete || !patientToDelete.userId) {
        showNotification('Nije pronađen korisnički ID za brisanje pacijenta.', 'danger');
        return;
      }

      await axios.delete(`${KORISNICI_DOKTORI_API_PREFIX}/pacijenti/${id}`, authHeaders);
      await axios.delete(`${AUTH_API_PREFIX}/users/${patientToDelete.userId}`, authHeaders);

      fetchPatients();
      showNotification('Pacijent uspješno obrisan!', 'danger');
    } catch (error) {
      console.error("Greška prilikom brisanja pacijenta:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom brisanja pacijenta.', 'danger');
    }
  };

  const openPatientModal = (patient = null) => {
    setEditingPatient(patient);
    setShowPatientModal(true);
  };

  const filteredPatients = patients.filter(patient =>
    patient.name.toLowerCase().includes(patientSearchTerm.toLowerCase()) ||
    patient.email.toLowerCase().includes(patientSearchTerm.toLowerCase()) ||
    (patient.phone && patient.phone.toLowerCase().includes(patientSearchTerm.toLowerCase()))
  );
  const indexOfLastPatient = patientsCurrentPage * itemsPerPage;
  const indexOfFirstPatient = indexOfLastPatient - itemsPerPage;
  const currentPatients = filteredPatients.slice(indexOfFirstPatient, indexOfLastPatient);
  const totalPatientPages = Math.ceil(filteredPatients.length / itemsPerPage);

  const handleUpdateAppointmentStatus = async (id, newStatus) => {
    try {
      const response = await axios.put(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/${id}/status`, { status: newStatus }, authHeaders);
      fetchAppointments();
      showNotification(`Status termina ažuriran na ${newStatus}!`, 'info');
    } catch (error) {
      console.error("Greška prilikom ažuriranja statusa termina:", error);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja statusa termina.', 'danger');
    }
  };

  const indexOfLastAppointment = appointmentsCurrentPage * itemsPerPage;
  const indexOfFirstAppointment = indexOfLastAppointment - itemsPerPage;
  const currentAppointments = appointments.slice(indexOfFirstAppointment, indexOfLastAppointment);
  const totalAppointmentPages = Math.ceil(appointments.length / itemsPerPage);

  const DataModal = ({ show, onClose, data, onSave, fields, title, idPrefix }) => {
    const [formData, setFormData] = useState(data || {});

    useEffect(() => {
      if (data) {
        // Kada se modal otvara za uređivanje, osiguravamo da su numerička polja null ako su prazna
        const updatedData = { ...data };
        // Ako je radnoVrijeme 'N/A' (prikaz), konvertuj u null za spremanje
        if (updatedData.radnoVrijeme === 'N/A') {
          updatedData.radnoVrijeme = null;
        }
        // Ako su iskustvo ili ocjena 0 (prikaz), a u bazi su bili null, postavi ih na null za formu
        // Ovo je bitno da se ne šalje 0 ako je polje prazno
        if (updatedData.iskustvo === 0 && (data.iskustvo === null || data.iskustvo === undefined)) {
            updatedData.iskustvo = null;
        }
        if (updatedData.ocjena === 0.0 && (data.ocjena === null || data.ocjena === undefined)) {
            updatedData.ocjena = null;
        }

        // Specijalizacije moraju biti niz
        if (Array.isArray(updatedData.specijalizacije) && updatedData.specijalizacije.length > 0) {
          // Nema potrebe za promjenom, već je niz
        } else {
          updatedData.specijalizacije = []; // Osiguraj da je niz
        }

        setFormData(updatedData);
      } else {
        // Za nove unose, inicijaliziraj numerička polja na null, tekstualna na prazan string ili null
        const initialData = {};
        fields.forEach(field => {
          if (field.name === 'specijalizacije') {
            initialData[field.name] = [];
          } else if (field.type === 'number') {
            initialData[field.name] = null; // Inicijaliziraj numerička polja na null
          } else {
            initialData[field.name] = ''; // Inicijaliziraj tekstualna polja na prazan string
          }
        });
        setFormData(initialData);
      }
    }, [data, fields]);

    const handleChange = (e) => {
      const { name, value, type } = e.target;

      setFormData(prev => {
        let newValue = value;

        if (name === 'specijalizacije') {
          newValue = [value]; // Uvijek šalji kao niz s jednim elementom za select
        } else if (type === 'number') {
          // Konvertuj prazan string u null, inače u broj
          newValue = value === '' ? null : Number(value);
          // Dodatna provjera za NaN ako konverzija ne uspije
          if (isNaN(newValue)) {
              newValue = null;
          }
        } else if (type === 'text' && value === '') {
          // Za tekstualna polja, ako želiš da prazan string bude null u bazi
          // Ako je polje opcionalno i želiš da se spremi kao NULL, koristi ovo:
          // if (name === 'radnoVrijeme') { // Primjer: samo za radnoVrijeme
          //   newValue = null;
          // } else {
          //   newValue = value; // Ostala tekstualna polja kao prazan string
          // }
        }

        return { ...prev, [name]: newValue };
      });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      // Prije spremanja, osiguraj da su specijalizacije uvijek niz
      const finalFormData = { ...formData };
      if (typeof finalFormData.specijalizacije === 'string') {
          finalFormData.specijalizacije = finalFormData.specijalizacije ? [finalFormData.specijalizacije] : [];
      }
      onSave(finalFormData);
    };

    return (
      <Modal show={show} onHide={onClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {fields.map(field => (
              <Form.Group className="mb-3" controlId={`${idPrefix}-${field.name}`} key={field.name}>
                <Form.Label>{field.label}</Form.Label>
                {field.type === 'select' ? (
                  <Form.Select
                    name={field.name}
                    value={Array.isArray(formData[field.name]) && formData[field.name].length > 0
                             ? formData[field.name][0]
                             : formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  >
                    <option value="">Select {field.label}</option>
                    {field.options.map(option => (
                      <option key={option} value={option}>{option}</option>
                    ))}
                  </Form.Select>
                ) : field.type === 'textarea' ? (
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  />
                ) : (
                  <Form.Control
                    type={field.type || 'text'}
                    name={field.name}
                    // Za numeričke inpute, prikaži prazan string ako je vrijednost null ili 0,
                    // ali samo ako je originalna vrijednost bila null/undefined (tj. nije bio 0)
                    value={formData[field.name] === null || (field.type === 'number' && formData[field.name] === 0 && (data === null || data[field.name] === null || data[field.name] === undefined))
                             ? ''
                             : formData[field.name]}
                    onChange={handleChange}
                    required={field.required}
                  />
                )}
              </Form.Group>
            ))}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={onClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Save changes
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    );
  };

  const Pagination = ({ itemsPerPage, totalItems, currentPage, paginate }) => {
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
      pageNumbers.push(i);
    }

    return (
      <nav>
        <ul className="pagination justify-content-center mt-4">
          {pageNumbers.map(number => (
            <li key={number} className={`page-item ${currentPage === number ? 'active' : ''}`}>
              <button onClick={() => paginate(number)} className="page-link">
                {number}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    );
  };

  const renderContent = () => {
    const addDoctorFields = [
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'password', label: 'Password', type: 'password', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
      { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla'] },
      { name: 'specijalizacije', label: 'Specialty', type: 'select', required: true, options: ['Kardiolog', 'Pedijatar', 'Dermatolog', 'Neurolog', 'Ortoped', 'Hirurg', 'Opća praksa'] },
      { name: 'radnoVrijeme', label: 'Work Hours', type: 'text', required: false },
      { name: 'iskustvo', label: 'Experience (years)', type: 'number', required: false },
      { name: 'ocjena', label: 'Rating (0.0-5.0)', type: 'number', required: false },
    ];

    const editDoctorFields = [
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
      { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla'] },
      { name: 'specijalizacije', label: 'Specialty', type: 'select', required: true, options: ['Kardiolog', 'Pedijatar', 'Dermatolog', 'Neurolog', 'Ortoped', 'Hirurg', 'Opća praksa'] },
      { name: 'radnoVrijeme', label: 'Work Hours', type: 'text', required: false },
      { name: 'iskustvo', label: 'Experience (years)', type: 'number', required: false },
      { name: 'ocjena', label: 'Rating (0.0-5.0)', type: 'number', required: false },
    ];

    const addPatientFields = [
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'password', label: 'Password', type: 'password', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
    ];

    const editPatientFields = [
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
    ];


    switch (activeTab) {
      case 'overview':
        return (
          <div>
            <h4 className="mb-4">Dashboard Overview</h4>
            <div className="row g-4">
              <div className="col-md-4">
                <div className="card text-white bg-primary mb-3">
                  <div className="card-header">Total Doctors</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{doctors.length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card text-white bg-success mb-3">
                  <div className="card-header">Total Patients</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{patients.length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card text-white bg-warning mb-3">
                  <div className="card-header">Pending Appointments</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{appointments.filter(app => app.status === 'Pending').length}</h5>
                  </div>
                </div>
              </div>
            </div>
            <div className="mt-5">
              <h5>Quick Actions</h5>
              <div className="d-flex gap-2">
                <button className="btn btn-outline-primary" onClick={() => openDoctorModal()}>Add New Doctor</button>
                <button className="btn btn-outline-success" onClick={() => openPatientModal()}>Add New Patient</button>
                <button className="btn btn-outline-info" onClick={() => setActiveTab('appointments')}>View Appointments</button>
              </div>
            </div>
          </div>
        );
      case 'doctors':
        return (
          <div>
            <h4 className="mb-4">Manage Doctors</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openDoctorModal()}>Add New Doctor</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Search doctors..."
                value={doctorSearchTerm}
                onChange={(e) => setDoctorSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Specialty</th>
                    <th>City</th>
                    <th>Email</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentDoctors.length > 0 ? (
                    currentDoctors.map(doctor => (
                      <tr key={doctor.id}>
                        <td>{doctor.id}</td>
                        <td>{doctor.name}</td>
                        <td>{doctor.specialty}</td>
                        <td>{doctor.city}</td>
                        <td>{doctor.email}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openDoctorModal(doctor)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteDoctor(doctor.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6" className="text-center">No doctors found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredDoctors.length}
              currentPage={doctorsCurrentPage}
              paginate={setDoctorsCurrentPage}
            />
          </div>
        );
      case 'patients':
        return (
          <div>
            <h4 className="mb-4">Manage Patients</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openPatientModal()}>Add New Patient</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Search patients..."
                value={patientSearchTerm}
                onChange={(e) => setPatientSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentPatients.length > 0 ? (
                    currentPatients.map(patient => (
                      <tr key={patient.id}>
                        <td>{patient.id}</td>
                        <td>{patient.name}</td>
                        <td>{patient.email}</td>
                        <td>{patient.phone}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openPatientModal(patient)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeletePatient(patient.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center">No patients found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredPatients.length}
              currentPage={patientsCurrentPage}
              paginate={setPatientsCurrentPage}
            />
          </div>
        );
      case 'appointments':
        return (
          <div>
            <h4 className="mb-4">Manage Appointments</h4>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Patient</th>
                    <th>Doctor</th>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentAppointments.length > 0 ? (
                    currentAppointments.map(appointment => (
                      <tr key={appointment.id}>
                        <td>{appointment.id}</td>
                        <td>{appointment.patientName}</td>
                        <td>{appointment.doctorName}</td>
                        <td>{appointment.date}</td>
                        <td>{appointment.time}</td>
                        <td>
                          <select
                            className="form-select"
                            value={appointment.status}
                            onChange={(e) => handleUpdateAppointmentStatus(appointment.id, e.target.value)}
                          >
                            <option value="Pending">Pending</option>
                            <option value="Confirmed">Confirmed</option>
                            <option value="Completed">Completed</option>
                            <option value="Cancelled">Cancelled</option>
                          </select>
                        </td>
                        <td>
                          <button className="btn btn-danger btn-sm" onClick={() => showNotification('Brisanje termina nije implementirano.', 'warning')}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="7" className="text-center">No appointments found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={appointments.length}
              currentPage={appointmentsCurrentPage}
              paginate={setAppointmentsCurrentPage}
            />
          </div>
        );
      default:
        return <p>Select a tab to view content.</p>;
    }
  };

  const addDoctorFields = [
    { name: 'ime', label: 'First Name', type: 'text', required: true },
    { name: 'prezime', label: 'Last Name', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'password', label: 'Password', type: 'password', required: true },
    { name: 'telefon', label: 'Phone', type: 'text', required: false },
    { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla'] },
    { name: 'specijalizacije', label: 'Specialty', type: 'select', required: true, options: ['Kardiolog', 'Pedijatar', 'Dermatolog', 'Neurolog', 'Ortoped', 'Hirurg', 'Opća praksa'] },
    { name: 'radnoVrijeme', label: 'Work Hours', type: 'text', required: false },
    { name: 'iskustvo', label: 'Experience (years)', type: 'number', required: false },
    { name: 'ocjena', label: 'Rating (0.0-5.0)', type: 'number', required: false },
  ];

  const editDoctorFields = [
    { name: 'ime', label: 'First Name', type: 'text', required: true },
    { name: 'prezime', label: 'Last Name', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'telefon', label: 'Phone', type: 'text', required: false },
    { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla'] },
    { name: 'specijalizacije', label: 'Specialty', type: 'select', required: true, options: ['Kardiolog', 'Pedijatar', 'Dermatolog', 'Neurolog', 'Ortoped', 'Hirurg', 'Opća praksa'] },
    { name: 'radnoVrijeme', label: 'Work Hours', type: 'text', required: false },
    { name: 'iskustvo', label: 'Experience (years)', type: 'number', required: false },
    { name: 'ocjena', label: 'Rating (0.0-5.0)', type: 'number', required: false },
  ];

  const addPatientFields = [
    { name: 'ime', label: 'First Name', type: 'text', required: true },
    { name: 'prezime', label: 'Last Name', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'password', label: 'Password', type: 'password', required: true },
    { name: 'telefon', label: 'Phone', type: 'text', required: false },
  ];

  const editPatientFields = [
    { name: 'ime', label: 'First Name', type: 'text', required: true },
    { name: 'prezime', label: 'Last Name', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'telefon', label: 'Phone', type: 'text', required: false },
  ];


  return (
    <div className="container-fluid">
      {showAlert && (
        <Alert variant={alertType} onClose={() => setShowAlert(false)} dismissible className="position-fixed top-0 start-50 translate-middle-x mt-3" style={{ zIndex: 1050 }}>
          {alertMessage}
        </Alert>
      )}

      <div className="row">
        <nav className="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse" style={{ minHeight: '100vh' }}>
          <div className="position-sticky pt-3">
            <ul className="nav flex-column">
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'overview' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('overview')}>
                  Overview
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'doctors' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('doctors')}>
                  Doctors
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'patients' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('patients')}>
                  Patients
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'appointments' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('appointments')}>
                  Appointments
                </a>
              </li>
            </ul>
          </div>
        </nav>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4 py-4">
          {renderContent()}
        </main>
      </div>

      <DataModal
        show={showDoctorModal}
        onClose={() => setShowDoctorModal(false)}
        data={editingDoctor}
        onSave={editingDoctor ? handleUpdateDoctor : handleAddDoctor}
        title={editingDoctor ? 'Edit Doctor' : 'Add New Doctor'}
        idPrefix="doctor"
        fields={editingDoctor ? editDoctorFields : addDoctorFields}
      />

      <DataModal
        show={showPatientModal}
        onClose={() => setShowPatientModal(false)}
        data={editingPatient}
        onSave={editingPatient ? handleUpdatePatient : handleAddPatient}
        title={editingPatient ? 'Edit Patient' : 'Add New Patient'}
        idPrefix="patient"
        fields={editingPatient ? editPatientFields : addPatientFields}
      />
    </div>
  );
}

export default AdminDashboard;
