import React, { useState, useEffect, useMemo } from 'react'; // AŽURIRANO: Dodaj useMemo ovdje
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useAuth } from '../contexts/AuthContext';

const GATEWAY_BASE_URL = 'http://localhost:8081';
const KORISNICI_DOKTORI_API_PREFIX = `${GATEWAY_BASE_URL}/api/korisnici-doktori`;
const TERMINI_PREGLEDI_API_PREFIX = `${GATEWAY_BASE_URL}/api/termini-pregledi`;

function DoctorProfilePage() {
    const { id } = useParams();
    const { token, user } = useAuth();

    const [doctor, setDoctor] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [availableTerms, setAvailableTerms] = useState([]);
    const [selectedDate, setSelectedDate] = useState('');
    const [selectedTime, setSelectedTime] = useState('');
    const [reason, setReason] = useState('');
    const [bookingMessage, setBookingMessage] = useState('');
    const [bookingMessageType, setBookingMessageType] = useState('success');

    const authHeaders = useMemo(() => ({
        headers: {
            'Authorization': `Bearer ${token}`
        }
    }), [token]);

    // Funkcija za dohvaćanje detalja doktora
    useEffect(() => {
        const fetchDoctorDetails = async () => {
            try {
                const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori/${id}`, authHeaders);
                const data = response.data;

                const transformedDoctor = {
                    doktorID: data.doktorID,
                    ime: data.ime,
                    prezime: data.prezime,
                    fullName: `${data.ime} ${data.prezime}`,
                    email: data.email,
                    telefon: data.telefon,
                    specijalizacije: Array.isArray(data.specijalizacije) ? data.specijalizacije.join(', ') : data.specijalizacije,
                    grad: data.grad,
                    radnoVrijeme: data.radnoVrijeme,
                    iskustvo: data.iskustvo,
                    ocjena: data.ocjena,
                    profileImageBase64: data.profileImageBase64 || `https://placehold.co/100x100/CCCCCC/000000?text=${data.ime.charAt(0)}${data.prezime.charAt(0)}`
                };
                setDoctor(transformedDoctor);
            } catch (err) {
                console.error("Greška prilikom dohvaćanja detalja doktora:", err.response?.data || err.message);
                setError("Nije moguće učitati profil doktora. Molimo pokušajte ponovo.");
            } finally {
                setLoading(false);
            }
        };

        if (id) {
            fetchDoctorDetails();
        }
    }, [id, token]);

    // Funkcija za dohvaćanje dostupnih termina
    useEffect(() => {
        const fetchDoctorAvailability = async () => {
            if (!selectedDate || !doctor?.doktorID) {
                setAvailableTerms([]);
                return;
            }
            try {
                const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/termini/doktor/${doctor.doktorID}/datum/${selectedDate}`, authHeaders);
                const available = response.data
                    .filter(term => term.dostupnost)
                    .map(term => ({ terminID: term.terminID, vrijeme: term.vrijeme }))
                    .sort((a, b) => a.vrijeme.localeCompare(b.vrijeme));
                setAvailableTerms(available);
            } catch (err) {
                console.error('Greška prilikom dohvaćanja dostupnosti doktora:', err.response?.data || err.message);
                setAvailableTerms([]);
                setBookingMessage('Greška prilikom dohvaćanja dostupnih termina.');
                setBookingMessageType('danger');
            }
        };

        if (doctor?.doktorID && selectedDate) {
            fetchDoctorAvailability();
        }
    }, [doctor, selectedDate, token]);

    const handleDateChange = (e) => {
        setSelectedDate(e.target.value);
        setSelectedTime(''); // Resetuj vrijeme kada se datum promijeni
    };

    const handleTimeChange = (e) => {
        setSelectedTime(e.target.value);
    };

    const handleReasonChange = (e) => {
        setReason(e.target.value);
    };

    const handleBookAppointment = async (e) => {
        e.preventDefault();
        setBookingMessage('');
        if (!user || !user.token) {
            setBookingMessage('Morate biti prijavljeni da biste zakazali pregled.');
            setBookingMessageType('danger');
            return;
        }
        if (!selectedDate || !selectedTime || !reason || !doctor?.doktorID) {
            setBookingMessage('Molimo popunite sva polja za zakazivanje pregleda.');
            setBookingMessageType('danger');
            return;
        }
        const selectedTermin = availableTerms.find(term => term.vrijeme === selectedTime);
        if (!selectedTermin) {
            setBookingMessage('Odabrani termin nije validan ili dostupan.');
            setBookingMessageType('danger');
            return;
        }

        try {
            const pacijentId = user?.pacijentId;
            if (!pacijentId) {
                setBookingMessage('Vaš pacijent ID nije pronađen. Molimo osigurajte da ste prijavljeni kao pacijent.');
                setBookingMessageType('danger');
                return;
            }

            const appointmentData = {
                pacijentID: pacijentId,
                doktorID: doctor.doktorID,
                terminID: selectedTermin.terminID,
                datumPregleda: selectedDate,
                vrijemePregleda: selectedTime,
                komentarPacijenta: reason,
                status: 'zakazan'
            };
            await axios.post(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi`, appointmentData, authHeaders);
            setBookingMessage('Pregled uspješno zakazan!');
            setBookingMessageType('success');
            setSelectedDate('');
            setSelectedTime('');
            setReason('');
            setAvailableTerms([]);
        } catch (err) {
            console.error("Greška prilikom zakazivanja pregleda:", err.response?.data || err.message);
            setBookingMessage(`Greška prilikom zakazivanja pregleda: ${err.response?.data?.message || err.message}`);
            setBookingMessageType('danger');
        }
    };

    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
                <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Učitavanje profila doktora...</span>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="container py-5 text-center">
                <p className="lead text-danger">{error}</p>
                <Link to="/service" className="btn btn-primary">Nazad na listu doktora</Link>
            </div>
        );
    }

    if (!doctor) {
        return (
            <div className="container py-5 text-center">
                <p className="lead">Doktor nije pronađen.</p>
                <Link to="/service" className="btn btn-primary">Nazad na listu doktora</Link>
            </div>
        );
    }

    return (
        <div className="container my-5">
            <div className="card shadow-lg p-4 mb-4">
                <div className="row g-4">
                    <div className="col-md-4 text-center">
                        <img
                            src={doctor.profileImageBase64}
                            alt={doctor.fullName}
                            className="img-fluid rounded-circle shadow-sm mb-3"
                            style={{ width: '150px', height: '150px', objectFit: 'cover', border: '3px solid #dc3545' }}
                        />
                        <h3 className="mb-1">{doctor.fullName}</h3>
                        <p className="text-muted">{doctor.specijalizacije}</p>
                        <div className="d-flex justify-content-center align-items-center">
                            <span className="text-warning me-1">★</span>
                            <span className="fw-bold">{doctor.ocjena || 'N/A'}</span>
                            <span className="text-muted ms-2">({doctor.grad})</span>
                        </div>
                    </div>
                    <div className="col-md-8">
                        <h4 className="text-danger mb-3">Informacije o doktoru</h4>
                        <p><strong>Email:</strong> {doctor.email}</p>
                        <p><strong>Telefon:</strong> {doctor.telefon}</p>
                        <p><strong>Grad:</strong> {doctor.grad}</p>
                        <p><strong>Radno Vrijeme:</strong> {doctor.radnoVrijeme}</p>
                        <p><strong>Godine Iskustva:</strong> {doctor.iskustvo}</p>
                    </div>
                </div>
            </div>

            <div className="card shadow-lg p-4">
                <h4 className="text-danger mb-3">Zakažite pregled</h4>
                {bookingMessage && (
                    <div className={`alert alert-${bookingMessageType} mb-3`} role="alert">
                        {bookingMessage}
                    </div>
                )}
                <form onSubmit={handleBookAppointment}>
                    <div className="row mb-3">
                        <div className="col-md-6">
                            <label htmlFor="appointmentDate" className="form-label">Datum</label>
                            <input
                                type="date"
                                className="form-control"
                                id="appointmentDate"
                                value={selectedDate}
                                onChange={handleDateChange}
                                required
                            />
                        </div>
                        <div className="col-md-6">
                            <label htmlFor="appointmentTime" className="form-label">Vrijeme</label>
                            <select
                                className="form-select"
                                id="appointmentTime"
                                value={selectedTime}
                                onChange={handleTimeChange}
                                required
                                disabled={availableTerms.length === 0}
                            >
                                <option value="">Odaberite vrijeme</option>
                                {availableTerms.map(term => (
                                    <option key={term.terminID} value={term.vrijeme}>{term.vrijeme}</option>
                                ))}
                            </select>
                            {availableTerms.length === 0 && selectedDate && !error && (
                                <small className="text-muted">Nema dostupnih termina za odabrani datum.</small>
                            )}
                        </div>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="reason" className="form-label">Razlog pregleda</label>
                        <textarea
                            className="form-control"
                            id="reason"
                            rows="3"
                            value={reason}
                            onChange={handleReasonChange}
                            placeholder="Kratak opis razloga pregleda..."
                            required
                        ></textarea>
                    </div>
                    <button type="submit" className="btn btn-danger">Zakaži pregled</button>
                </form>
            </div>
        </div>
    );
}

export default DoctorProfilePage;
