import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Form, Alert } from 'react-bootstrap';

const GATEWAY_BASE_URL = 'http://localhost:8081';

// Ovi prefiksi sada sadrže gateway URL i context-path servisa.
// Pretpostavljamo da backend servisi interno imaju /api/ prefiks za svoje kontrolere.
const KORISNICI_DOKTORI_API_PREFIX = `${GATEWAY_BASE_URL}/api/korisnici-doktori`;
const TERMINI_PREGLEDI_API_PREFIX = `${GATEWAY_BASE_URL}/api/termini-pregledi`;
const AUTH_API_PREFIX = `${GATEWAY_BASE_URL}/auth`;

function AdminDashboard() {
  const { token } = useAuth();

  // Stanje za praćenje početnog učitavanja panela i autentifikacije
  const [loadingInitial, setLoadingInitial] = useState(true);
  const [isAuthChecked, setIsAuthChecked] = useState(false);

  // Headeri za autentifikaciju, koristeći token iz AuthContext-a
  const authHeaders = {
    headers: {
      'Authorization': `Bearer ${token}`
    }
  };

  // Aktivni tab, sa čuvanjem u lokalnom skladištu
  const [activeTab, setActiveTab] = useState(() => {
    const savedTab = localStorage.getItem('adminActiveTab');
    return savedTab || 'overview';
  });

  // Stanja za podatke iz API-ja
  const [doctors, setDoctors] = useState([]);
  const [patients, setPatients] = useState([]);
  const [appointments, setAppointments] = useState([]);

  // Stanja za modale i forme
  const [showDoctorModal, setShowDoctorModal] = useState(false);
  const [editingDoctor, setEditingDoctor] = useState(null); // Čuva podatke doktora za uređivanje
  const [showPatientModal, setShowPatientModal] = useState(false);
  const [editingPatient, setEditingPatient] = useState(null);

  // Stanja za notifikacije (Alert)
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('success');

  // Stanja za pretragu
  const [doctorSearchTerm, setDoctorSearchTerm] = useState('');
  const [patientSearchTerm, setPatientSearchTerm] = useState('');

  // Stanja za paginaciju
  const [doctorsCurrentPage, setDoctorsCurrentPage] = useState(1);
  const [patientsCurrentPage, setPatientsCurrentPage] = useState(1);
  const [appointmentsCurrentPage, setAppointmentsCurrentPage] = useState(1);
  const itemsPerPage = 5; // Broj stavki po stranici

  // Funkcija za prikaz notifikacija
  const showNotification = (message, type = 'success') => {
    setAlertMessage(message);
    setAlertType(type);
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
      setAlertMessage('');
    }, 3000); // Notifikacija nestaje nakon 3 sekunde
  };

  // Funkcije za dohvat podataka sa backend-a
  const fetchDoctors = async () => {
    try {
      // Pravi URL poziv: http://localhost:8081/api/korisnici-doktori/api/doktori
      const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori`, authHeaders);
      const transformedDoctors = response.data.map(doc => ({
        id: doc.doktorID,
        userId: doc.userId, // Važno za povezivanje sa Auth servisom
        name: `${doc.ime} ${doc.prezime}`,
        ime: doc.ime,
        prezime: doc.prezime,
        specialty: Array.isArray(doc.specijalizacije) && doc.specijalizacije.length > 0
                   ? doc.specijalizacije.join(', ')
                   : 'N/A',
        specijalizacije: doc.specijalizacije, // Ovo mora biti niz stringova
        city: doc.grad || 'N/A',
        grad: doc.grad,
        email: doc.email || 'N/A',
        telefon: doc.telefon || 'N/A',
        radnoVrijeme: doc.radnoVrijeme || 'N/A',
        iskustvo: doc.iskustvo !== null && doc.iskustvo !== undefined ? doc.iskustvo : 'N/A',
        ocjena: doc.ocjena !== null && doc.ocjena !== undefined ? doc.ocjena : 'N/A',
      }));
      setDoctors(transformedDoctors);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja doktora:", error.response?.data || error.message);
      showNotification("Greška prilikom dohvaćanja doktora sa servera.", "danger");
    }
  };

  const fetchPatients = async () => {
    try {
      // Pravi URL poziv: http://localhost:8081/api/korisnici-doktori/api/pacijenti
      const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti`, authHeaders);
      const transformedPatients = response.data.map(pat => ({
        id: pat.pacijentID,
        userId: pat.userId, // Važno za povezivanje sa Auth servisom
        name: `${pat.ime} ${pat.prezime}`,
        ime: pat.ime,
        prezime: pat.prezime,
        email: pat.email || 'N/A',
        phone: pat.telefon || 'N/A',
        telefon: pat.telefon,
      }));
      setPatients(transformedPatients);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja pacijenata:", error.response?.data || error.message);
      showNotification("Greška prilikom dohvaćanja pacijenata sa servera.", "danger");
    }
  };

  const fetchAppointments = async () => {
    try {
      // Pravi URL poziv: http://localhost:8081/api/termini-pregledi/api/pregledi
      const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi`, authHeaders);
      const transformedAppointments = response.data.map(app => ({
        id: app.pregledID, // Koristimo pregledID umjesto terminID ako je to stvarni ID
        patientName: app.pacijentIme || 'N/A',
        doctorName: app.doktorIme || 'N/A',
        date: app.datumPregleda, // Prilagodite prema stvarnom nazivu polja u backend-u
        time: app.vrijemePregleda, // Prilagodite prema stvarnom nazivu polja u backend-u
        status: app.status,
      }));
      setAppointments(transformedAppointments);
    } catch (error) {
      console.error("Greška prilikom dohvaćanja termina:", error.response?.data || error.message);
      showNotification("Greška prilikom dohvaćanja termina sa servera.", "danger");
    }
  };

  // Glavni useEffect hook za provjeru autentifikacije i dohvat podataka
  useEffect(() => {
    console.log("AdminDashboard useEffect - Trenutni token:", token);
    console.log("AdminDashboard useEffect - isAuthChecked:", isAuthChecked);
    console.log("AdminDashboard useEffect - loadingInitial (prije logike):", loadingInitial);

    if (token !== undefined && !isAuthChecked) {
      setIsAuthChecked(true);
      console.log("AdminDashboard useEffect - Provjera autentifikacije završena, postavljen isAuthChecked na true.");
    }

    const initializeDashboard = async () => {
      if (isAuthChecked) {
        if (token) {
          try {
            await Promise.all([
              fetchDoctors(),
              fetchPatients(),
              fetchAppointments()
            ]);
            console.log("AdminDashboard useEffect - Podaci uspješno dohvaćeni.");
          } catch (error) {
            console.error("Greška prilikom inicijalnog dohvaćanja podataka:", error);
            showNotification("Greška prilikom učitavanja početnih podataka.", "danger");
          } finally {
            setLoadingInitial(false);
            console.log("AdminDashboard useEffect - Postavljen loadingInitial na false nakon dohvaćanja podataka/greške.");
          }
        } else {
          setLoadingInitial(false);
          console.warn("Provjera autentifikacije završena, ali token nije dostupan. Korisnik nije ulogovan/autorizovan.");
        }
      } else {
        console.log("AdminDashboard useEffect - Provjera autentifikacije još nije završena, loadingInitial ostaje true.");
      }
    };

    initializeDashboard();
  }, [token, isAuthChecked]);

  useEffect(() => {
    localStorage.setItem('adminActiveTab', activeTab);
  }, [activeTab]);

  // Funkcija za dodavanje novog doktora
  const handleAddDoctor = async (newDoctorData) => {
    try {
      // 1. Registracija korisnika u Auth servisu sa osnovnim podacima
      const registerPayload = {
        email: newDoctorData.email,
        password: newDoctorData.password,
        fullName: `${newDoctorData.ime} ${newDoctorData.prezime}`,
        telefon: newDoctorData.telefon,
        roles: ["ROLE_DOKTOR"],
        grad: newDoctorData.grad,
        specijalizacije: newDoctorData.specijalizacije,
      };

      const authResponse = await axios.post(`${AUTH_API_PREFIX}/register`, registerPayload);
      const userId = authResponse.data.id; // Dohvat ID-a korisnika iz Auth servisa

      // 2. Slanje kompletnih detalja doktora servisu korisnici-doktori
      // Ovo je PUT poziv jer će korisnici-doktori servis "ažurirati" (dodati detalje) za tek kreiranog doktora.
      // Važno: U korisnici-doktori servisu mora postojati PUT endpoint poput /api/doktori/{userId}
      // koji prima detalje doktora i ažurira ih za datog userId.
      const doctorDetailsPayload = {
        userId: userId, // Povezivanje sa korisničkim nalogom
        ime: newDoctorData.ime,
        prezime: newDoctorData.prezime,
        email: newDoctorData.email,
        telefon: newDoctorData.telefon,
        grad: newDoctorData.grad,
        specijalizacije: newDoctorData.specijalizacije, // Može biti niz stringova
        radnoVrijeme: newDoctorData.radnoVrijeme,
        iskustvo: newDoctorData.iskustvo,
        ocjena: newDoctorData.ocjena,
      };

      // Poziv na korisnici-doktori servis za spašavanje detalja
      await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori/${userId}`, doctorDetailsPayload, authHeaders);

      fetchDoctors(); // Ponovo dohvati doktore da bi se prikazale promjene
      setShowDoctorModal(false);
      showNotification('Novi doktor uspješno registrovan i detalji sačuvani!');
    } catch (error) {
      console.error("Greška prilikom dodavanja doktora:", error.response?.data || error.message);
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja doktora.', 'danger');
    }
  };

  // Funkcija za ažuriranje postojećeg doktora
  const handleUpdateDoctor = async (updatedDoctor) => {
    try {
      // Podaci za ažuriranje doktora u korisnici-doktori servisu
      const doctorUpdatePayload = {
        ime: updatedDoctor.ime,
        prezime: updatedDoctor.prezime,
        email: updatedDoctor.email,
        telefon: updatedDoctor.telefon,
        grad: updatedDoctor.grad,
        specijalizacije: updatedDoctor.specijalizacije,
        radnoVrijeme: updatedDoctor.radnoVrijeme,
        iskustvo: updatedDoctor.iskustvo,
        ocjena: updatedDoctor.ocjena,
      };
      // Ažuriranje detalja doktora u korisnici-doktori servisu (koristimo doktorID)
      await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori/${updatedDoctor.id}`, doctorUpdatePayload, authHeaders);

      // Podaci za ažuriranje korisničkog naloga u Auth servisu
      const userUpdatePayload = {
        email: updatedDoctor.email,
        fullName: `${updatedDoctor.ime} ${updatedDoctor.prezime}`,
        telefon: updatedDoctor.telefon,
      };
      // Ažuriraj korisnički nalog u Auth servisu ako postoji userId
      if (updatedDoctor.userId) {
          await axios.put(`${AUTH_API_PREFIX}/users/${updatedDoctor.userId}`, userUpdatePayload, authHeaders);
      } else {
          console.warn("Nije pronađen userId za ažuriranje korisnika u Auth servisu.");
      }

      fetchDoctors(); // Ponovo dohvati doktore nakon ažuriranja
      setShowDoctorModal(false);
      setEditingDoctor(null);
      showNotification('Doktor uspješno ažuriran!');
    } catch (error) {
      console.error("Greška prilikom ažuriranja doktora:", error.response?.data || error.message);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja doktora.', 'danger');
    }
  };

  // CUSTOM CONFIRMATION MODAL STATE AND FUNCTIONS
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [confirmAction, setConfirmAction] = useState(() => () => {}); // Pohranjuje funkciju koja se poziva pri potvrdi
  const [confirmMessage, setConfirmMessage] = useState("");
  const [confirmTitle, setConfirmTitle] = useState("Potvrda");

  // Funkcija za prikaz prilagođenog modala za potvrdu
  const showCustomConfirm = (message, action, title = "Potvrda") => {
    setConfirmMessage(message);
    setConfirmAction(() => action); // Koristi funkcionalno ažuriranje za pohranu akcije
    setConfirmTitle(title);
    setShowConfirmModal(true);
  };

  // Funkcija koja se poziva kada korisnik potvrdi akciju u modalu
  const handleConfirmAction = () => {
    confirmAction(); // Izvrši pohranjenu akciju
    setShowConfirmModal(false);
  };

  // Funkcija za brisanje doktora (koristi custom confirm)
  const handleDeleteDoctor = async (id) => {
    showCustomConfirm(
      'Jeste li sigurni da želite obrisati ovog doktora? Ovom akcijom će se obrisati i korisnički nalog.',
      async () => {
        try {
          const doctorToDelete = doctors.find(doc => doc.id === id);
          if (!doctorToDelete || !doctorToDelete.userId) {
            showNotification('Nije pronađen korisnički ID za brisanje doktora.', 'danger');
            return;
          }

          // Brisanje doktora iz korisnici-doktori servisa
          await axios.delete(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori/${id}`, authHeaders);
          // Brisanje korisnika iz Auth servisa
          await axios.delete(`${AUTH_API_PREFIX}/users/${doctorToDelete.userId}`, authHeaders);

          fetchDoctors(); // Ponovo dohvati doktore nakon brisanja
          showNotification('Doktor uspješno obrisan!', 'danger');
        } catch (error) {
          console.error("Greška prilikom brisanja doktora:", error.response?.data || error.message);
          showNotification(error.response?.data?.message || 'Greška prilikom brisanja doktora.', 'danger');
        }
      },
      "Potvrdi brisanje doktora"
    );
  };

  // Otvaranje modala za doktora (dodavanje/uređivanje)
  const openDoctorModal = (doctor = null) => {
    setEditingDoctor(doctor);
    setShowDoctorModal(true);
  };

  // Filtriranje i paginacija doktora
  const filteredDoctors = doctors.filter(doctor =>
    doctor.name.toLowerCase().includes(doctorSearchTerm.toLowerCase()) ||
    doctor.specialty.toLowerCase().includes(doctorSearchTerm.toLowerCase()) ||
    doctor.city.toLowerCase().includes(doctorSearchTerm.toLowerCase()) ||
    (doctor.email && doctor.email.toLowerCase().includes(doctorSearchTerm.toLowerCase())) ||
    (doctor.telefon && doctor.telefon.toLowerCase().includes(doctorSearchTerm.toLowerCase())) ||
    (doctor.radnoVrijeme && doctor.radnoVrijeme.toLowerCase().includes(doctorSearchTerm.toLowerCase()))
  );
  const indexOfLastDoctor = doctorsCurrentPage * itemsPerPage;
  const indexOfFirstDoctor = indexOfLastDoctor - itemsPerPage;
  const currentDoctors = filteredDoctors.slice(indexOfFirstDoctor, indexOfLastDoctor);
  const totalDoctorPages = Math.ceil(filteredDoctors.length / itemsPerPage);

  // Funkcija za dodavanje novog pacijenta
  const handleAddPatient = async (newPatient) => {
    try {
      console.log("handleAddPatient: Počinjem proces dodavanja pacijenta.");
      // 1. Registracija korisnika u Auth servisu
      const registerPayload = {
        email: newPatient.email,
        password: newPatient.password,
        fullName: `${newPatient.ime} ${newPatient.prezime}`,
        telefon: newPatient.telefon,
        roles: ["ROLE_PATIENT"],
        grad: null, // Pacijentima grad nije obavezan
        specijalizacije: null, // Pacijenti nemaju specijalizacije
      };
      console.log("handleAddPatient: Slanje registerPayload-a Auth servisu:", registerPayload);
      const authResponse = await axios.post(`${AUTH_API_PREFIX}/register`, registerPayload);
      const userId = authResponse.data.id; // Dohvat ID-a korisnika
      console.log("handleAddPatient: Korisnik registrovan, userId:", userId, "Auth response:", authResponse.data);

      // 2. Slanje detalja pacijenta servisu korisnici-doktori
      // KLJUČNO: Ovdje bi trebao biti POST poziv na /api/pacijenti endpoint
      // ako tvoj backend očekuje kreiranje novog pacijenta putem POST metode na kolekciju
      // Ako tvoj backend ima PUT /api/pacijenti/{userId} endpoint koji kreira entitet
      // ako ne postoji, onda je PUT ispravan, ali provjeri odgovor tog endpointa.
      const patientDetailsPayload = {
          userId: userId, // Povezujemo sa korisničkim nalogom
          ime: newPatient.ime,
          prezime: newPatient.prezime,
          email: newPatient.email,
          telefon: newPatient.telefon,
      };
      console.log("handleAddPatient: Slanje patientDetailsPayload-a korisnici-doktori servisu:", patientDetailsPayload);

      // PROVJERI OVO: Ako backend za dodavanje pacijenata očekuje POST na /api/pacijenti
      // onda promijeni u axios.post(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti`, patientDetailsPayload, authHeaders);
      // Ako očekuje PUT na /api/pacijenti/{userId} koji kreira ako ne postoji, onda ostaje kako jeste.
      const patientCreationResponse = await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${userId}`, patientDetailsPayload, authHeaders);
      console.log("handleAddPatient: Pacijent detalji sačuvani. Odgovor korisnici-doktori:", patientCreationResponse.data);

      fetchPatients(); // Ponovo dohvati pacijente nakon dodavanja
      setShowPatientModal(false);
      showNotification('Novi pacijent uspješno registrovan i detalji sačuvani!');
      console.log("handleAddPatient: Proces dodavanja pacijenta uspješno završen.");
    } catch (error) {
      console.error("Greška prilikom dodavanja pacijenta:", error.response?.data || error.message);
      // Ovdje se izvodi showNotification sa 'danger' tipom, što je uzrok poruke
      // "Greška prilikom dodavanja" čak i ako je na kraju pacijent dodan.
      // Trebamo detaljnije provjeriti 'error.response.status' ili 'error.code'
      // ako je greška iz Axiosa, da bismo razlikovali stvarne greške od "lažnih pozitiva".
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja pacijenta. Detalji u konzoli.', 'danger');
    }
  };

  // Funkcija za ažuriranje postojećeg pacijenta
  const handleUpdatePatient = async (updatedPatient) => {
    try {
      const patientUpdatePayload = {
        ime: updatedPatient.ime,
        prezime: updatedPatient.prezime,
        email: updatedPatient.email,
        telefon: updatedPatient.telefon, // ISPRAVLJENO: bilo updatedData.telefon
      };
      // Ažuriranje detalja pacijenta u korisnici-doktori servisu
      await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${updatedPatient.id}`, patientUpdatePayload, authHeaders);

      // Ažuriranje korisničkog naloga u Auth servisu
      const userUpdatePayload = {
        email: updatedPatient.email,
        fullName: `${updatedPatient.ime} ${updatedPatient.prezime}`,
        telefon: updatedPatient.telefon,
      };
      if (updatedPatient.userId) {
          await axios.put(`${AUTH_API_PREFIX}/users/${updatedPatient.userId}`, userUpdatePayload, authHeaders);
      } else {
          console.warn("Nije pronađen userId za ažuriranje korisnika u Auth servisu.");
      }

      fetchPatients();
      setShowPatientModal(false);
      setEditingPatient(null);
      showNotification('Pacijent uspješno ažuriran!');
    } catch (error) {
      console.error("Greška prilikom ažuriranja pacijenta:", error.response?.data || error.message);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja pacijenta.', 'danger');
    }
  };

  // Funkcija za brisanje pacijenta
  const handleDeletePatient = async (id) => {
    showCustomConfirm(
      'Jeste li sigurni da želite obrisati ovog pacijenta? Ovom akcijom će se obrisati i korisnički nalog.',
      async () => {
        try {
          const patientToDelete = patients.find(pat => pat.id === id);
          if (!patientToDelete || !patientToDelete.userId) {
            showNotification('Nije pronađen korisnički ID za brisanje pacijenta.', 'danger');
            return;
          }

          await axios.delete(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${id}`, authHeaders);
          await axios.delete(`${AUTH_API_PREFIX}/users/${patientToDelete.userId}`, authHeaders);

          fetchPatients();
          showNotification('Pacijent uspješno obrisan!', 'danger');
        } catch (error) {
          console.error("Greška prilikom brisanja pacijenta:", error.response?.data || error.message);
          showNotification(error.response?.data?.message || 'Greška prilikom brisanja pacijenta.', 'danger');
        }
      },
      "Potvrdi brisanje pacijenta"
    );
  };

  // Otvaranje modala za pacijenta
  const openPatientModal = (patient = null) => {
    setEditingPatient(patient);
    setShowPatientModal(true);
  };

  // Filtriranje i paginacija pacijenata
  const filteredPatients = patients.filter(patient =>
    patient.name.toLowerCase().includes(patientSearchTerm.toLowerCase()) ||
    patient.email.toLowerCase().includes(patientSearchTerm.toLowerCase()) ||
    (patient.phone && patient.phone.toLowerCase().includes(patientSearchTerm.toLowerCase()))
  );
  const indexOfLastPatient = patientsCurrentPage * itemsPerPage;
  const indexOfFirstPatient = indexOfLastPatient - itemsPerPage;
  const currentPatients = filteredPatients.slice(indexOfFirstPatient, indexOfLastPatient);
  const totalPatientPages = Math.ceil(filteredPatients.length / itemsPerPage);

  // Funkcija za ažuriranje statusa termina
  const handleUpdateAppointmentStatus = async (id, newStatus) => {
    try {
      await axios.put(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/${id}/status`, { status: newStatus }, authHeaders);
      fetchAppointments();
      showNotification(`Status termina ažuriran na ${newStatus}!`, 'info');
    } catch (error) {
      console.error("Greška prilikom ažuriranja statusa termina:", error.response?.data || error.message);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja statusa termina.', 'danger');
    }
  };

  // Paginacija termina
  const indexOfLastAppointment = appointmentsCurrentPage * itemsPerPage;
  const indexOfFirstAppointment = indexOfLastAppointment - itemsPerPage;
  const currentAppointments = appointments.slice(indexOfFirstAppointment, indexOfLastAppointment);
  const totalAppointmentPages = Math.ceil(appointments.length / itemsPerPage);

  // Komponenta za modal za unos/uređivanje podataka (Doktor/Pacijent)
  const DataModal = ({ show, onClose, data, onSave, fields, title, idPrefix }) => {
    // Interno stanje forme, inicijalizirano sa proslijeđenim podacima ili praznim objektom
    const [formData, setFormData] = useState(data || {});

    // Ažuriraj formu kada se promijene podaci (npr. kada se otvori modal za uređivanje drugog doktora)
    useEffect(() => {
      if (data) {
        const updatedData = { ...data };
        // Normalizacija podataka za prikaz u formi: 'N/A' pretvaramo u null/prazno za input polja
        if (updatedData.radnoVrijeme === 'N/A') {
          updatedData.radnoVrijeme = ''; // Prazan string za input
        }
        // Ako je iskustvo/ocjena "N/A" ili null/undefined, postavi na prazan string za input
        if (updatedData.iskustvo !== undefined && updatedData.iskustvo !== null && typeof updatedData.iskustvo === 'string' && updatedData.iskustvo.toLowerCase() === 'n/a') {
             updatedData.iskustvo = '';
        } else if (updatedData.iskustvo === 0 && (data.iskustvo === null || data.iskustvo === undefined)) {
            updatedData.iskustvo = ''; // Prikaz 0 kao prazno ako je originalno N/A ili null
        }

        if (updatedData.ocjena !== undefined && updatedData.ocjena !== null && typeof updatedData.ocjena === 'string' && updatedData.ocjena.toLowerCase() === 'n/a') {
            updatedData.ocjena = '';
        } else if (updatedData.ocjena === 0.0 && (data.ocjena === null || data.ocjena === undefined)) {
            updatedData.ocjena = '';
        }

        if (updatedData.email === 'N/A' || updatedData.email === null) {
            updatedData.email = '';
        }
        // Osiguraj da su specijalizacije uvijek niz, čak i ako je prazan
        if (!Array.isArray(updatedData.specijalizacije) || updatedData.specijalizacije.length === 0) {
          updatedData.specijalizacije = [];
        }

        setFormData(updatedData);
      } else {
        // Inicijalizacija forme za novi unos (svi podaci prazni ili defaultni)
        const initialData = {};
        fields.forEach(field => {
          if (field.name === 'specijalizacije') {
            initialData[field.name] = [];
          } else if (field.type === 'number') {
            initialData[field.name] = null; // Koristi null za brojeve ako nema vrijednosti
          } else {
            initialData[field.name] = '';
          }
        });
        setFormData(initialData);
      }
    }, [data, fields]);

    // Rukovanje promjenama u input poljima forme
    const handleChange = (e) => {
      const { name, value, type } = e.target;

      setFormData(prev => {
        let newValue = value;

        // Posebna obrada za 'specijalizacije' (kao select sa jednim izborom)
        if (name === 'specijalizacije') {
          newValue = [value]; // Spremi kao niz stringova
        } else if (type === 'number') {
          // Konvertuj u broj, ako je prazan string ili nevalidan broj, postavi na null
          newValue = value === '' ? null : Number(value);
          if (isNaN(newValue)) {
              newValue = null;
          }
        } else if (type === 'email' && value === '') {
            newValue = value; // Omogući prazan email ako je dozvoljeno
        }

        return { ...prev, [name]: newValue };
      });
    };

    // Rukovanje slanjem forme
    const handleSubmit = (e) => {
      e.preventDefault();
      const finalFormData = { ...formData };
      // Još jedna provjera da su specijalizacije pravilno formatirane kao niz
      if (typeof finalFormData.specijalizacije === 'string') {
          finalFormData.specijalizacije = finalFormData.specijalizacije ? [finalFormData.specijalizacije] : [];
      }
      onSave(finalFormData); // Pozovi funkciju za spašavanje
    };

    return (
      <Modal show={show} onHide={onClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {fields.map(field => (
              <Form.Group className="mb-3" controlId={`${idPrefix}-${field.name}`} key={field.name}>
                <Form.Label>{field.label}</Form.Label>
                {/* Uslovno renderovanje tipa inputa na osnovu field.type */}
                {field.type === 'select' ? (
                  <Form.Select
                    name={field.name}
                    value={Array.isArray(formData[field.name]) && formData[field.name].length > 0
                             ? formData[field.name][0] // Uzmi prvi element niza za prikaz u selectu
                             : formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  >
                    <option value="">Select {field.label}</option>
                    {field.options.map(option => (
                      <option key={option} value={option}>{option}</option>
                    ))}
                  </Form.Select>
                ) : field.type === 'textarea' ? (
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  />
                ) : (
                  <Form.Control
                    type={field.type || 'text'}
                    name={field.name}
                    // Logika za prikaz 'null', '0' ili 'N/A' brojeva/emaila kao prazan string u inputu
                    value={
                      (formData[field.name] === null || formData[field.name] === 'N/A') ||
                      (field.type === 'number' && (formData[field.name] === 0 || formData[field.name] === 'N/A') && (data === null || data[field.name] === null || data[field.name] === undefined))
                      ? '' // Prikazuje prazan string za unos
                      : formData[field.name]
                    }
                    onChange={handleChange}
                    required={field.required}
                  />
                )}
              </Form.Group>
            ))}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={onClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Save changes
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    );
  };

  // Komponenta za paginaciju
  const Pagination = ({ itemsPerPage, totalItems, currentPage, paginate }) => {
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
      pageNumbers.push(i);
    }

    return (
      <nav>
        <ul className="pagination justify-content-center mt-4">
          {pageNumbers.map(number => (
            <li key={number} className={`page-item ${currentPage === number ? 'active' : ''}`}>
              <button onClick={() => paginate(number)} className="page-link">
                {number}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    );
  };

