import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Za navigaciju na druge stranice
import 'bootstrap/dist/css/bootstrap.min.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginMessage, setLoginMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ovdje biste obično poslali email i lozinku na backend za autentifikaciju.
    // Za sada, samo simuliramo prijavu.
    if (email === 'test@example.com' && password === 'password123') {
      setLoginMessage('Uspješna prijava! Preusmjeravam...');
      // U stvarnoj aplikaciji, ovdje biste preusmjerili korisnika na dashboard
      // npr. navigate('/dashboard');
    } else {
      setLoginMessage('Neispravan email ili lozinka.');
    }
    console.log('Login attempt:', { email, password });
  };

  return (
    <div className="container d-flex justify-content-center align-items-center" style={{ minHeight: '80vh' }}>
      <div className="card shadow-lg p-4" style={{ maxWidth: '400px', width: '100%' }}>
        <div className="card-body">
          <h2 className="card-title text-center mb-4 text-danger">Login to WinxCare</h2>
          {loginMessage && (
            <div className={`alert ${loginMessage.includes('Uspješna') ? 'alert-success' : 'alert-danger'} text-center`} role="alert">
              {loginMessage}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="emailInput" className="form-label">Email address</label>
              <input
                type="email"
                className="form-control"
                id="emailInput"
                placeholder="name@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="passwordInput" className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                id="passwordInput"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="d-grid gap-2 mb-3">
              <button type="submit" className="btn btn-danger btn-lg">Login</button>
            </div>
          </form>
          <div className="text-center mt-3">
            <Link to="/forgot-password" className="text-muted text-decoration-none">Forgot Password?</Link>
          </div>
          <div className="text-center mt-2">
            <p className="text-muted">Don't have an account? <Link to="/sign-up" className="text-danger text-decoration-none">Sign Up</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
