import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Login from './components/Login';
import AdminDashboard from './components/AdminDashboard';
import DoctorDashboard from './components/DoctorDashboard'; // Pretpostavljam da imaš ove komponente
import PatientDashboard from './components/PatientDashboard'; // Pretpostavljam da imaš ove komponente

// Komponenta koja štiti rute
const PrivateRoute = ({ children, allowedRoles }) => {
  const { token, user, loading } = useAuth(); // NOVO: Dohvati loading state

  if (loading) {
    // Prikazati loading spinner ili prazan ekran dok se autentifikacija ne provjeri
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '24px' }}>
        Loading...
      </div>
    );
  }

  if (!token || !user) { // Ako nema tokena ili korisnika (nakon što je loading false)
    return <Navigate to="/login" />;
  }

  // Provjera uloga
  if (allowedRoles && user.roles && !allowedRoles.some(role => user.roles.includes(role))) {
    // Preusmjeri na neautorizovanu stranicu ili login ako uloga ne odgovara
    return <Navigate to="/login" />; // Preusmjeri na login za neautorizovane
  }

  return children;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          <Route path="/login" element={<Login />} />

          <Route path="/admin" element={
            <PrivateRoute allowedRoles={["ROLE_ADMIN"]}>
              <AdminDashboard />
            </PrivateRoute>
          } />

          <Route path="/doctor" element={
            <PrivateRoute allowedRoles={["ROLE_DOKTOR"]}>
              <DoctorDashboard />
            </PrivateRoute>
          } />

          <Route path="/patient" element={
            <PrivateRoute allowedRoles={["ROLE_PACIJENT"]}>
              <PatientDashboard />
            </PrivateRoute>
          } />

          {/* Default ruta - preusmjeri na login ako nije definisana nijedna druga ruta */}
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
