import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Form, Alert } from 'react-bootstrap';

const GATEWAY_BASE_URL = 'http://localhost:8081';

// Ažurirani API Prefiksi:
// Sada svi pozivaju /api/resurs jer je Gateway postavljen da strippa samo /api/
const KORISNICI_DOKTORI_API_PREFIX = `${GATEWAY_BASE_URL}/api`; // Sada ćeš pozivati /api/doktori, /api/pacijenti
const TERMINI_PREGLEDI_API_PREFIX = `${GATEWAY_BASE_URL}/api`; // Sada ćeš pozivati /api/termini, /api/pregledi
const AUTH_API_PREFIX = `${GATEWAY_BASE_URL}/auth`; // Ovo ostaje isto jer Gateway ruta za Auth strippa /auth/

function AdminDashboard() {
  const { token } = useAuth(); // Dohvat tokena iz AuthContext-a

  // Stanje za praćenje početnog učitavanja panela
  const [loadingInitial, setLoadingInitial] = useState(true);
  // Stanje za praćenje je li AuthContext završio početnu provjeru autentifikacije
  const [isAuthChecked, setIsAuthChecked] = useState(false);

  const authHeaders = {
    headers: {
      'Authorization': `Bearer ${token}`
    }
  };

  // Aktivni tab, sa čuvanjem u lokalnom skladištu
  const [activeTab, setActiveTab] = useState(() => {
    const savedTab = localStorage.getItem('adminActiveTab');
    return savedTab || 'overview';
  });

  const [doktori, setDoktori] = useState([]);
  const [pacijenti, setPacijenti] = useState([]);
  const [termini, setTermini] = useState([]);
  const [pregledi, setPregledi] = useState([]);

  // Modali za dodavanje/uređivanje
  const [showDoctorModal, setShowDoctorModal] = useState(false);
  const [editingDoctor, setEditingDoctor] = useState(null);

  const [showPatientModal, setShowPatientModal] = useState(false);
  const [editingPatient, setEditingPatient] = useState(null);

  const [showAppointmentModal, setShowAppointmentModal] = useState(false);
  const [editingAppointment, setEditingAppointment] = useState(null);

  // Poruke o uspjehu/grešci
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'success' or 'danger'

  useEffect(() => {
    // Spremamo aktivni tab u lokalno skladište
    localStorage.setItem('adminActiveTab', activeTab);
    if (token) {
      setIsAuthChecked(true); // Token je dostupan, AuthContext je provjeren
      fetchData(); // Dohvati podatke kada je token dostupan
    } else {
      setLoadingInitial(false); // Ako token nije dostupan odmah, završi inicijalno učitavanje
    }
  }, [activeTab, token]); // Dodan token kao depedencija

  const fetchData = async () => {
    setLoadingInitial(true);
    try {
      // Dohvat doktora
      // AŽURIRANA PUTANJA
      const doktoriResponse = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/doktori`, authHeaders);
      setDoktori(doktoriResponse.data);

      // Dohvat pacijenata
      // AŽURIRANA PUTANJA
      const pacijentiResponse = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/pacijenti`, authHeaders);
      setPacijenti(pacijentiResponse.data);

      // Dohvat termina
      // AŽURIRANA PUTANJA
      const terminiResponse = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/termini`, authHeaders);
      setTermini(terminiResponse.data.content); // Pretpostavka da vraća Page objekt

      // Dohvat pregleda
      // AŽURIRANA PUTANJA
      const preglediResponse = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/pregledi`, authHeaders);
      setPregledi(preglediResponse.data);

    } catch (error) {
      console.error('Greška pri dohvatu podataka:', error);
      setMessage('Greška pri dohvatu podataka. Molimo pokušajte ponovo.');
      setMessageType('danger');
    } finally {
      setLoadingInitial(false);
    }
  };

  const showSuccessMessage = (msg) => {
    setMessage(msg);
    setMessageType('success');
    setTimeout(() => setMessage(''), 3000);
  };

  const showErrorMessage = (msg) => {
    setMessage(msg);
    setMessageType('danger');
    setTimeout(() => setMessage(''), 5000);
  };

  // Definicije polja za modalne forme (doktori, pacijenti, termini, pregledi)
  const addDoctorFields = [
    { name: 'ime', label: 'Ime', type: 'text', required: true },
    { name: 'prezime', label: 'Prezime', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'telefon', label: 'Telefon', type: 'text', required: true },
    { name: 'grad', label: 'Grad', type: 'text', required: true },
    { name: 'radnoVrijeme', label: 'Radno Vrijeme', type: 'text', required: true },
    { name: 'iskustvo', label: 'Iskustvo (godine)', type: 'number', required: true },
    { name: 'ocjena', label: 'Ocjena (1-5)', type: 'number', step: '0.1', min: '1', max: '5', required: false },
    { name: 'specijalizacije', label: 'Specijalizacije (odvojene zarezom)', type: 'text', required: true },
    { name: 'lozinka', label: 'Lozinka', type: 'password', required: true },
  ];

  const editDoctorFields = [
    { name: 'ime', label: 'Ime', type: 'text', required: true },
    { name: 'prezime', label: 'Prezime', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'telefon', label: 'Telefon', type: 'text', required: true },
    { name: 'grad', label: 'Grad', type: 'text', required: true },
    { name: 'radnoVrijeme', label: 'Radno Vrijeme', type: 'text', required: true },
    { name: 'iskustvo', label: 'Iskustvo (godine)', type: 'number', required: true },
    { name: 'ocjena', label: 'Ocjena (1-5)', type: 'number', step: '0.1', min: '1', max: '5', required: false },
    { name: 'specijalizacije', label: 'Specijalizacije (odvojene zarezom)', type: 'text', required: true },
  ];

  const addPatientFields = [
    { name: 'ime', label: 'Ime', type: 'text', required: true },
    { name: 'prezime', label: 'Prezime', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'telefon', label: 'Telefon', type: 'text', required: true },
    { name: 'lozinka', label: 'Lozinka', type: 'password', required: true },
  ];

  const editPatientFields = [
    { name: 'ime', label: 'Ime', type: 'text', required: true },
    { name: 'prezime', label: 'Prezime', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'telefon', label: 'Telefon', type: 'text', required: true },
  ];

  const addTerminFields = [
    { name: 'doktorID', label: 'Doktor ID', type: 'number', required: true },
    { name: 'datum', label: 'Datum', type: 'date', required: true },
    { name: 'vrijeme', label: 'Vrijeme', type: 'time', required: true },
    { name: 'dostupnost', label: 'Dostupnost', type: 'checkbox', required: false },
  ];

  const editTerminFields = [
    { name: 'doktorID', label: 'Doktor ID', type: 'number', required: true, readOnly: true }, // Doktor ID se obično ne mijenja
    { name: 'datum', label: 'Datum', type: 'date', required: true },
    { name: 'vrijeme', label: 'Vrijeme', type: 'time', required: true },
    { name: 'dostupnost', label: 'Dostupnost', type: 'checkbox', required: false },
  ];

  const addPregledFields = [
    { name: 'pacijentID', label: 'Pacijent ID', type: 'number', required: true },
    { name: 'doktorID', label: 'Doktor ID', type: 'number', required: true },
    { name: 'terminID', label: 'Termin ID', type: 'number', required: true },
    { name: 'datumPregleda', label: 'Datum Pregleda', type: 'date', required: true },
    { name: 'vrijemePregleda', label: 'Vrijeme Pregleda', type: 'time', required: true },
    {
      name: 'status', label: 'Status', type: 'select', options: [
        { value: 'zakazan', label: 'Zakazan' },
        { value: 'obavljen', label: 'Obavljen' },
        { value: 'otkazan', label: 'Otkazan' }
      ], required: true
    },
    { name: 'ocjenaDoktora', label: 'Ocjena Doktora (1-5)', type: 'number', step: '0.1', min: '1', max: '5' },
    { name: 'komentarPacijenta', label: 'Komentar Pacijenta', type: 'textarea' },
  ];

  const editPregledFields = [
    { name: 'pacijentID', label: 'Pacijent ID', type: 'number', required: true, readOnly: true },
    { name: 'doktorID', label: 'Doktor ID', type: 'number', required: true, readOnly: true },
    { name: 'terminID', label: 'Termin ID', type: 'number', required: true, readOnly: true },
    { name: 'datumPregleda', label: 'Datum Pregleda', type: 'date', required: true },
    { name: 'vrijemePregleda', label: 'Vrijeme Pregleda', type: 'time', required: true },
    {
      name: 'status', label: 'Status', type: 'select', options: [
        { value: 'zakazan', label: 'Zakazan' },
        { value: 'obavljen', label: 'Obavljen' },
        { value: 'otkazan', label: 'Otkazan' }
      ], required: true
    },
    { name: 'ocjenaDoktora', label: 'Ocjena Doktora (1-5)', type: 'number', step: '0.1', min: '1', max: '5' },
    { name: 'komentarPacijenta', label: 'Komentar Pacijenta', type: 'textarea' },
  ];


  // Helper funkcija za slanje podataka, uključujući registraciju za doktore/pacijente
  const sendData = async (url, data, isNew, id = null, isAuthFlow = false) => {
    try {
      let response;
      if (isNew) {
        response = await axios.post(url, data, authHeaders);
      } else {
        response = await axios.put(`${url}/${id}`, data, authHeaders);
      }
      showSuccessMessage(`${isNew ? 'Dodano' : 'Ažurirano'} uspješno!`);
      fetchData(); // Osvježi podatke
      return true;
    } catch (error) {
      console.error(`Greška pri ${isNew ? 'dodavanju' : 'ažuriranju'} podataka:`, error.response?.data || error.message);
      showErrorMessage(`Greška pri ${isNew ? 'dodavanju' : 'ažuriranju'}: ${error.response?.data?.message || error.message}`);
      return false;
    }
  };

  const handleDelete = async (url, id) => {
    if (window.confirm('Jeste li sigurni da želite obrisati ovu stavku?')) {
      try {
        await axios.delete(`${url}/${id}`, authHeaders);
        showSuccessMessage('Obrisano uspješno!');
        fetchData();
      } catch (error) {
        console.error('Greška pri brisanju:', error.response?.data || error.message);
        showErrorMessage(`Greška pri brisanju: ${error.response?.data?.message || error.message}`);
      }
    }
  };


  // *** Doktor CRUD Operacije ***
  const handleAddDoctor = async (formData) => {
    const { lozinka, ...doctorData } = formData;
    doctorData.specijalizacije = formData.specijalizacije ? formData.specijalizacije.split(',').map(s => s.trim()) : [];

    // Prvo registruj korisnika
    try {
      const registerResponse = await axios.post(`${AUTH_API_PREFIX}/register`, {
        username: formData.email, // Koristimo email kao username
        password: lozinka,
        role: 'doctor'
      });
      const userId = registerResponse.data.id; // Pretpostavka da Auth servis vraća ID korisnika

      // Zatim dodaj doktora sa dobivenim userId
      const success = await sendData(
        `${KORISNICI_DOKTORI_API_PREFIX}/doktori`, // AŽURIRANA PUTANJA
        { ...doctorData, userId },
        true
      );
      if (success) setShowDoctorModal(false);
    } catch (error) {
      console.error("Greška pri registraciji doktora:", error.response?.data || error.message);
      showErrorMessage(`Greška pri registraciji doktora: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleUpdateDoctor = async (formData) => {
    formData.specijalizacije = formData.specijalizacije ? formData.specijalizacije.split(',').map(s => s.trim()) : [];
    const success = await sendData(
      `${KORISNICI_DOKTORI_API_PREFIX}/doktori`, // AŽURIRANA PUTANJA
      formData,
      false,
      formData.doktorID
    );
    if (success) setShowDoctorModal(false);
  };

  const handleDeleteDoctor = (id) => handleDelete(`${KORISNICI_DOKTORI_API_PREFIX}/doktori`, id); // AŽURIRANA PUTANJA

  // *** Pacijent CRUD Operacije ***
  const handleAddPatient = async (formData) => {
    const { lozinka, ...patientData } = formData;

    // Prvo registruj korisnika
    try {
      const registerResponse = await axios.post(`${AUTH_API_PREFIX}/register`, {
        username: formData.email, // Koristimo email kao username
        password: lozinka,
        role: 'patient'
      });
      const userId = registerResponse.data.id;

      // Zatim dodaj pacijenta sa dobivenim userId
      const success = await sendData(
        `${KORISNICI_DOKTORI_API_PREFIX}/pacijenti`, // AŽURIRANA PUTANJA
        { ...patientData, userId },
        true
      );
      if (success) setShowPatientModal(false);
    } catch (error) {
      console.error("Greška pri registraciji pacijenta:", error.response?.data || error.message);
      showErrorMessage(`Greška pri registraciji pacijenta: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleUpdatePatient = async (formData) => {
    const success = await sendData(
      `${KORISNICI_DOKTORI_API_PREFIX}/pacijenti`, // AŽURIRANA PUTANJA
      formData,
      false,
      formData.pacijentID
    );
    if (success) setShowPatientModal(false);
  };

  const handleDeletePatient = (id) => handleDelete(`${KORISNICI_DOKTORI_API_PREFIX}/pacijenti`, id); // AŽURIRANA PUTANJA

  // *** Termin CRUD Operacije ***
  const handleAddTermin = async (formData) => {
    const success = await sendData(
      `${TERMINI_PREGLEDI_API_PREFIX}/termini`, // AŽURIRANA PUTANJA
      formData,
      true
    );
    if (success) setShowAppointmentModal(false);
  };

  const handleUpdateTermin = async (formData) => {
    const success = await sendData(
      `${TERMINI_PREGLEDI_API_PREFIX}/termini`, // AŽURIRANA PUTANJA
      formData,
      false,
      formData.terminID
    );
    if (success) setShowAppointmentModal(false);
  };

  const handleDeleteTermin = (id) => handleDelete(`${TERMINI_PREGLEDI_API_PREFIX}/termini`, id); // AŽURIRANA PUTANJA

  // *** Pregled CRUD Operacije ***
  const handleAddPregled = async (formData) => {
    const success = await sendData(
      `${TERMINI_PREGLEDI_API_PREFIX}/pregledi`, // AŽURIRANA PUTANJA
      formData,
      true
    );
    if (success) setShowAppointmentModal(false);
  };

  const handleUpdatePregled = async (formData) => {
    const success = await sendData(
      `${TERMINI_PREGLEDI_API_PREFIX}/pregledi`, // AŽURIRANA PUTANJA
      formData,
      false,
      formData.pregledID
    );
    if (success) setShowAppointmentModal(false);
  };

  const handleDeletePregled = (id) => handleDelete(`${TERMINI_PREGLEDI_API_PREFIX}/pregledi`, id); // AŽURIRANA PUTANJA


  const DataModal = ({ show, onClose, data, onSave, title, idPrefix, fields }) => {
    const [formData, setFormData] = useState(data || {});
    const [validationErrors, setValidationErrors] = useState({});

    useEffect(() => {
      // Reset forme kada se modal otvori sa novim podacima
      setFormData(data || {});
      setValidationErrors({});
    }, [data, show]);

    const handleChange = (e) => {
      const { name, value, type, checked } = e.target;
      setFormData(prev => ({
        ...prev,
        [name]: type === 'checkbox' ? checked : value
      }));
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      const errors = {};
      fields.forEach(field => {
        if (field.required && (!formData[field.name] || (typeof formData[field.name] === 'string' && formData[field.name].trim() === ''))) {
          errors[field.name] = `${field.label} je obavezno.`;
        }
        if (field.type === 'email' && formData[field.name] && !/\S+@\S+\.\S+/.test(formData[field.name])) {
          errors[field.name] = 'Unesite ispravnu email adresu.';
        }
        if (field.type === 'number') {
          if (field.min && parseFloat(formData[field.name]) < parseFloat(field.min)) {
            errors[field.name] = `${field.label} mora biti najmanje ${field.min}.`;
          }
          if (field.max && parseFloat(formData[field.name]) > parseFloat(field.max)) {
            errors[field.name] = `${field.label} ne može biti veće od ${field.max}.`;
          }
        }
      });

      if (Object.keys(errors).length > 0) {
        setValidationErrors(errors);
      } else {
        onSave(formData);
      }
    };

    return (
      <Modal show={show} onHide={onClose}>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            {fields.map(field => (
              <Form.Group className="mb-3" controlId={`${idPrefix}-${field.name}`} key={field.name}>
                <Form.Label>{field.label}</Form.Label>
                {field.type === 'select' ? (
                  <Form.Control
                    as="select"
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                    readOnly={field.readOnly}
                  >
                    <option value="">Odaberite...</option>
                    {field.options.map(option => (
                      <option key={option.value} value={option.value}>{option.label}</option>
                    ))}
                  </Form.Control>
                ) : field.type === 'textarea' ? (
                  <Form.Control
                    as="textarea"
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                    readOnly={field.readOnly}
                  />
                ) : field.type === 'checkbox' ? (
                  <Form.Check
                    type="checkbox"
                    name={field.name}
                    label={field.label}
                    checked={!!formData[field.name]} // Osiguraj da je boolean
                    onChange={handleChange}
                    readOnly={field.readOnly}
                  />
                ) : (
                  <Form.Control
                    type={field.type}
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                    readOnly={field.readOnly}
                    step={field.step}
                    min={field.min}
                    max={field.max}
                  />
                )}
                {validationErrors[field.name] && <Alert variant="danger" className="mt-2">{validationErrors[field.name]}</Alert>}
              </Form.Group>
            ))}
            <Button variant="primary" type="submit">
              Sačuvaj
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    );
  };


  const renderContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div>
            <h1 className="h2 mb-4">Pregled</h1>
            {message && <Alert variant={messageType}>{message}</Alert>}
            <div className="row g-4">
              <div className="col-md-4">
                <div className="card text-center p-3 shadow-sm rounded-lg">
                  <h3 className="card-title">Ukupno Doktora</h3>
                  <p className="display-4">{doktori.length}</p>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card text-center p-3 shadow-sm rounded-lg">
                  <h3 className="card-title">Ukupno Pacijenata</h3>
                  <p className="display-4">{pacijenti.length}</p>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card text-center p-3 shadow-sm rounded-lg">
                  <h3 className="card-title">Ukupno Pregleda</h3>
                  <p className="display-4">{pregledi.length}</p>
                </div>
              </div>
              <div className="col-md-6">
                <div className="card p-3 shadow-sm rounded-lg">
                  <h3 className="card-title">Nadolazeći Termini</h3>
                  <ul className="list-group list-group-flush">
                    {termini.filter(t => t.dostupnost).slice(0, 5).map(termin => (
                      <li key={termin.terminID} className="list-group-item d-flex justify-content-between align-items-center">
                        <div>
                          <strong>Datum:</strong> {termin.datum} <br />
                          <strong>Vrijeme:</strong> {termin.vrijeme} <br />
                          <strong>Doktor ID:</strong> {termin.doktorID}
                        </div>
                      </li>
                    ))}
                    {termini.filter(t => t.dostupnost).length === 0 && (
                      <li className="list-group-item text-center">Nema slobodnih termina.</li>
                    )}
                  </ul>
                </div>
              </div>
              <div className="col-md-6">
                <div className="card p-3 shadow-sm rounded-lg">
                  <h3 className="card-title">Posljednji Zakazani Pregledi</h3>
                  <ul className="list-group list-group-flush">
                    {pregledi.filter(p => p.status === 'zakazan').slice(0, 5).map(pregled => (
                      <li key={pregled.pregledID} className="list-group-item d-flex justify-content-between align-items-center">
                        <div>
                          <strong>Datum:</strong> {pregled.datumPregleda} <br />
                          <strong>Pacijent ID:</strong> {pregled.pacijentID} <br />
                          <strong>Doktor ID:</strong> {pregled.doktorID}
                        </div>
                      </li>
                    ))}
                    {pregledi.filter(p => p.status === 'zakazan').length === 0 && (
                      <li className="list-group-item text-center">Nema zakazanih pregleda.</li>
                    )}
                  </ul>
                </div>
              </div>
            </div>
          </div>
        );
      case 'doctors':
        return (
          <div>
            <h1 className="h2 mb-4">Doktori</h1>
            {message && <Alert variant={messageType}>{message}</Alert>}
            <div className="d-flex justify-content-end mb-3">
              <Button variant="success" onClick={() => { setEditingDoctor(null); setShowDoctorModal(true); }}>Dodaj Novog Doktora</Button>
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover rounded-lg overflow-hidden shadow-sm">
                <thead className="bg-primary text-white">
                  <tr>
                    <th>ID</th>
                    <th>Ime</th>
                    <th>Prezime</th>
                    <th>Email</th>
                    <th>Telefon</th>
                    <th>Grad</th>
                    <th>Radno Vrijeme</th>
                    <th>Iskustvo</th>
                    <th>Ocjena</th>
                    <th>Specijalizacije</th>
                    <th>Akcije</th>
                  </tr>
                </thead>
                <tbody>
                  {doktori.map(doktor => (
                    <tr key={doktor.doktorID}>
                      <td>{doktor.doktorID}</td>
                      <td>{doktor.ime}</td>
                      <td>{doktor.prezime}</td>
                      <td>{doktor.email}</td>
                      <td>{doktor.telefon}</td>
                      <td>{doktor.grad}</td>
                      <td>{doktor.radnoVrijeme}</td>
                      <td>{doktor.iskustvo}</td>
                      <td>{doktor.ocjena?.toFixed(1) || 'N/A'}</td>
                      <td>{doktor.specijalizacije?.join(', ') || 'N/A'}</td>
                      <td>
                        <Button variant="warning" size="sm" className="me-2" onClick={() => { setEditingDoctor(doktor); setShowDoctorModal(true); }}>Uredi</Button>
                        <Button variant="danger" size="sm" onClick={() => handleDeleteDoctor(doktor.doktorID)}>Obriši</Button>
                      </td>
                    </tr>
                  ))}
                  {doktori.length === 0 && (
                    <tr>
                      <td colSpan="11" className="text-center">Nema unesenih doktora.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        );
      case 'patients':
        return (
          <div>
            <h1 className="h2 mb-4">Pacijenti</h1>
            {message && <Alert variant={messageType}>{message}</Alert>}
            <div className="d-flex justify-content-end mb-3">
              <Button variant="success" onClick={() => { setEditingPatient(null); setShowPatientModal(true); }}>Dodaj Novog Pacijenta</Button>
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover rounded-lg overflow-hidden shadow-sm">
                <thead className="bg-primary text-white">
                  <tr>
                    <th>ID</th>
                    <th>Ime</th>
                    <th>Prezime</th>
                    <th>Email</th>
                    <th>Telefon</th>
                    <th>Akcije</th>
                  </tr>
                </thead>
                <tbody>
                  {pacijenti.map(pacijent => (
                    <tr key={pacijent.pacijentID}>
                      <td>{pacijent.pacijentID}</td>
                      <td>{pacijent.ime}</td>
                      <td>{pacijent.prezime}</td>
                      <td>{pacijent.email}</td>
                      <td>{pacijent.telefon}</td>
                      <td>
                        <Button variant="warning" size="sm" className="me-2" onClick={() => { setEditingPatient(pacijent); setShowPatientModal(true); }}>Uredi</Button>
                        <Button variant="danger" size="sm" onClick={() => handleDeletePatient(pacijent.pacijentID)}>Obriši</Button>
                      </td>
                    </tr>
                  ))}
                  {pacijenti.length === 0 && (
                    <tr>
                      <td colSpan="6" className="text-center">Nema unesenih pacijenata.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        );
      case 'appointments':
        return (
          <div>
            <h1 className="h2 mb-4">Termini i Pregledi</h1>
            {message && <Alert variant={messageType}>{message}</Alert>}
            <div className="d-flex justify-content-end mb-3">
              <Button variant="success" className="me-2" onClick={() => { setEditingAppointment(null); setShowAppointmentModal(true); }}>Dodaj Novi Termin/Pregled</Button>
            </div>

            {/* Termini */}
            <h2 className="h4 mt-4 mb-3">Termini</h2>
            <div className="table-responsive mb-5">
              <table className="table table-striped table-hover rounded-lg overflow-hidden shadow-sm">
                <thead className="bg-info text-white">
                  <tr>
                    <th>ID</th>
                    <th>Doktor ID</th>
                    <th>Datum</th>
                    <th>Vrijeme</th>
                    <th>Dostupnost</th>
                    <th>Akcije</th>
                  </tr>
                </thead>
                <tbody>
                  {termini.map(termin => (
                    <tr key={termin.terminID}>
                      <td>{termin.terminID}</td>
                      <td>{termin.doktorID}</td>
                      <td>{termin.datum}</td>
                      <td>{termin.vrijeme}</td>
                      <td>{termin.dostupnost ? 'Dostupan' : 'Zauzet'}</td>
                      <td>
                        <Button variant="warning" size="sm" className="me-2" onClick={() => { setEditingAppointment({ ...termin, type: 'termin' }); setShowAppointmentModal(true); }}>Uredi</Button>
                        <Button variant="danger" size="sm" onClick={() => handleDeleteTermin(termin.terminID)}>Obriši</Button>
                      </td>
                    </tr>
                  ))}
                  {termini.length === 0 && (
                    <tr>
                      <td colSpan="6" className="text-center">Nema unesenih termina.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>

            {/* Pregledi */}
            <h2 className="h4 mt-4 mb-3">Pregledi</h2>
            <div className="d-flex justify-content-end mb-3">
              <Button variant="success" onClick={() => { setEditingAppointment(null); setShowAppointmentModal(true); }}>Dodaj Novi Pregled</Button>
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover rounded-lg overflow-hidden shadow-sm">
                <thead className="bg-success text-white">
                  <tr>
                    <th>ID</th>
                    <th>Pacijent ID</th>
                    <th>Doktor ID</th>
                    <th>Termin ID</th>
                    <th>Datum Pregleda</th>
                    <th>Vrijeme Pregleda</th>
                    <th>Status</th>
                    <th>Ocjena Doktora</th>
                    <th>Komentar Pacijenta</th>
                    <th>Akcije</th>
                  </tr>
                </thead>
                <tbody>
                  {pregledi.map(pregled => (
                    <tr key={pregled.pregledID}>
                      <td>{pregled.pregledID}</td>
                      <td>{pregled.pacijentID}</td>
                      <td>{pregled.doktorID}</td>
                      <td>{pregled.terminID}</td>
                      <td>{pregled.datumPregleda}</td>
                      <td>{pregled.vrijemePregleda}</td>
                      <td>{pregled.status}</td>
                      <td>{pregled.ocjenaDoktora?.toFixed(1) || 'N/A'}</td>
                      <td>{pregled.komentarPacijenta || 'N/A'}</td>
                      <td>
                        <Button variant="warning" size="sm" className="me-2" onClick={() => { setEditingAppointment({ ...pregled, type: 'pregled' }); setShowAppointmentModal(true); }}>Uredi</Button>
                        <Button variant="danger" size="sm" onClick={() => handleDeletePregled(pregled.pregledID)}>Obriši</Button>
                      </td>
                    </tr>
                  ))}
                  {pregledi.length === 0 && (
                    <tr>
                      <td colSpan="10" className="text-center">Nema unesenih pregleda.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        );
      default:
        return <div>Nepoznat tab.</div>;
    }
  };

  if (!isAuthChecked) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Učitavanje...</span>
        </div>
      </div>
    );
  }

  // Glavni layout panela
  return (
    <div className="container-fluid">
      {message && <Alert variant={messageType}>{message}</Alert>}
      <div className="row">
        {/* Navigacija */}
        <nav id="sidebarMenu" className="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse shadow-sm rounded-lg">
          <div className="position-sticky pt-3">
            <ul className="nav flex-column">
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'overview' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('overview')}>
                  Pregled
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'doctors' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('doctors')}>
                  Doktori
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'patients' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('patients')}>
                  Pacijenti
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'appointments' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('appointments')}>
                  Termini i Pregledi
                </a>
              </li>
            </ul>
          </div>
        </nav>

        {/* Glavni sadržaj panela */}
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4 py-4">
          {renderContent()}
        </main>
      </div>

      {/* Modal za dodavanje/uređivanje doktora */}
      <DataModal
        show={showDoctorModal}
        onClose={() => setShowDoctorModal(false)}
        data={editingDoctor}
        onSave={editingDoctor ? handleUpdateDoctor : handleAddDoctor}
        title={editingDoctor ? 'Uredi Doktora' : 'Dodaj Novog Doktora'}
        idPrefix="doctor"
        fields={editingDoctor ? editDoctorFields : addDoctorFields}
      />

      {/* Modal za dodavanje/uređivanje pacijenata */}
      <DataModal
        show={showPatientModal}
        onClose={() => setShowPatientModal(false)}
        data={editingPatient}
        onSave={editingPatient ? handleUpdatePatient : handleAddPatient}
        title={editingPatient ? 'Uredi Pacijenta' : 'Dodaj Novog Pacijenta'}
        idPrefix="patient"
        fields={editingPatient ? editPatientFields : addPatientFields}
      />

      {/* Modal za dodavanje/uređivanje termina/pregleda */}
      <DataModal
        show={showAppointmentModal}
        onClose={() => setShowAppointmentModal(false)}
        // Ovdje se koristi logika za određivanje forme na osnovu 'type' u editingAppointment
        data={editingAppointment}
        onSave={editingAppointment?.type === 'termin' ? handleUpdateTermin :
          editingAppointment?.type === 'pregled' ? handleUpdatePregled :
            // Ako je nov, ili termin ili pregled, ovisno o poljima
            (formData) => {
              if (formData.terminID && formData.doktorID && formData.datum && formData.vrijeme) {
                handleAddTermin(formData);
              } else if (formData.pacijentID && formData.doktorID && formData.terminID && formData.datumPregleda) {
                handleAddPregled(formData);
              } else {
                showErrorMessage("Nije moguće odrediti tip obrasca (termin ili pregled).");
              }
            }
        }
        title={editingAppointment?.type === 'termin' ? 'Uredi Termin' :
          editingAppointment?.type === 'pregled' ? 'Uredi Pregled' :
            'Dodaj Novi Termin/Pregled'}
        idPrefix={editingAppointment?.type === 'termin' ? 'termin' :
          editingAppointment?.type === 'pregled' ? 'pregled' :
            'new-appointment'}
        fields={editingAppointment?.type === 'termin' ? editTerminFields :
          editingAppointment?.type === 'pregled' ? editPregledFields :
            // Ako je nov, prikaži kombinovana polja za novi termin ili pregled
            [...addTerminFields, ...addPregledFields.filter(f => !['doktorID', 'datumPregleda', 'vrijemePregleda'].includes(f.name))]}
      />
    </div>
  );
}

export default AdminDashboard;
