        try {
          await axios.delete(`${TERMINI_PREGLEDI_API_PREFIX}/api/termini/${id}`, authHeaders);
          showNotification('Termin uspješno obrisan!', 'success');
          fetchTerms();
        } catch (error) {
          console.error("Greška prilikom brisanja termina:", error.response?.data || error.message);
          showNotification(error.response?.data?.message || 'Greška prilikom brisanja termina.', "danger");
        }
