      "Potvrdi brisanje termina"
    );
  };

  const openTermModal = (term = null) => {
    if (term) {
        setEditingTerm({ ...term, doktorID: term.doktorID, dostupnost: term.dostupnost });
    } else {
        setEditingTerm(null);
    }
    setShowTermModal(true);
  };

  const filteredTerms = terms.filter(term =>
    (term.doktorID != null && term.doktorID.toString().toLowerCase().includes(termSearchTerm.toLowerCase())) ||
    (term.datum && term.datum.toLowerCase().includes(termSearchTerm.toLowerCase())) ||
    (term.vrijeme && term.vrijeme.toLowerCase().includes(termSearchTerm.toLowerCase())) ||
    (term.dostupnost !== undefined && (term.dostupnost ? 'dostupan' : 'zauzet').includes(termSearchTerm.toLowerCase()))
  );
  const indexOfLastTerm = termsCurrentPage * itemsPerPage;
  const indexOfFirstTerm = indexOfLastTerm - itemsPerPage;
  const currentTerms = filteredTerms.slice(indexOfFirstTerm, indexOfLastTerm);
  const totalTermPages = Math.ceil(filteredTerms.length / itemsPerPage);


  const handleAddExamination = async (newExaminationData) => {
    try {
      console.log('handleAddExamination - Podaci prije slanja na backend:', newExaminationData);

      const examinationPayload = {
        pacijentID: newExaminationData.pacijentID,
        doktorID: newExaminationData.doktorID,
        terminID: newExaminationData.terminID,
        datumPregleda: newExaminationData.datumPregleda,
        vrijemePregleda: newExaminationData.vrijemePregleda,
        status: newExaminationData.status,
        komentarPacijenta: newExaminationData.komentarPacijenta,
        ocjenaDoktora: newExaminationData.ocjenaDoktora,
      };
      console.log('handleAddExamination - Sending Payload:', examinationPayload);

      const response = await axios.post(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi`, examinationPayload, authHeaders);
      console.log('handleAddExamination - Backend response:', response.data); // Log response data
      showNotification('Novi pregled uspješno dodan!', 'success');
      fetchExaminations();
      setShowExaminationModal(false);
    } catch (error) {
      console.error("Greška prilikom dodavanja pregleda:", error.response?.data || error.message);
      showNotification(error.response?.data?.message || 'Greška prilikom dodavanja pregleda.', "danger");
    }
  };

  const handleUpdateExamination = async (updatedExamination) => {
    try {
      console.log('handleUpdateExamination primio payload:', updatedExamination);

      const examinationPayload = {
        pacijentID: updatedExamination.pacijentID,
        doktorID: updatedExamination.doktorID,
        terminID: updatedExamination.terminID,
        datumPregleda: updatedExamination.datumPregleda,
        vrijemePregleda: updatedExamination.vrijemePregleda,
        status: updatedExamination.status,
        komentarPacijenta: updatedExamination.komentarPacijenta,
        ocjenaDoktora: updatedExamination.ocjenaDoktora,
      };
      console.log('handleUpdateExamination - Sending Payload:', examinationPayload);

      await axios.put(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/${updatedExamination.id}`, examinationPayload, authHeaders);
      console.log('handleUpdateExamination - Backend response:', response.data); // Log response data
      showNotification('Pregled uspješno ažuriran!', 'success');
      fetchExaminations();
      setShowExaminationModal(false);
      setEditingExamination(null);
    } catch (error) {
      console.error("Greška prilikom ažuriranja pregleda:", error.response?.data || error.message);
      showNotification(error.response?.data?.message || 'Greška prilikom ažuriranja pregleda.', "danger");
    }
  };

  const handleDeleteExamination = async (id) => {
    showCustomConfirm(
      'Jeste li sigurni da želite obrisati ovaj pregled?',
      async () => {
        try {
          await axios.delete(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/${id}`, authHeaders);
          showNotification('Pregled uspješno obrisan!', 'success');
          fetchExaminations();
        } catch (error) {
          console.error("Greška prilikom brisanja pregleda:", error.response?.data || error.message);
          showNotification(error.response?.data?.message || 'Greška prilikom brisanja pregleda.', "danger");
        }
      },
      "Potvrdi brisanje pregleda"
    );
  };

  const openExaminationModal = (examination = null) => {
    if (examination) {
        setEditingExamination({
            ...examination,
            pacijentID: examination.pacijentID,
            doktorID: examination.doktorID,
            terminID: examination.terminID,
            datumPregleda: examination.datumPregleda,
            vrijemePregleda: examination.vrijemePregleda
        });
    } else {
        setEditingExamination(null);
    }
    setShowExaminationModal(true);
  };

  const filteredExaminations = examinations.filter(exam =>
    (exam.patientName && exam.patientName.toLowerCase().includes(examinationSearchTerm.toLowerCase())) ||
    (exam.doctorName && exam.doctorName.toLowerCase().includes(examinationSearchTerm.toLowerCase())) ||
    (exam.status && exam.status.toLowerCase().includes(examinationSearchTerm.toLowerCase())) ||
    (exam.datumPregleda && exam.datumPregleda.toLowerCase().includes(examinationSearchTerm.toLowerCase())) ||
    (exam.vrijemePregleda && exam.vrijemePregleda.toLowerCase().includes(examinationSearchTerm.toLowerCase()))
  );
  const indexOfLastExamination = examinationsCurrentPage * itemsPerPage;
  const indexOfFirstExamination = indexOfLastExamination - itemsPerPage;
  const currentExaminations = filteredExaminations.slice(indexOfFirstExamination, indexOfLastExamination);
  const totalExaminationPages = Math.ceil(filteredExaminations.length / itemsPerPage);


  // Komponenta za modal za unos/uređivanje podataka (Doktor/Pacijent/Termin/Pregled)
  // DataModal definisana UNUTAR AdminDashboard funkcije (kao u tvojoj admin2.txt verziji)
  const DataModal = ({ show, onClose, data, onSave, fields, title, idPrefix, authHeaders, showNotification }) => {
    // useRef za praćenje prethodnog terminID-a
    const prevTerminIDRef = React.useRef(null);

    const [formData, setFormData] = useState(() => {
        const initialData = data ? { ...data } : {};
        fields.forEach(field => {
            if (data && data[field.name] !== undefined && data[field.name] !== null) {
                let value = data[field.name];
                if (field.type === 'date' && typeof value === 'string' && value.includes('T')) {
                    value = value.split('T')[0];
                } else if (field.type === 'time' && typeof value === 'string' && value.length > 8) {
                    value = value.substring(0, 8);
                } else if (field.name === 'specijalizacije' && Array.isArray(value)) {
                    value = value[0] || '';
                } else if (field.type === 'checkbox') {
                    value = Boolean(value);
                } else if (field.type === 'number') {
                    // Za brojeve, ako je 0, prikaži prazno, osim za ocjenu doktora (koja može biti 0.0)
                    // Neka ID-evi uvijek prikazuju 0 ako je 0.
                    if (field.name === 'ocjenaDoktora' || field.name === 'iskustvo') {
                        value = (value === 0 || value === 0.0) ? '' : value;
                    }
                }
                initialData[field.name] = value;
            } else {
                if (field.name === 'specijalizacije') {
                    initialData[field.name] = '';
                } else if (field.type === 'number') {
                    // Inicijaliziraj numerička polja na null za nove unose
                    initialData[field.name] = null;
                } else if (field.type === 'checkbox') {
                    initialData[field.name] = false;
                } else {
                    initialData[field.name] = '';
                }
            }
        });
        return initialData;
    });

    // Resetovanje forme kada se modal otvara/zatvara ili mijenjaju podaci
    useEffect(() => {
      const initialData = data ? { ...data } : {};
        fields.forEach(field => {
            if (data && data[field.name] !== undefined && data[field.name] !== null) {
                let value = data[field.name];
                if (field.type === 'date' && typeof value === 'string' && value.includes('T')) {
                    value = value.split('T')[0];
                } else if (field.type === 'time' && typeof value === 'string' && value.length > 8) {
                    value = value.substring(0, 8);
                } else if (field.name === 'specijalizacije' && Array.isArray(value)) {
                    value = value[0] || '';
                } else if (field.type === 'checkbox') {
                    value = Boolean(value);
                } else if (field.type === 'number') {
                    if (field.name === 'ocjenaDoktora' || field.name === 'iskustvo') {
                        value = (value === 0 || value === 0.0) ? '' : value;
                    }
                }
                initialData[field.name] = value;
            } else {
                if (field.name === 'specijalizacije') {
                    initialData[field.name] = '';
                } else if (field.type === 'number') {
                    // Inicijaliziraj na null i pri resetiranju modala
                    initialData[field.name] = null;
                } else if (field.type === 'checkbox') {
                    initialData[field.name] = false;
                } else {
                    initialData[field.name] = '';
                }
            }
        });
        setFormData(initialData);
        prevTerminIDRef.current = null; // Resetuj referencu za terminID
    }, [data, fields, idPrefix]);

    // useEffect za automatsko popunjavanje detalja termina
    useEffect(() => {
        const currentTerminID = formData.terminID;
        // Provjeri da li je terminID validan broj i da nije "N/A" string
        const isNumericAndValidTerminID = typeof currentTerminID === 'number' && currentTerminID > 0;

        console.log(`[DataModal useEffect] idPrefix: ${idPrefix}, currentTerminID: ${currentTerminID}, isNumericAndValidTerminID: ${isNumericAndValidTerminID}, prevTerminIDRef.current: ${prevTerminIDRef.current}`);

        if (
            idPrefix === 'examination' &&
            isNumericAndValidTerminID &&
            currentTerminID !== prevTerminIDRef.current
        ) {
            const fetchTermDetails = async () => {
                console.log(`[DataModal useEffect] Pokušavam dohvatiti detalje termina za ID: ${currentTerminID}`);
                try {
                    const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/termini/${currentTerminID}`, authHeaders);
                    const fetchedTerm = response.data;
                    console.log(`[DataModal useEffect] Detalji termina dohvaćeni:`, fetchedTerm);
                    setFormData(prev => ({
                        ...prev,
                        doktorID: fetchedTerm.doktorID,
                        datumPregleda: fetchedTerm.datum,
                        vrijemePregleda: fetchedTerm.vrijeme.substring(0, 8),
                    }));
                    prevTerminIDRef.current = currentTerminID;
                    showNotification('Detalji termina uspješno popunjeni!', 'info');
                } catch (error) {
                    console.error("Greška prilikom dohvaćanja detalja termina:", error.response?.data || error.message);
                    showNotification('Greška prilikom dohvaćanja detalja termina. Provjerite Term ID.', "danger");
                    setFormData(prev => ({
                        ...prev,
                        doktorID: null,
                        datumPregleda: '',
                        vrijemePregleda: '',
                    }));
                    prevTerminIDRef.current = null;
                }
            };
            fetchTermDetails();
        } else if (idPrefix === 'examination' && (currentTerminID === null || currentTerminID === 0 || currentTerminID === 'N/A' || currentTerminID === '')) {
            console.log(`[DataModal useEffect] Termin ID nije validan ili je prazan (${currentTerminID}), resetujem polja.`);
            setFormData(prev => ({
                ...prev,
                doktorID: null,
                datumPregleda: '',
                vrijemePregleda: '',
            }));
            prevTerminIDRef.current = null;
        }
    }, [formData.terminID, idPrefix, authHeaders, showNotification]);

    const handleChange = (e) => {
      const { name, value, type, checked } = e.target;
      console.log(`[handleChange] name: ${name}, value: "${value}", type: ${type}`);

      setFormData(prev => {
        let newValue = value;

        if (name === 'specijalizacije') {
            newValue = value;
        } else if (type === 'number') {
            // Konvertiraj prazan string u null za numeričke inpute, ili u broj
            newValue = value === '' ? null : Number(value);
            if (isNaN(newValue)) {
                newValue = null; // Osiguraj da NaN također postane null
            }
        } else if (type === 'checkbox') {
            newValue = checked;
        }
        console.log(`[handleChange] new formData[${name}]:`, newValue);
        return { ...prev, [name]: newValue };
      });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      const finalFormData = { ...formData };

      if (idPrefix === 'doctor' && typeof finalFormData.specijalizacije === 'string') {
          finalFormData.specijalizacije = finalFormData.specijalizacije ? [finalFormData.specijalizacije] : [];
      }

      // Prođi kroz sva polja i konvertiraj null u 0 za numerička polja koja su obavezna
      // ili gdje 0 ima specifično značenje (npr. ocjena, iskustvo).
      // Važno: Ovdje null pretvaramo u 0 samo ako je to prikladno za backend.
      // Za ID-eve je bolje poslati null ako nije unesen, pa neka backend validira obaveznost.
      fields.forEach(field => {
          if (field.type === 'number') {
              if (finalFormData[field.name] === null || finalFormData[field.name] === undefined) {
                  // Ako je polje numeričko i null/undefined, pretvori ga u 0.
                  // Ovo je sigurnosna mjera ako backend NE prihvata null za int/long.
                  // Za ID-eve, ako su obavezni, backend bi trebao javiti grešku.
                  finalFormData[field.name] = 0; // Postavljamo 0 za numerička polja koja su ostala null/undefined
              }
          }
      });

      if (idPrefix === 'term' && finalFormData.vrijeme) {
          if (finalFormData.vrijeme.length === 5) {
              finalFormData.vrijeme += ':00';
          }
      } else if (idPrefix === 'examination' && finalFormData.vrijemePregleda) {
          if (finalFormData.vrijemePregleda.length === 5) {
              finalFormData.vrijemePregleda += ':00';
          }
      }

      console.log('DataModal handleSubmit - Finalni payload za slanje:', finalFormData);

      onSave(finalFormData);
    };

    return (
      <Modal show={show} onHide={onClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {fields.map(field => (
              <Form.Group className="mb-3" controlId={`${idPrefix}-${field.name}`} key={field.name}>
                <Form.Label>{field.label}</Form.Label>
                {field.type === 'select' ? (
                  <Form.Select
                    name={field.name}
                    value={formData[field.name] === null ? '' : formData[field.name]} // Prikazati null kao prazno
                    onChange={handleChange}
                    required={field.required}
                  >
                    <option value="">Select {field.label}</option>
                    {field.options.map(option => (
                      <option key={option} value={option}>{option}</option>
                    ))}
                  </Form.Select>
                ) : field.type === 'textarea' ? (
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  />
                ) : field.type === 'checkbox' ? (
                    <Form.Check
                        type="checkbox"
                        name={field.name}
                        checked={formData[field.name] || false}
                        onChange={handleChange}
                        label={field.label}
                    />
                ) : (
                  <Form.Control
                    type={field.type || 'text'}
                    name={field.name}
                    // ISPRAVKA: Prikazati null brojeve kao prazno, ali stvarne nule kao '0'
                    value={
                        (formData[field.name] === null || formData[field.name] === undefined || formData[field.name] === '')
                        ? '' // Prikazati null, undefined, ili prazan string kao prazno polje
                        : (formData[field.name] === 'N/A' ? '' : formData[field.name]) // Održati N/A kao prazno
                    }
                    onChange={handleChange}
                    required={field.required}
                    // READONLY IZMJENE OVDJE:
                    readOnly={field.readOnly && idPrefix !== 'examination'} // field.readOnly ostaje true za doktore/pacijente
                  />
                )}
              </Form.Group>
            ))}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={onClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Save changes
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    );
  };

  const Pagination = ({ itemsPerPage, totalItems, currentPage, paginate }) => {
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
      pageNumbers.push(i);
    }

    return (
      <nav>
        <ul className="pagination justify-content-center mt-4">
          {pageNumbers.map(number => (
            <li key={number} className={`page-item ${currentPage === number ? 'active' : ''}`}>
              <button onClick={() => paginate(number)} className="page-link">
                {number}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    );
  };

  const renderContent = () => {
    const addDoctorFields = [
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'password', label: 'Password', type: 'password', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
      { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla', 'Bihać'] },
      { name: 'specijalizacije', label: 'Specialty', type: 'select', required: true, options: ['Kardiolog', 'Neurolog', 'Ortoped', 'Pedijatar', 'Hirurg', 'Dermatolog', 'Oftamolog', 'Psihijatar'] },
      { name: 'radnoVrijeme', label: 'Work Hours', type: 'text', required: false },
      { name: 'iskustvo', label: 'Experience (years)', type: 'number', required: false },
    ];

    const editDoctorFields = [
      { name: 'id', label: 'Doctor ID', type: 'text', readOnly: true },
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
      { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla', 'Bihać'] },
      { name: 'specijalizacije', label: 'Specialty', type: 'select', required: true, options: ['Kardiolog', 'Neurolog', 'Ortoped', 'Pedijatar', 'Hirurg', 'Dermatolog', 'Oftamolog', 'Psihijatar'] },
      { name: 'radnoVrijeme', label: 'Work Hours', type: 'text', required: false },
      { name: 'iskustvo', label: 'Experience (years)', type: 'number', required: false },
    ];

    const addPatientFields = [
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'password', label: 'Password', type: 'password', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
    ];

    const editPatientFields = [
      { name: 'id', label: 'Patient ID', type: 'text', readOnly: true },
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
    ];

    const addTermFields = [
      { name: 'doktorID', label: 'Doctor ID', type: 'number', required: true },
      { name: 'datum', label: 'Date (YYYY-MM-DD)', type: 'date', required: true },
      { name: 'vrijeme', label: 'Time (HH:MM:SS)', type: 'time', required: true },
      { name: 'dostupnost', label: 'Available', type: 'checkbox', required: false },
    ];

    const editTermFields = [
      { name: 'id', label: 'Term ID (Auto)', type: 'text', required: false, readOnly: true },
      { name: 'doktorID', label: 'Doctor ID', type: 'number', required: true },
      { name: 'datum', label: 'Date (YYYY-MM-DD)', type: 'date', required: true },
      { name: 'vrijeme', label: 'Time (HH:MM:SS)', type: 'time', required: true },
      { name: 'dostupnost', label: 'Available', type: 'checkbox', required: false },
    ];

    const addExaminationFields = [
      { name: 'pacijentID', label: 'Patient ID', type: 'number', required: true },
      // IZMJENA: uklonjen readOnly za ručni unos za testiranje
      { name: 'doktorID', label: 'Doctor ID', type: 'number', required: true },
      { name: 'terminID', label: 'Term ID', type: 'number', required: true },
      // IZMJENA: uklonjen readOnly za ručni unos za testiranje
      { name: 'datumPregleda', label: 'Date of Examination (YYYY-MM-DD)', type: 'date', required: true },
      // IZMJENA: uklonjen readOnly za ručni unos za testiranje
      { name: 'vrijemePregleda', label: 'Time of Examination (HH:MM:SS)', type: 'time', required: true },
      { name: 'status', label: 'Status', type: 'select', required: true, options: ['zakazan', 'obavljen', 'otkazan'] },
      { name: 'komentarPacijenta', label: 'Patient Comment', type: 'textarea', required: false },
      { name: 'ocjenaDoktora', label: 'Doctor Rating (0.0-5.0)', type: 'number', required: false },
    ];

    const editExaminationFields = [
      { name: 'id', label: 'Examination ID (Auto)', type: 'text', readOnly: true },
      { name: 'pacijentID', label: 'Patient ID', type: 'number', required: true },
      { name: 'doktorID', label: 'Doctor ID', type: 'number', required: true, readOnly: true },
      { name: 'terminID', label: 'Term ID', type: 'number', required: true },
      { name: 'datumPregleda', label: 'Date of Examination (YYYY-MM-DD)', type: 'date', required: true, readOnly: true },
      { name: 'vrijemePregleda', label: 'Time of Examination (HH:MM:SS)', type: 'time', required: true, readOnly: true },
      { name: 'status', label: 'Status', type: 'select', required: true, options: ['zakazan', 'obavljen', 'otkazan'] },
      { name: 'komentarPacijenta', label: 'Patient Comment', type: 'textarea', required: false },
      { name: 'ocjenaDoktora', label: 'Doctor Rating (0.0-5.0)', type: 'number', required: false },
    ];


    switch (activeTab) {
      case 'overview':
        return (
          <div>
            <h4 className="mb-4">Dashboard Overview</h4>
            <div className="row g-4">
              <div className="col-md-3">
                <div className="card text-white bg-primary mb-3">
                  <div className="card-header">Total Doctors</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{doctors.length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="card text-white bg-success mb-3">
                  <div className="card-header">Total Patients</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{patients.length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="card text-white bg-info mb-3">
                  <div className="card-header">Available Terms</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{terms.filter(t => t.dostupnost === true).length}</h5>
                  </div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="card text-white bg-warning mb-3">
                  <div className="card-header">Pending Examinations</div>
                  <div className="card-body">
                    <h5 className="card-title display-4">{examinations.filter(exam => exam.status === 'zakazan').length}</h5>
                  </div>
                </div>
              </div>
            </div>
            <div className="mt-5">
              <h5>Quick Actions</h5>
              <div className="d-flex gap-2 flex-wrap">
                <button className="btn btn-outline-primary" onClick={() => openDoctorModal()}>Add New Doctor</button>
                <button className="btn btn-outline-success" onClick={() => openPatientModal()}>Add New Patient</button>
                <button className="btn btn-outline-info" onClick={() => openTermModal()}>Add New Term</button>
                <button className="btn btn-outline-warning" onClick={() => openExaminationModal()}>Add New Examination</button>
              </div>
            </div>
          </div>
        );
      case 'doctors':
        return (
          <div>
            <h4 className="mb-4">Manage Doctors</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openDoctorModal()}>Add New Doctor</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Search doctors..."
                value={doctorSearchTerm}
                onChange={(e) => setDoctorSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Specialty</th>
                    <th>City</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Work Hours</th>
                    <th>Experience</th>
                    <th>Rating</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentDoctors.length > 0 ? (
                    currentDoctors.map(doctor => (
                      <tr key={doctor.id}>
                        <td>{doctor.id}</td>
                        <td>{doctor.name}</td>
                        <td>{doctor.specialty}</td>
                        <td>{doctor.city}</td>
                        <td>{doctor.email}</td>
                        <td>{doctor.telefon}</td>
                        <td>{doctor.radnoVrijeme}</td>
                        <td>{doctor.iskustvo}</td>
                        <td>{doctor.ocjena}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openDoctorModal(doctor)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteDoctor(doctor.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="10" className="text-center">No doctors found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredDoctors.length}
              currentPage={doctorsCurrentPage}
              paginate={setDoctorsCurrentPage}
            />
          </div>
        );
      case 'patients':
        return (
          <div>
            <h4 className="mb-4">Manage Patients</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openPatientModal()}>Add New Patient</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Search patients..."
                value={patientSearchTerm}
                onChange={(e) => setPatientSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentPatients.length > 0 ? (
                    currentPatients.map(patient => (
                      <tr key={patient.id}>
                        <td>{patient.id}</td>
                        <td>{patient.name}</td>
                        <td>{patient.email}</td>
                        <td>{patient.phone}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openPatientModal(patient)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeletePatient(patient.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center">No patients found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredPatients.length}
              currentPage={patientsCurrentPage}
              paginate={setPatientsCurrentPage}
            />
          </div>
        );
      case 'terms': // Novi tab za termine
        return (
          <div>
            <h4 className="mb-4">Manage Terms</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openTermModal()}>Add New Term</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Search terms..."
                value={termSearchTerm}
                onChange={(e) => setTermSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Doctor ID</th>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Available</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentTerms.length > 0 ? (
                    currentTerms.map(term => (
                      <tr key={term.id}>
                        <td>{term.id}</td>
                        <td>{term.doktorID}</td>
                        <td>{term.datum}</td>
                        <td>{term.vrijeme}</td>
                        <td>{term.dostupnost ? 'Dostupan' : 'Zauzet'}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openTermModal(term)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteTerm(term.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6" className="text-center">No terms found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredTerms.length}
              currentPage={termsCurrentPage}
              paginate={setTermsCurrentPage}
            />
          </div>
        );
      case 'examinations': // Novi tab za preglede (prethodno appointments)
        return (
          <div>
            <h4 className="mb-4">Manage Examinations</h4>
            <div className="d-flex justify-content-between mb-3">
              <button className="btn btn-success" onClick={() => openExaminationModal()}>Add New Examination</button>
              <input
                type="text"
                className="form-control w-25"
                placeholder="Search examinations..."
                value={examinationSearchTerm}
                onChange={(e) => setExaminationSearchTerm(e.target.value)}
              />
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Patient</th>
                    <th>Doctor</th>
                    <th>Term ID</th>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Status</th>
                    <th>Patient Comment</th>
                    <th>Doctor Rating</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentExaminations.length > 0 ? (
                    currentExaminations.map(examination => (
                      <tr key={examination.id}>
                        <td>{examination.id}</td>
                        <td>{examination.patientName}</td>
                        <td>{examination.doctorName}</td>
                        <td>{examination.terminID}</td>
                        <td>{examination.datumPregleda}</td>
                        <td>{examination.vrijemePregleda}</td>
                        <td>
                          <select
                            className="form-select"
                            value={examination.status}
                            onChange={(e) => handleUpdateExamination(
                                {
                                  ...examination,
                                  status: e.target.value,
                                  datumPregleda: examination.datumPregleda,
                                  vrijemePregleda: examination.vrijemePregleda
                                }
                            )}
                          >
                            <option value="zakazan">Zakazan</option>
                            <option value="obavljen">Obavljen</option>
                            <option value="otkazan">Otkazan</option>
                          </select>
                        </td>
                        <td>{examination.komentarPacijenta}</td>
                        <td>{examination.ocjenaDoktora}</td>
                        <td>
                          <button className="btn btn-info btn-sm me-2" onClick={() => openExaminationModal(examination)}>Edit</button>
                          <button className="btn btn-danger btn-sm" onClick={() => handleDeleteExamination(examination.id)}>Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="10" className="text-center">No examinations found.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Pagination
              itemsPerPage={itemsPerPage}
              totalItems={filteredExaminations.length}
              currentPage={examinationsCurrentPage}
              paginate={setExaminationsCurrentPage}
            />
          </div>
        );
      default:
        return <p>Select a tab to view content.</p>;
    }
  };


  const addDoctorFields = [
    { name: 'ime', label: 'First Name', type: 'text', required: true },
    { name: 'prezime', label: 'Last Name', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'password', label: 'Password', type: 'password', required: true },
    { name: 'telefon', label: 'Phone', type: 'text', required: false },
    { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla', 'Bihać'] },
    { name: 'specijalizacije', label: 'Specialty', type: 'select', required: true, options: ['Kardiolog', 'Neurolog', 'Ortoped', 'Pedijatar', 'Hirurg', 'Dermatolog', 'Oftamolog', 'Psihijatar'] },
    { name: 'radnoVrijeme', label: 'Work Hours', type: 'text', required: false },
    { name: 'iskustvo', label: 'Experience (years)', type: 'number', required: false },
  ];

    const editDoctorFields = [
      { name: 'id', label: 'Doctor ID', type: 'text', readOnly: true },
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
      { name: 'grad', label: 'City', type: 'select', required: true, options: ['Sarajevo', 'Mostar', 'Zenica', 'Banja Luka', 'Tuzla', 'Bihać'] },
      { name: 'specijalizacije', label: 'Specialty', type: 'select', required: true, options: ['Kardiolog', 'Neurolog', 'Ortoped', 'Pedijatar', 'Hirurg', 'Dermatolog', 'Oftamolog', 'Psihijatar'] },
      { name: 'radnoVrijeme', label: 'Work Hours', type: 'text', required: false },
      { name: 'iskustvo', label: 'Experience (years)', type: 'number', required: false },
    ];

    const addPatientFields = [
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'password', label: 'Password', type: 'password', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
    ];

    const editPatientFields = [
      { name: 'id', label: 'Patient ID', type: 'text', readOnly: true },
      { name: 'ime', label: 'First Name', type: 'text', required: true },
      { name: 'prezime', label: 'Last Name', type: 'text', required: true },
      { name: 'email', label: 'Email', type: 'email', required: true },
      { name: 'telefon', label: 'Phone', type: 'text', required: false },
    ];

    const addTermFields = [
      { name: 'doktorID', label: 'Doctor ID', type: 'number', required: true },
      { name: 'datum', label: 'Date (YYYY-MM-DD)', type: 'date', required: true },
      { name: 'vrijeme', label: 'Time (HH:MM:SS)', type: 'time', required: true },
      { name: 'dostupnost', label: 'Available', type: 'checkbox', required: false },
    ];

    const editTermFields = [
      { name: 'id', label: 'Term ID (Auto)', type: 'text', required: false, readOnly: true },
      { name: 'doktorID', label: 'Doctor ID', type: 'number', required: true },
      { name: 'datum', label: 'Date (YYYY-MM-DD)', type: 'date', required: true },
      { name: 'vrijeme', label: 'Time (HH:MM:SS)', type: 'time', required: true },
      { name: 'dostupnost', label: 'Available', type: 'checkbox', required: false },
    ];

    const editExaminationFields = [
      { name: 'id', label: 'Examination ID (Auto)', type: 'text', readOnly: true },
      { name: 'pacijentID', label: 'Patient ID', type: 'number', required: true },
      { name: 'doktorID', label: 'Doctor ID', type: 'number', required: true, readOnly: true },
      { name: 'terminID', label: 'Term ID', type: 'number', required: true },
      { name: 'datumPregleda', label: 'Date of Examination (YYYY-MM-DD)', type: 'date', required: true, readOnly: true },
      { name: 'vrijemePregleda', label: 'Time of Examination (HH:MM:SS)', type: 'time', required: true, readOnly: true },
      { name: 'status', label: 'Status', type: 'select', required: true, options: ['zakazan', 'obavljen', 'otkazan'] },
      { name: 'komentarPacijenta', label: 'Patient Comment', type: 'textarea', required: false },
      { name: 'ocjenaDoktora', label: 'Doctor Rating (0.0-5.0)', type: 'number', required: false },
    ];


  if (loadingInitial) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Učitavanje administratorskog panela...</span>
        </div>
      </div>
    );
  }

  if (!loadingInitial && !token && isAuthChecked) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
        <div className="text-center">
          <h2 className="text-danger">Pristup odbijen</h2>
          <p className="lead">Niste autorizovani za pristup ovoj stranici. Molimo prijavite se kao administrator.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      {showAlert && (
        <Alert variant={alertType} onClose={() => setShowAlert(false)} dismissible className="position-fixed top-0 start-50 translate-middle-x mt-3" style={{ zIndex: 1050 }}>
          {alertMessage}
        </Alert>
      )}

      <Modal show={showConfirmModal} onHide={() => setShowConfirmModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>{confirmTitle}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {confirmMessage}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowConfirmModal(false)}>
            Poništi
          </Button>
          <Button variant="danger" onClick={handleConfirmAction}>
            Potvrdi
          </Button>
        </Modal.Footer>
      </Modal>

      <div className="row">
        <nav className="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse" style={{ minHeight: '100vh' }}>
          <div className="position-sticky pt-3">
            <ul className="nav flex-column">
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'overview' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('overview')}>
                  Overview
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'doctors' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('doctors')}>
                  Doctors
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'patients' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('patients')}>
                  Patients
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'terms' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('terms')}>
                  Terms
                </a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${activeTab === 'examinations' ? 'active' : ''}`} href="#" onClick={() => setActiveTab('examinations')}>
                  Examinations
                </a>
              </li>
            </ul>
          </div>
        </nav>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4 py-4">
          {renderContent()}
        </main>
      </div>

      {/* Važno: DataModal, Pagination i renderContent su sada unutar AdminDashboard kao u tvojoj working verziji */}
      <DataModal
        show={showDoctorModal}
        onClose={() => setShowDoctorModal(false)}
        data={editingDoctor}
        onSave={editingDoctor ? handleUpdateDoctor : handleAddDoctor}
        title={editingDoctor ? 'Edit Doctor' : 'Add New Doctor'}
        idPrefix="doctor"
        fields={editingDoctor ? editDoctorFields : addDoctorFields}
        authHeaders={authHeaders}
        showNotification={showNotification}
      />

      <DataModal
        show={showPatientModal}
        onClose={() => setShowPatientModal(false)}
        data={editingPatient}
        onSave={editingPatient ? handleUpdatePatient : handleAddPatient}
        title={editingPatient ? 'Edit Patient' : 'Add New Patient'}
        idPrefix="patient"
        fields={editingPatient ? editPatientFields : addPatientFields}
        authHeaders={authHeaders}
        showNotification={showNotification}
      />

      <DataModal
        show={showTermModal}
        onClose={() => setShowTermModal(false)}
        data={editingTerm}
        onSave={editingTerm ? handleUpdateTerm : handleAddTerm}
        title={editingTerm ? 'Edit Term' : 'Add New Term'}
        idPrefix="term"
        fields={editingTerm ? editTermFields : addTermFields}
        authHeaders={authHeaders}
        showNotification={showNotification}
      />

      <DataModal
        show={showExaminationModal}
        onClose={() => setShowExaminationModal(false)}
        data={editingExamination}
        onSave={editingExamination ? handleUpdateExamination : handleAddExamination}
        title={editingExamination ? 'Edit Examination' : 'Add New Examination'}
        idPrefix="examination"
        fields={editingExamination ? editExaminationFields : addExaminationFields}
        authHeaders={authHeaders}
        showNotification={showNotification}
      />
    </div>
  );
}

export default AdminDashboard;
