            newValue = value;
        } else if (type === 'number') {
            // Konvertiraj u broj. Ako je prazan string, postavi na 0.
            newValue = value === '' ? 0 : Number(value); // Postavljamo na 0 umjesto null
            if (isNaN(newValue)) {
                newValue = 0; // Ako nije validan broj, postavi na 0
            }
        } else if (type === 'checkbox') {
            newValue = checked;
        }

        return { ...prev, [name]: newValue };
      });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      const finalFormData = { ...formData };

      // Specijalizacije: pretvori string u niz
      if (idPrefix === 'doctor' && typeof finalFormData.specijalizacije === 'string') {
          finalFormData.specijalizacije = finalFormData.specijalizacije ? [finalFormData.specijalizacije] : [];
      }

      // Osiguraj da 'iskustvo' i 'ocjena' budu brojevi (0 ili stvarna vrijednost), ne null/prazan string
      if (finalFormData.iskustvo === null || finalFormData.iskustvo === '') {
          finalFormData.iskustvo = 0;
      }
      if (finalFormData.ocjena === null || finalFormData.ocjena === '') {
          finalFormData.ocjena = 0.0;
      }

      // Formatiranje vremena za termine i preglede (vrijemePregleda)
      // Provjerava da li je u pitanju termin ili pregled i da li polje 'vrijeme' ili 'vrijemePregleda' postoji.
      if (idPrefix === 'term' && finalFormData.vrijeme) {
          // Ako je format HH:MM, dodaj :00
          if (finalFormData.vrijeme.length === 5) {
              finalFormData.vrijeme += ':00';
          }
      } else if (idPrefix === 'examination' && finalFormData.vrijemePregleda) {
          // Ako je format HH:MM, dodaj :00
          if (finalFormData.vrijemePregleda.length === 5) {
              finalFormData.vrijemePregleda += ':00';
          }
      }

      console.log('DataModal submitting:', finalFormData); // <-- DODANO ZA DEBUG

      onSave(finalFormData);
    };

    return (
      <Modal show={show} onHide={onClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {fields.map(field => (
              <Form.Group className="mb-3" controlId={`${idPrefix}-${field.name}`} key={field.name}>
                <Form.Label>{field.label}</Form.Label>
                {field.type === 'select' ? (
                  <Form.Select
                    name={field.name}
                    value={formData[field.name] || ''} // Postavi value na prazan string ako je null/undefined
                    onChange={handleChange}
                    required={field.required}
                  >
                    <option value="">Select {field.label}</option>
                    {field.options.map(option => (
                      <option key={option} value={option}>{option}</option>
                    ))}
                  </Form.Select>
                ) : field.type === 'textarea' ? (
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required={field.required}
                  />
                ) : field.type === 'checkbox' ? (
                    <Form.Check
                        type="checkbox"
                        name={field.name}
                        checked={formData[field.name] || false} // checked state for checkbox
                        onChange={handleChange}
                        label={field.label}
                    />
                ) : (
                  <Form.Control
                    type={field.type || 'text'}
                    name={field.name}
                    // Prikazuje prazan string ako je vrijednost null, undefined, 0 (za number), 'N/A'
                    value={(formData[field.name] === null || formData[field.name] === undefined || formData[field.name] === 'N/A' || (field.type === 'number' && (formData[field.name] === 0 || formData[field.name] === 0.0))) ? '' : formData[field.name]}
                    onChange={handleChange}
                    required={field.required}
                    readOnly={field.readOnly} // Dodano da bi se ID polje moglo prikazati kao readOnly
                  />
                )}
              </Form.Group>
            ))}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={onClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Save changes
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
