import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await axios.post('http://localhost:8081/auth/login', {
        email,
        password,
      });

      if (response.status === 200) {
        const { token, email: userEmail, fullName, roles, message } = response.data;

        login(token, roles, userEmail, fullName);

        setSuccess(message || 'Login uspješan!');
        console.log('Login successful:', response.data);

        if (roles && roles.includes('ROLE_ADMIN')) {
          navigate('/admin-dashboard');
        } else if (roles && roles.includes('ROLE_DOCTOR')) {
          navigate('/doctor-dashboard');
        } else if (roles && roles.includes('ROLE_PATIENT')) {
          navigate('/patient-dashboard');
        } else {
          navigate('/');
        }
      }

    } catch (err) {
      if (err.response) {
        setError(err.response.data.message || 'Greška prilikom prijave.');
        console.error('Login error:', err.response.data);
      } else if (err.request) {
        setError('Nema odgovora od servera.');
        console.error('Login error: No response from server.');
      } else {
        setError('Greška: ' + err.message);
        console.error('Login error:', err.message);
      }
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header text-center bg-danger text-white">
              <h2>Login</h2>
            </div>
            <div className="card-body">
              {error && <div className="alert alert-danger">{error}</div>}
              {success && <div className="alert alert-success">{success}</div>}
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="emailInput" className="form-label">Email address</label>
                  <input
                    type="email"
                    className="form-control"
                    id="emailInput"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="passwordInput" className="form-label">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    id="passwordInput"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="d-grid gap-2">
                  <button type="submit" className="btn btn-danger">Login</button>
                  <Link to="/forgot-password" className="text-center text-danger">Forgot Password?</Link>
                  <div className="text-center mt-3">
                    Don't have an account? <Link to="/sign-up" className="text-danger">Sign Up</Link>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
