import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Za dohvat ID-a iz URL-a
import 'bootstrap/dist/css/bootstrap.min.css';

function DoctorProfilePage() {
  const { id } = useParams(); // Dohvaća ID doktora iz URL-a
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State za formu za zakazivanje termina
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');
  const [patientName, setPatientName] = useState('');
  const [patientEmail, setPatientEmail] = useState(''); // Ispravljeno: setPatientName -> setPatientEmail
  const [bookingMessage, setBookingMessage] = useState('');

  // Mock podaci za doktora (zamijenite stvarnim dohvaćanjem s backenda)
  const mockDoctorData = {
    id: parseInt(id), // Osigurava da je ID broj
    name: 'Dr. John Doe',
    specialty: 'Pediatrician',
    location: 'Mostar',
    experience: '10+ Years',
    rating: 4.5,
    patients: '2.5K+',
    reviews: '1.5K+',
    image: 'https://placehold.co/150x150/8BC34A/FFFFFF?text=JD', // Placeholder image
    about: "Dr. John Doe is a highly experienced pediatrician with over 10 years of practice dedicated to providing comprehensive and compassionate care for children of all ages. He is passionate about early childhood development and preventive health. Dr. Doe is known for his friendly demeanor and ability to make young patients feel comfortable during their visits.",
    education: [
      "MD - University of Sarajevo, 2010",
      "Residency in Pediatrics - Clinical Center University of Sarajevo, 2015"
    ],
    awards: [
      "Top Pediatrician Award, 2022",
      "Patient's Choice Award, 2021"
    ],
    workingHours: "Mon-Fri: 09:00 - 17:00", // Radno vrijeme doktora
    contact: {
      phone: "+387 61 123 456",
      email: "john.doe@example.com"
    },
    // Dodani mock podaci za bolnicu/kliniku
    hospitalName: "WinxCare Clinic",
    hospitalAddress: "Ulica Bolnice 123, 71000 Sarajevo, Bosna i Hercegovina",
    hospitalMapEmbedUrl: "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2877.940409029983!2d18.40698111559815!3d43.85628587911579!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x4758c92a95c9a173%3A0x1d1d1d1d1d1d1d1d!2sSarajevo!5e0!3m2!1sen!2sba!4v1678901234567!5m2!1sen!2sba" // Primjer Google Maps embed URL-a
  };

  // Mock podaci za dostupne termine (zamijenite stvarnim podacima s backenda)
  const mockAvailableSlots = {
    '2025-05-26': ['09:00', '10:00', '11:00', '14:00', '15:00'],
    '2025-05-27': ['09:30', '10:30', '11:30', '13:00', '14:00', '16:00'],
    '2025-05-28': ['09:00', '10:00', '11:00', '12:00'],
    '2025-05-29': ['10:00', '11:00', '15:00', '16:00'],
  };

  useEffect(() => {
    // U stvarnoj aplikaciji, ovdje biste dohvatili podatke o doktoru s backenda
    // koristeći `id` iz `useParams`.
    // Primjer: const API_URL_SINGLE_DOCTOR = `http://localhost:8080/api/doktori/${id}`;
    // fetch(API_URL_SINGLE_DOCTOR) ...

    // Simulacija dohvaćanja podataka s kašnjenjem
    setTimeout(() => {
      if (mockDoctorData.id === parseInt(id)) { // Provjera da li se ID podudara
        setDoctor(mockDoctorData);
      } else {
        setError(new Error("Doctor not found"));
      }
      setLoading(false);
    }, 500); // Simulacija mrežnog kašnjenja
  }, [id]); // Ponovo dohvati podatke ako se ID u URL-u promijeni

  // Funkcija za rukovanje zakazivanjem termina
  const handleBookingSubmit = (e) => {
    e.preventDefault();
    if (!selectedDate || !selectedTimeSlot || !patientName || !patientEmail) {
      setBookingMessage('Molimo popunite sva polja za zakazivanje termina.');
      return;
    }
    // Ovdje biste poslali podatke na backend
    console.log('Zakazan termin:', {
      doktorId: doctor.id,
      date: selectedDate,
      time: selectedTimeSlot,
      patientName: patientName,
      patientEmail: patientEmail
    });
    setBookingMessage(`Termin zakazan za ${patientName} na ${selectedDate} u ${selectedTimeSlot}.`);
    // Resetuj formu
    setSelectedDate('');
    setSelectedTimeSlot('');
    setPatientName('');
    setPatientEmail('');
  };

  // Dobivanje dostupnih termina za odabrani datum
  const availableSlotsForSelectedDate = mockAvailableSlots[selectedDate] || [];

  if (loading) {
    return (
      <div className="container text-center py-5">
        <p className="lead">Učitavanje profila doktora...</p>
        <div className="spinner-border text-danger" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container text-center py-5">
        <p className="lead text-danger">Greška prilikom učitavanja profila: {error.message}</p>
        <p>Molimo pokušajte ponovo kasnije.</p>
      </div>
    );
  }

  if (!doctor) {
    return (
      <div className="container text-center py-5">
        <p className="lead">Doktor nije pronađen.</p>
      </div>
    );
  }

  return (
    <div className="container my-5">
      {/* Dodajemo stil za baby pink pozadinu forme */}
      <style>
        {`
        .bg-baby-pink {
          background-color: #ffe6f0; /* Nježno roza boja */
        }
        `}
      </style>
      <div className="card shadow-sm border-0 rounded-lg">
        <div className="card-body p-4">
          <div className="row align-items-center mb-4">
            <div className="col-md-3 text-center">
              <img
                src={doctor.image}
                alt={doctor.name}
                className="rounded-circle border border-5 border-light shadow-sm"
                style={{ width: '150px', height: '150px', objectFit: 'cover' }}
              />
            </div>
            <div className="col-md-9">
              <h2 className="mb-1 text-danger">{doctor.name}</h2>
              <h5 className="text-muted mb-2">{doctor.specialty}</h5>
              <div className="d-flex align-items-center mb-2">
                <span className="text-warning me-1">★</span>
                <span className="fw-bold me-2">{doctor.rating}</span>
                <span className="text-muted">({doctor.reviews} Reviews)</span>
              </div>
              <div className="d-flex flex-wrap gap-3 mt-3">
                <div className="text-center">
                  <h6 className="mb-0 text-dark">Experience</h6>
                  <p className="text-muted">{doctor.experience}</p>
                </div>
                <div className="text-center">
                  <h6 className="mb-0 text-dark">Patients</h6>
                  <p className="text-muted">{doctor.patients}</p>
                </div>
                <div className="text-center">
                  <h6 className="mb-0 text-dark">Reviews</h6>
                  <p className="text-muted">{doctor.reviews}</p>
                </div>
              </div>
            </div>
          </div>

          <hr className="my-4" />

          {/* About Section */}
          <div className="mb-4">
            <h4 className="text-danger mb-3">About</h4>
            <p className="text-muted">{doctor.about}</p>
          </div>

          {/* Education Section */}
          <div className="mb-4">
            <h4 className="text-danger mb-3">Education</h4>
            <ul className="list-unstyled">
              {doctor.education.map((edu, index) => (
                <li key={index} className="text-muted mb-1">
                  <i className="bi bi-dot me-2"></i>{edu}
                </li>
              ))}
            </ul>
          </div>

          {/* Awards Section */}
          <div className="mb-4">
            <h4 className="text-danger mb-3">Awards</h4>
            <ul className="list-unstyled">
              {doctor.awards.map((award, index) => (
                <li key={index} className="text-muted mb-1">
                  <i className="bi bi-award-fill me-2"></i>{award}
                </li>
              ))}
            </ul>
          </div>

          {/* Working Hours & Contact */}
          <div className="row">
            <div className="col-12 mb-4">
              <h4 className="text-danger mb-3">Working Hours</h4>
              <p className="text-muted">{doctor.workingHours}</p>
            </div>
            <div className="col-12 mb-4">
              <h4 className="text-danger mb-3">Contact Information</h4>
              <p className="text-muted mb-1">Phone: {doctor.contact.phone}</p>
              <p className="text-muted">Email: {doctor.contact.email}</p>
            </div>
          </div>

          <hr className="my-4" />

          {/* Appointment Booking Section */}
          <div className="mb-4 p-4 rounded bg-baby-pink">
            <h4 className="text-danger mb-4 text-center">Book an Appointment</h4>
            <form onSubmit={handleBookingSubmit}>
              <div className="row g-3 mb-3">
                <div className="col-md-6">
                  <label htmlFor="patientName" className="form-label">Your Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="patientName"
                    value={patientName}
                    onChange={(e) => setPatientName(e.target.value)}
                    required
                  />
                </div>
                <div className="col-md-6">
                  <label htmlFor="patientEmail" className="form-label">Your Email</label>
                  <input
                    type="email"
                    className="form-control"
                    id="patientEmail"
                    value={patientEmail}
                    onChange={(e) => setPatientEmail(e.target.value)}
                    required
                  />
                </div>
              </div>

              <div className="mb-3">
                <label htmlFor="appointmentDate" className="form-label">Select Date</label>
                <input
                  type="date"
                  className="form-control"
                  id="appointmentDate"
                  value={selectedDate}
                  onChange={(e) => {
                    setSelectedDate(e.target.value);
                    setSelectedTimeSlot(''); // Reset time slot when date changes
                  }}
                  required
                />
              </div>

              {selectedDate && (
                <div className="mb-4">
                  <h5 className="text-dark mb-3">Available Time Slots for {selectedDate}:</h5>
                  {availableSlotsForSelectedDate.length > 0 ? (
                    <div className="d-flex flex-wrap gap-2">
                      {availableSlotsForSelectedDate.map(slot => (
                        <button
                          key={slot}
                          type="button"
                          className={`btn ${selectedTimeSlot === slot ? 'btn-danger' : 'btn-outline-danger'} btn-sm`}
                          onClick={() => setSelectedTimeSlot(slot)}
                        >
                          {slot}
                        </button>
                      ))}
                    </div>
                  ) : (
                    <p className="text-muted">No available slots for this date.</p>
                  )}
                </div>
              )}

              <div className="text-center mt-4">
                <button type="submit" className="btn btn-danger btn-lg">Book Appointment</button>
              </div>
              {bookingMessage && (
                <div className="alert alert-info mt-3 text-center" role="alert">
                  {bookingMessage}
                </div>
              )}
            </form>
          </div>

          {/* New Section: Hospital Address and Map */}
                    <section className="py-5 bg-light rounded-lg shadow-sm mb-4">
                      <div className="container">
                        <h5 className="text-center mb-5 display-5 text-dark">{doctor.hospitalName}</h5>
                        <p className="text-center lead mb-5 text-muted">
                          {doctor.hospitalAddress}
                        </p>
                        <div className="embed-responsive embed-responsive-16by9" style={{ height: '400px' }}>
                          <iframe
                            className="embed-responsive-item w-100 h-100 rounded shadow"
                            src={doctor.hospitalMapEmbedUrl}
                            allowFullScreen=""
                            loading="lazy"
                            referrerPolicy="no-referrer-when-downgrade"
                            title="Hospital Location Map"
                          ></iframe>
                        </div>
                      </div>
                    </section>

        </div>
      </div>
    </div>
  );
}

export default DoctorProfilePage;
