    const fetchDocuments = async () => {
        if (!token || !user || user.doktorId === null || user.doktorId === undefined) {
            console.warn("Cannot fetch documents: doktorId is missing or auth not ready.");
            return;
        }
        try {
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/doktor/${user.doktorId}`, authHeaders);
            const transformedDocuments = response.data.map(doc => ({
                id: doc.dokumentID,
                fileName: doc.nazivDokumenta,
                documentType: doc.tipDokumenta,
                uploadDate: doc.datumKreiranja,
                pristup: doc.pristup,
                pacijentIme: doc.pacijentIme
            }));
            setDocuments(transformedDocuments);
            showNotification("Documents successfully fetched!", "success");
        } catch (error) {
            console.error('Error fetching documents for doctor:', error.response?.data || error.message);
            showNotification('Failed to fetch documents.', "danger");
        }
    };

    const handleDocumentUpload = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        if (!user || user.doktorId === null || user.doktorId === undefined) {
            showNotification("Doctor ID is not available for document upload.", "danger");
            return;
        }

        console.log("Attempting document upload with Doktor ID:", user.doktorId);

        const formData = new FormData();
        formData.append('file', file);
        formData.append('doktorID', user.doktorId); // Send doktorID
        // formData.append('pacijentID', null); // Do not send if not relevant
        formData.append('tipDokumenta', 'Doctor Upload');
        formData.append('pristup', 'PRIVATNA'); // Or whatever default access you want

        try {
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/upload`, formData, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data'
                }
            });
            showNotification('Document successfully uploaded!', 'success');
            fetchDocuments(); // Refresh documents after upload
        } catch (error) {
            console.error('Error uploading document:', error.response?.data || error.message);
            showNotification('Document upload failed. ' + (error.response?.data?.message || error.message), "danger");
        }
    };

  // NEW: Function to handle document download via Axios (to include auth header)
  const handleDownloadDocument = async (docId, fileName) => {
      // --- VAŽNE IZMJENE POČINJU OVDJE ---
      // 1. Validacija docId-a
      // Prije nego što pošaljemo zahtjev, provjeravamo da li je docId važeći broj.
      // Koristimo Number() da pokušamo konvertovati docId u broj,
      // a zatim provjeravamo da li je rezultat NaN (Not a Number).
      // Također provjeravamo da li je docId null ili undefined.
      const numericDocId = Number(docId); // Pokušaj konverzije u broj

      if (docId === null || docId === undefined || isNaN(numericDocId)) {
          console.error('Pokušaj preuzimanja dokumenta sa nevažećim ID-om:', docId);
          showNotification('Greška: ID dokumenta je nevažeći. Preuzimanje nije moguće.', 'danger');
          return; // Prekini izvršavanje funkcije ako ID nije važeći
      }
      // --- VAŽNE IZMJENE ZAVRŠAVAJU OVDJE ---

      try {
          // Sada koristimo validirani numericDocId
          const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/download/${numericDocId}`, {
              ...authHeaders, // Include authorization headers
              responseType: 'blob', // Important: get response as a Blob
          });

          // Create a Blob from the response data
          const blob = new Blob([response.data], { type: response.headers['content-type'] });
          const url = window.URL.createObjectURL(blob);
          const link = document.createElement('a');
          link.href = url;
          link.setAttribute('download', fileName); // Use the original file name
          document.body.appendChild(link);
          link.click();
          link.remove();
          window.URL.revokeObjectURL(url); // Clean up the URL object

          showNotification('Dokument uspješno preuzet!', 'success');
      } catch (error) {
          console.error('Greška pri preuzimanju dokumenta:', error.response?.data || error.message);
          showNotification('Nije uspjelo preuzimanje dokumenta. ' + (error.response?.data?.message || error.message), 'danger');
      }
  };


    const fetchMessages = async () => {
        if (!token || !user || user.userId === null || user.userId === undefined) { // Use user.userId
            console.warn("Cannot fetch messages: user.userId is missing or auth not ready.");
            return;
        }
        try {
            // Updated endpoint for fetching messages using user.userId and userType DOKTOR
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/konverzacija/${user.userId}?userType=DOKTOR`, authHeaders);
            const transformedMessages = response.data.map(msg => ({
                id: msg.porukaID,
                senderId: Number(msg.senderId), // Explicitly convert to Number
                senderType: msg.senderType,
                senderIme: msg.senderIme,
                receiverId: Number(msg.receiverId), // Explicitly convert to Number
                receiverType: msg.receiverType,
                receiverIme: msg.receiverIme,
                subject: msg.subject,
                content: msg.content,
                timestamp: msg.timestamp,
                replies: msg.replies || [] // Ensure replies is always an array
            }));
            setMessages(transformedMessages);
            showNotification("Messages successfully fetched!", "success");
        } catch (error) {
            console.error('Error fetching messages for doctor:', error.response?.data || error.message);
            showNotification('Failed to fetch messages.', "danger");
        }
    };

    const handleNewMessageChange = (e) => {
        const { name, value } = e.target;
        setNewMessage(prev => ({ ...prev, [name]: value }));
    };

    const handleSendMessage = async () => {
        if (!user || user.userId === null || user.userId === undefined) { // Use user.userId
            showNotification('Your user ID is not available. Cannot send message.', 'danger');
            return;
        }
        if (!newMessage.recipientId || !newMessage.subject || !newMessage.content) {
            showNotification('Please fill in all message fields.', 'warning');
            return;
        }

        try {
            const messageData = {
                senderId: user.userId, // SENDER is now user.userId (Long)
                senderType: 'DOKTOR',
                receiverId: Number(newMessage.recipientId), // RECIPIENT is patient's user.userId (Long) - convert to Number
                receiverType: 'PACIJENT',
                subject: newMessage.subject,
                content: newMessage.content
            };
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/posalji`, messageData, authHeaders);
            showNotification('Message successfully sent!', 'success');
            setNewMessage({ recipientId: '', subject: '', content: '' });
            fetchMessages();
        } catch (error) {
            console.error('Error sending message:', error.response?.data || error.message);
            showNotification('Failed to send message. ' + (error.response?.data?.message || error.message), "danger");
        }
    };

    const handleReplyMessageChange = (e) => {
        setReplyMessage(prev => ({ ...prev, content: e.target.value }));
    };

    const handleReply = async (messageId) => {
        if (!user || user.userId === null || user.userId === undefined) { // Use user.userId
            showNotification('Your user ID is not available. Cannot reply.', 'danger');
            return;
        }
        if (!replyMessage.content) {
