            return;
        }
        try {
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/${messageId}/odgovori`, {
                senderId: user.userId, // Important: Use user.userId here
                senderIme: `${user.ime || ''} ${user.prezime || ''}`.trim(), // Use name/surname from useAuth, if available
                senderType: 'DOKTOR',
                content: replyMessage.content,
                timestamp: new Date().toISOString()
            }, authHeaders);
            showNotification('Reply successfully sent!', 'success');
            setReplyMessage({ messageId: '', content: '' });
            fetchMessages();
            // Update local state for current reply display
            setSelectedMessage(prev => {
                if (!prev) return null;
                return {
                    ...prev,
                    replies: [...(prev.replies || []), {
                        senderId: user.userId, // Important: Use user.userId here
                        senderIme: `${user.ime || ''} ${user.prezime || ''}`.trim(), // Use name/surname from useAuth, if available
                        senderType: 'DOKTOR',
                        content: replyMessage.content,
                        timestamp: new Date().toISOString()
                    }]
