import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Za navigaciju na druge stranice
import 'bootstrap/dist/css/bootstrap.min.css';

function SignUpPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [registrationMessage, setRegistrationMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setRegistrationMessage('Lozinke se ne podudaraju.');
      return;
    }
    // Ovdje biste obično poslali podatke za registraciju na backend.
    // Za sada, samo simuliramo registraciju.
    console.log('Registration attempt:', { name, email, password });
    setRegistrationMessage('Registracija uspješna! Molimo prijavite se.');
    // Nakon uspješne registracije, možete resetovati formu ili preusmjeriti korisnika.
    setName('');
    setEmail('');
    setPassword('');
    setConfirmPassword('');
  };

  return (
    <div className="container d-flex justify-content-center align-items-center" style={{ minHeight: '80vh' }}>
      <div className="card shadow-lg p-4" style={{ maxWidth: '450px', width: '100%' }}>
        <div className="card-body">
          <h2 className="card-title text-center mb-4 text-danger">Sign Up for WinxCare</h2>
          {registrationMessage && (
            <div className={`alert ${registrationMessage.includes('uspješna') ? 'alert-success' : 'alert-danger'} text-center`} role="alert">
              {registrationMessage}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="nameInput" className="form-label">Full Name</label>
              <input
                type="text"
                className="form-control"
                id="nameInput"
                placeholder="Your Full Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="emailInput" className="form-label">Email address</label>
              <input
                type="email"
                className="form-control"
                id="emailInput"
                placeholder="name@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="passwordInput" className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                id="passwordInput"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="confirmPasswordInput" className="form-label">Confirm Password</label>
              <input
                type="password"
                className="form-control"
                id="confirmPasswordInput"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>
            <div className="d-grid gap-2 mb-3">
              <button type="submit" className="btn btn-danger btn-lg">Sign Up</button>
            </div>
          </form>
          <div className="text-center mt-3">
            <p className="text-muted">Already have an account? <Link to="/login" className="text-danger text-decoration-none">Login</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SignUpPage;
