import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect, useMemo } from 'react';
import { Modal, Button, Form, Alert, Spinner, Tab, Tabs } from 'react-bootstrap';

const GATEWAY_BASE_URL = 'http://localhost:8081';

const KORISNICI_DOKTORI_API_PREFIX = `${GATEWAY_BASE_URL}/api/korisnici-doktori`;
const TERMINI_PREGLEDI_API_PREFIX = `${GATEWAY_BASE_URL}/api/termini-pregledi`;
const OBAVIJESTI_DOKUMENTI_API_PREFIX = `${GATEWAY_BASE_URL}/api/obavijesti-dokumentacija`;
const AUTH_API_PREFIX = `${GATEWAY_BASE_URL}/auth`;

function PatientDashboard() {
    const { token, user } = useAuth();
    console.log("Trenutni korisnički objekt iz useAuth (PatientDashboard):", user);

    const authHeaders = useMemo(() => ({
        headers: {
            'Authorization': `Bearer ${token}`
        }
    }), [token]);

    const [activeTab, setActiveTab] = useState('overview');
    const [appointments, setAppointments] = useState([]);
    const [medicalHistory, setMedicalHistory] = useState([]);
    const [patientProfile, setPatientProfile] = useState({});
    const [doctors, setDoctors] = useState([]);
    const [allDoctorAvailableTerms, setAllDoctorAvailableTerms] = useState([]);
    const [documents, setDocuments] = useState([]);
    const [bills, setBills] = useState([]);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState({ recipientId: '', subject: '', content: '' });
    const [replyMessage, setReplyMessage] = useState({ messageId: '', content: '' });
    const [selectedMessage, setSelectedMessage] = useState(null);

    const [showAlert, setShowAlert] = useState(false);
    const [alertMessage, setAlertMessage] = useState('');
    const [alertType, setAlertType] = useState('success');

    const [showAppointmentModal, setShowAppointmentModal] = useState(false);
    const [newAppointment, setNewAppointment] = useState({
        doctorId: '',
        appointmentDate: '',
        appointmentTime: '',
        terminId: '',
        reason: ''
    });
    const [editingAppointment, setEditingAppointment] = useState(null);

    // Stanje za modal za ocjenjivanje
    const [showRatingModal, setShowRatingModal] = useState(false);
    const [selectedDoctorForRating, setSelectedDoctorForRating] = useState(null);
    const [ratingData, setRatingData] = useState({ ocjena: 5, komentar: '' }); // Default ocjena 5

    const [loading, setLoading] = useState(true);

    // --- API Calls ---

    const fetchPatientAppointments = async () => {
        if (!user || !user.pacijentId) {
            console.warn("ID pacijenta nije dostupan za dohvaćanje pregleda.");
            return;
        }
        try {
            const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/zakazani/${user.pacijentId}`, authHeaders);
            const transformedAppointments = response.data.map(app => ({
                id: app.pregledID,
                doktorID: app.doktorID,
                pacijentID: app.pacijentID,
                datumPregleda: app.datumPregleda,
                vrijemePregleda: app.vrijemePregleda,
                komentarPacijenta: app.komentarPacijenta,
                status: app.status,
                terminID: app.terminID,
                doktorIme: app.doktorIme
            }));
            setAppointments(transformedAppointments);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Greška prilikom dohvaćanja pacijentovih pregleda:', error.response?.data || error.message);
            setAlertMessage('Neuspješno dohvaćanje zakazanih pregleda.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchMedicalHistory = async () => {
        if (!user || !user.pacijentId) {
            console.warn("ID pacijenta nije dostupan za dohvaćanje medicinske historije.");
            return;
        }
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/medicinska-historija/pacijent/${user.pacijentId}`, authHeaders);
            const transformedHistory = response.data.map(record => ({
                id: record.zapisID,
                patientId: record.pacijentID,
                doctorId: record.doktorID,
                doctorName: record.doktorIme,
                date: record.datumZapisivanja,
                diagnosis: record.dijagnoza,
                treatment: record.lijecenje,
                notes: record.napomene
            }));
            setMedicalHistory(transformedHistory);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Greška prilikom dohvaćanja medicinske historije:', error.response?.data || error.message);
            setAlertMessage('Neuspješno dohvaćanje medicinske historije.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchPatientProfile = async () => {
        if (!user || !user.pacijentId) {
            console.warn("ID pacijenta nije dostupan za dohvaćanje pacijentovog profila.");
            return;
        }
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${user.pacijentId}`, authHeaders);
            setPatientProfile({
                id: response.data.pacijentID,
                firstName: response.data.ime,
                lastName: response.data.prezime,
                email: response.data.email,
                phone: response.data.telefon,
                datumRodjenja: response.data.datumRodjenja,
                adresa: response.data.adresa,
                userId: response.data.userId
            });
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Greška prilikom dohvaćanja pacijentovog profila:', error.response?.data || error.message);
            setAlertMessage('Neuspješno dohvaćanje podataka profila.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchDoctors = async () => {
        try {
            const response = await axios.get(`${KORISNICI_DOKTORI_API_PREFIX}/api/doktori`, authHeaders);
            const transformedDoctors = response.data.map(doc => ({
                id: doc.doktorID,
                userId: doc.userId,
                firstName: doc.ime,
                lastName: doc.prezime,
                specialty: Array.isArray(doc.specijalizacije) ? doc.specijalizacije.join(', ') : doc.specijalizacije,
                email: doc.email,
                phone: doc.telefon,
                ocjena: doc.ocjena // Uvjeri se da je ocjena mapirana sa backend-a
            }));
            setDoctors(transformedDoctors);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Greška prilikom dohvaćanja doktora:', error.response?.data || error.message);
            setAlertMessage('Neuspješno dohvaćanje liste doktora.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchDoctorAllAvailableTerms = async (doktorId) => {
        if (!doktorId) {
            setAllDoctorAvailableTerms([]);
            return;
        }
        setLoading(true);
        try {
            const response = await axios.get(`${TERMINI_PREGLEDI_API_PREFIX}/api/termini/slobodni/${doktorId}`, authHeaders);
            const termsData = Array.isArray(response.data) ? response.data : [];
            const available = termsData
                .map(term => ({
                    terminID: term.terminID,
                    datum: term.datum,
                    vrijeme: term.vrijeme,
                    display: `${term.datum} ${term.vrijeme.substring(0, 5)}`
                }))
                .sort((a, b) => {
                    const dateTimeA = `${a.datum}T${a.vrijeme}`;
                    const dateTimeB = `${b.datum}T${b.vrijeme}`;
                    return new Date(dateTimeA) - new Date(dateTimeB);
                });
            setAllDoctorAvailableTerms(available);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Greška prilikom dohvaćanja dostupnosti doktora:', error.response?.data || error.message);
            setAlertMessage('Neuspješno dohvaćanje dostupnosti doktora.');
            setAlertType('danger');
            setShowAlert(true);
            setAllDoctorAvailableTerms([]);
        } finally {
            setLoading(false);
        }
    };

    const fetchDocuments = async () => {
        if (!user || !user.pacijentId) {
            console.warn("ID pacijenta nije dostupan za dohvaćanje dokumenata.");
            return;
        }
        try {
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/pacijent/${user.pacijentId}`, authHeaders);
            const transformedDocuments = response.data.map(doc => ({
                id: doc.dokumentID,
                fileName: doc.nazivDokumenta,
                documentType: doc.tipDokumenta,
                uploadDate: doc.datumKreiranja,
                pristup: doc.pristup
            }));
            setDocuments(transformedDocuments);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Greška prilikom dohvaćanja dokumenata:', error.response?.data || error.message);
            setAlertMessage('Neuspješan prijenos dokumenta.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchBills = async () => {
        if (!user || !user.pacijentId) {
            console.warn("ID pacijenta nije dostupan za dohvaćanje računa.");
            return;
        }
        try {
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/racuni/pacijent/${user.pacijentId}`, authHeaders);
            const transformedBills = response.data.map(bill => ({
                id: bill.racunID,
                billDate: bill.datumIzdavanja,
                amount: bill.iznos,
                status: bill.status,
                dueDate: bill.datumDospijeca,
                opis: bill.opis,
                doktorIme: bill.doktorIme
            }));
            setBills(transformedBills);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Greška prilikom dohvaćanja računa:', error.response?.data || error.message);
            setAlertMessage('Neuspješno dohvaćanje informacija o naplati.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const fetchMessages = async () => {
        if (!user || !user.pacijentId) {
            console.warn("ID pacijenta nije dostupan za dohvaćanje poruka.");
            return;
        }
        try {
            const response = await axios.get(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/konverzacija/${user.userId}?userType=PACIJENT`, authHeaders);
            const transformedMessages = response.data.map(msg => ({
                id: msg.porukaID,
                senderId: msg.senderId,
                senderType: msg.senderType,
                senderIme: msg.senderIme,
                receiverId: msg.receiverId,
                receiverType: msg.receiverType,
                receiverIme: msg.receiverIme,
                subject: msg.subject,
                content: msg.content,
                timestamp: msg.timestamp,
                replies: msg.replies || []
            }));
            setMessages(transformedMessages);
            setAlertType('success');
            setShowAlert(false);
        } catch (error) {
            console.error('Greška prilikom dohvaćanja poruka:', error.response?.data || error.message);
            setAlertMessage('Neuspješno dohvaćanje poruka.');
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    // --- useEffect Hooks ---
    useEffect(() => {
        const loadAllData = async () => {
            if (token && user?.pacijentId) {
                setLoading(true);
                await Promise.all([
                    fetchPatientAppointments(),
                    fetchMedicalHistory(),
                    fetchPatientProfile(),
                    fetchDoctors(),
                    fetchDocuments(),
                    fetchBills(),
                    fetchMessages()
                ]);
                setLoading(false);
            } else if (!token || (user && !user.pacijentId)) {
                setLoading(false);
                if (!token) {
                    console.warn("Token nije dostupan. Korisnik nije ulogovan.");
                } else if (user && !user.pacijentId) {
                    console.warn("Korisnički objekt je dostupan, ali pacijentId nije. Moguće da korisnik nije pacijent.");
                }
            }
        };
        loadAllData();
    }, [token, user]);


    // --- Appointment Scheduling/Cancellation ---

    const openAppointmentModal = (appointment = null, doctorIdFromSearch = null) => {
        if (appointment) {
            setEditingAppointment(appointment);
            setNewAppointment({
                doctorId: appointment.doktorID,
                appointmentDate: appointment.datumPregleda,
                appointmentTime: appointment.vrijemePregleda,
                terminId: appointment.terminID,
                reason: appointment.komentarPacijenta
            });
            fetchDoctorAllAvailableTerms(appointment.doktorID);
        } else {
            setEditingAppointment(null);
            setNewAppointment({
                doctorId: doctorIdFromSearch || '',
                appointmentDate: '',
                appointmentTime: '',
                terminId: '',
                reason: ''
            });
            setAllDoctorAvailableTerms([]);
            // Ako je doctorIdFromSearch prisutan, odmah dohvati termine
            if (doctorIdFromSearch) {
                fetchDoctorAllAvailableTerms(doctorIdFromSearch);
            }
        }
        setShowAppointmentModal(true);
    };

    const closeAppointmentModal = () => {
        setShowAppointmentModal(false);
        setEditingAppointment(null);
        setNewAppointment({
            doctorId: '',
            appointmentDate: '',
            appointmentTime: '',
            terminId: '',
            reason: ''
        });
        setAllDoctorAvailableTerms([]);
    };

    const handleNewAppointmentChange = async (e) => {
        const { name, value } = e.target;

        let updatedAppointment = { ...newAppointment, [name]: value };

        if (name === 'doctorId') {
            updatedAppointment.appointmentDate = '';
            updatedAppointment.appointmentTime = '';
            updatedAppointment.terminId = '';
            if (value) {
                await fetchDoctorAllAvailableTerms(value);
            } else {
                setAllDoctorAvailableTerms([]);
            }
        } else if (name === 'terminId') {
            const selectedTerm = allDoctorAvailableTerms.find(term => term.terminID === parseInt(value));
            if (selectedTerm) {
                updatedAppointment.terminId = selectedTerm.terminID;
                updatedAppointment.appointmentDate = selectedTerm.datum;
                updatedAppointment.appointmentTime = selectedTerm.vrijeme;
            } else {
                updatedAppointment.terminId = '';
                updatedAppointment.appointmentDate = '';
                updatedAppointment.appointmentTime = '';
            }
        }
        setNewAppointment(updatedAppointment);
    };


    const handleScheduleAppointment = async () => {
        if (!user || !user.pacijentId) {
            setAlertMessage('ID pacijenta nije dostupan. Nije moguće zakazati pregled.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }

        if (!newAppointment.doctorId || !newAppointment.terminId || !newAppointment.reason) {
            setAlertMessage('Molimo odaberite doktora, slobodan termin i navedite razlog.');
            setAlertType('warning');
            setShowAlert(true);
            return;
        }

        try {
            const appointmentData = {
                pacijentID: user.pacijentId,
                doktorID: parseInt(newAppointment.doctorId),
                terminID: newAppointment.terminId,
                datumPregleda: newAppointment.appointmentDate,
                vrijemePregleda: newAppointment.appointmentTime,
                komentarPacijenta: newAppointment.reason,
                status: editingAppointment ? editingAppointment.status : 'zakazan',
            };

            let response;
            if (editingAppointment) {
                response = await axios.put(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/${editingAppointment.id}`, appointmentData, authHeaders);
                setAlertMessage('Pregled uspješno ažuriran!');
            } else {
                response = await axios.post(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi`, appointmentData, authHeaders);
                setAlertMessage('Pregled uspješno zakazan!');
            }

            setAlertType('success');
            setShowAlert(true);
            fetchPatientAppointments();
            closeAppointmentModal();
        } catch (error) {
            console.error('Greška prilikom zakazivanja/ažuriranja pregleda:', error.response?.data || error.message);
            let errorMessage = 'Neuspješno zakazivanje/ažuriranje pregleda. Molimo pokušajte ponovo.';
            if (error.response?.data?.message) {
                errorMessage = error.response.data.message;
            } else if (error.message) {
                errorMessage = error.message;
            }
            setAlertMessage(errorMessage);
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const handleCancelAppointment = async (appointmentId) => {
        if (window.confirm('Jeste li sigurni da želite otkazati ovaj pregled?')) {
            if (!user || !user.pacijentId) {
                setAlertMessage('ID pacijenta nije dostupan za otkazivanje. Molimo pokušajte ponovo.');
                setAlertType('danger');
                setShowAlert(true);
                return;
            }
            try {
                const existingApp = appointments.find(app => app.id === appointmentId);
                if (!existingApp) {
                    setAlertMessage("Pregled nije pronađen za otkazivanje.");
                    setAlertType('danger');
                    setShowAlert(true);
                    return;
                }

                const cancelPayload = {
                    pregledID: existingApp.id,
                    pacijentID: user.pacijentId,
                    doktorID: existingApp.doktorID,
                    terminID: existingApp.terminID,
                    datumPregleda: existingApp.datumPregleda,
                    vrijemePregleda: existingApp.vrijemePregleda,
                    komentarPacijenta: existingApp.komentarPacijenta,
                    status: 'otkazan'
                };

                await axios.put(`${TERMINI_PREGLEDI_API_PREFIX}/api/pregledi/${appointmentId}`, cancelPayload, authHeaders);
                setAlertMessage('Pregled uspješno otkazan!');
                setAlertType('success');
                setShowAlert(true);
                fetchPatientAppointments();
            } catch (error) {
                console.error('Greška prilikom otkazivanja pregleda:', error.response?.data || error.message);
                setAlertMessage('Neuspješno otkazivanje pregleda. Molimo pokušajte ponovo. ' + (error.response?.data?.message || error.message));
                setAlertType('danger');
                setShowAlert(true);
            }
        }
    };

    // --- Profile Update ---
    const handleProfileChange = (e) => {
        const { name, value } = e.target;
        setPatientProfile(prev => ({ ...prev, [name]: value }));
    };

    const handleSaveProfile = async () => {
        if (!user || !user.pacijentId || !user.userId) {
            setAlertMessage('ID pacijenta ili Korisnički ID nije dostupan. Nije moguće sačuvati profil.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }

        try {
            const patientDetailsPayload = {
                pacijentID: user.pacijentId,
                ime: patientProfile.firstName,
                prezime: patientProfile.lastName,
                email: patientProfile.email,
                telefon: patientProfile.phone,
                datumRodjenja: patientProfile.datumRodjenja,
                adresa: patientProfile.adresa,
                userId: user.userId
            };
            await axios.put(`${KORISNICI_DOKTORI_API_PREFIX}/api/pacijenti/${user.pacijentId}`, patientDetailsPayload, authHeaders);

            const authProfilePayload = {
                email: patientProfile.email,
                fullName: `${patientProfile.firstName} ${patientProfile.lastName}`,
                telefon: patientProfile.phone,
            };
            await axios.put(`${AUTH_API_PREFIX}/users/${user.userId}`, authProfilePayload, authHeaders);


            setAlertMessage('Profil uspješno ažuriran!');
            setAlertType('success');
            setShowAlert(true);
            fetchPatientProfile();
        } catch (error) {
            console.error('Greška prilikom ažuriranja profila:', error.response?.data || error.message);
            setAlertMessage('Neuspješno ažuriranje profila. Molimo pokušajte ponovo. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    // --- Document Upload ---
    const handleDocumentUpload = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        const formData = new FormData();
        formData.append('file', file);
        formData.append('pacijentID', user.pacijentId);
        formData.append('tipDokumenta', 'Patient Upload');

        try {
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/upload`, formData, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data'
                }
            });
            setAlertMessage('Dokument uspješno prenesen!');
            setAlertType('success');
            setShowAlert(true);
            fetchDocuments();
        } catch (error) {
            console.error('Greška prilikom prijenosa dokumenta:', error.response?.data || error.message);
            setAlertMessage('Neuspješan prijenos dokumenta. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    // --- Secure Messaging ---
    const handleNewMessageChange = (e) => {
        const { name, value } = e.target;
        setNewMessage(prev => ({ ...prev, [name]: value }));
    };

    const handleReplyMessageChange = (e) => {
        setReplyMessage(prev => ({ ...prev, content: e.target.value }));
    };

    const handleSendMessage = async () => {
        if (!user || !user.pacijentId) {
            setAlertMessage('ID pacijenta nije dostupan. Nije moguće poslati poruku.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }
        if (!newMessage.recipientId || !newMessage.subject || !newMessage.content) {
            setAlertMessage('Molimo popunite sva polja za poruku.');
            setAlertType('warning');
            setShowAlert(true);
            return;
        }

        try {
            const messageData = {
                senderId: user.pacijentId,
                senderType: 'PACIJENT',
                receiverId: parseInt(newMessage.recipientId),
                receiverType: 'DOKTOR',
                subject: newMessage.subject,
                content: newMessage.content
            };
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/posalji`, messageData, authHeaders);
            setAlertMessage('Poruka uspješno poslana!');
            setAlertType('success');
            setShowAlert(true);
            setNewMessage({ recipientId: '', subject: '', content: '' });
            fetchMessages();
        } catch (error) {
            console.error('Greška prilikom slanja poruke:', error.response?.data || error.message);
            setAlertMessage('Neuspješno slanje poruke. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const handleReply = async (messageId) => {
        if (!user || !user.pacijentId) {
            setAlertMessage('ID pacijenta nije dostupan. Nije moguće odgovoriti.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }
        if (!replyMessage.content) {
            setAlertMessage('Sadržaj odgovora ne može biti prazan.');
            setAlertType('warning');
            setShowAlert(true);
            return;
        }
        try {
            await axios.post(`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/poruke/${messageId}/odgovori`, {
                senderId: user.pacijentId,
                senderType: 'PACIJENT',
                content: replyMessage.content
            }, authHeaders);
            setAlertMessage('Odgovor uspješno poslan!');
            setAlertType('success');
            setShowAlert(true);
            setReplyMessage({ messageId: '', content: '' });
            fetchMessages();
            setSelectedMessage(prev => ({
                ...prev,
                replies: [...(prev.replies || []), {
                    senderId: user.pacijentId,
                    senderIme: user.fullName,
                    senderType: 'PACIJENT',
                    content: replyMessage.content,
                    timestamp: new Date().toISOString()
                }]
            }));
        } catch (error) {
            console.error('Greška prilikom slanja odgovora:', error.response?.data || error.message);
            setAlertMessage('Neuspješno slanje odgovora. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };

    const handleViewMessage = (message) => {
        setSelectedMessage(message);
    };

    const closeMessageModal = () => {
        setSelectedMessage(null);
        setReplyMessage({ messageId: '', content: '' });
    };

    // --- Funkcije za ocjenjivanje ---
    const openRatingModal = (doktorID, doktorIme) => {
        setSelectedDoctorForRating({ id: doktorID, name: doktorIme });
        setRatingData({ ocjena: 5, komentar: '' });
        setShowRatingModal(true);
    };

    const closeRatingModal = () => {
        setShowRatingModal(false);
        setSelectedDoctorForRating(null);
        setRatingData({ ocjena: 5, komentar: '' });
    };

    const handleRatingChange = (e) => {
        const { name, value } = e.target;
        setRatingData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmitRating = async () => {
        if (!user || !user.pacijentId) {
            setAlertMessage('ID pacijenta nije dostupan. Nije moguće poslati ocjenu.');
            setAlertType('danger');
            setShowAlert(true);
            return;
        }
        if (!selectedDoctorForRating || ratingData.ocjena === null) { // Provjeri da ocjena nije null
            setAlertMessage('Molimo odaberite ocjenu.');
            setAlertType('warning');
            setShowAlert(true);
            return;
        }

        try {
            const ratingPayload = {
                pacijentID: user.pacijentId,
                doktorID: selectedDoctorForRating.id,
                ocjena: parseFloat(ratingData.ocjena), // KLJUČNA PROMJENA: Šaljemo kao Double
                komentar: ratingData.komentar
                // Datum ocjene se postavlja na backendu prilikom spremanja (u OcjenaDoktoraService)
            };

            // KLJUČNA PROMJENA: Pozivamo pravi endpoint za ocjene u KORISNICI_DOKTORI_API_PREFIX
            // Endpoint je /api/ocjene, ne /api/doktori/ocijeni
            await axios.post(`${KORISNICI_DOKTORI_API_PREFIX}/api/ocjene`, ratingPayload, authHeaders);

            setAlertMessage(`Ocjena uspješno poslana za doktora ${selectedDoctorForRating.name}!`);
            setAlertType('success');
            setShowAlert(true);
            closeRatingModal();
            // Ažuriraj liste da se vide promjene ocjene ili termini
            fetchPatientAppointments(); // Možda osvježiti listu pregleda
            fetchDoctors(); // Obavezno osvježiti listu doktora da se prikaže nova prosječna ocjena
        } catch (error) {
            console.error('Greška prilikom slanja ocjene:', error.response?.data || error.message);
            setAlertMessage('Neuspješno slanje ocjene. ' + (error.response?.data?.message || error.message));
            setAlertType('danger');
            setShowAlert(true);
        }
    };


    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
                <Spinner animation="border" role="status">
                    <span className="visually-hidden">Učitavanje...</span>
                </Spinner>
                <p className="ms-2">Učitavanje podataka kontrolne ploče...</p>
            </div>
        );
    }

    return (
        <div className="d-flex" style={{ minHeight: '100vh' }}>
            {/* Sidebar */}
            <div className="bg-dark text-white p-3" style={{ width: '250px' }}>
                <h4 className="mb-4">Kontrolna ploča pacijenta</h4>
                <ul className="nav flex-column">
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'overview' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('overview')}
                        >
                            Pregled
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'myAppointments' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('myAppointments')}
                        >
                            Moji pregledi
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'medicalHistory' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('medicalHistory')}
                        >
                            Medicinska historija
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'documents' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('documents')}
                        >
                            Moji dokumenti
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'billing' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('billing')}
                        >
                            Naplata i plaćanja
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'messages' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('messages')}
                        >
                            Poruke
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'doctorSearch' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('doctorSearch')}
                        >
                            Pronađi doktora
                        </button>
                    </li>
                    <li className="nav-item mb-2">
                        <button
                            className={`btn btn-link text-white text-decoration-none w-100 text-start ${activeTab === 'profileSettings' ? 'active bg-secondary rounded' : ''}`}
                            onClick={() => setActiveTab('profileSettings')}
                        >
                            Postavke profila
                        </button>
                    </li>
                </ul>
            </div>

            {/* Main Content Area */}
            <div className="flex-grow-1 p-4 bg-light">
                {showAlert && (
                    <Alert variant={alertType} onClose={() => setShowAlert(false)} dismissible className="position-fixed top-0 start-50 translate-middle-x mt-3" style={{ zIndex: 1050 }}>
                        {alertMessage}
                    </Alert>
                )}

                <div className="d-flex justify-content-between align-items-center mb-4">
                    <h2 className="mb-0">Kontrolna ploča pacijenta</h2>
                </div>

                {/* Content based on activeTab */}
                {activeTab === 'overview' && (
                    <div>
                        <h3>Dobrodošli, {patientProfile.firstName || (user ? user.fullName : 'Pacijent')}!</h3>
                        <p>Ovo je vaš pregled pacijenta. Ovdje možete brzo vidjeti svoje nadolazeće preglede i nedavne aktivnosti.</p>
                        <div className="row mt-4">
                            <div className="col-md-6">
                                <div className="card">
                                    <div className="card-header">
                                        Nadolazeći pregledi
                                    </div>
                                    <ul className="list-group list-group-flush">
                                        {appointments.filter(app => app.status === 'zakazan' || app.status === 'potvrđen').length > 0 ? (
                                            appointments.filter(app => app.status === 'zakazan' || app.status === 'potvrđen').map(app => (
                                                <li key={app.id} className="list-group-item d-flex justify-content-between align-items-center">
                                                    <div>
                                                        <strong>{app.datumPregleda} u {app.vrijemePregleda}</strong>
                                                        <br />
                                                        <small>Razlog: {app.komentarPacijenta}</small>
                                                        <br />
                                                        <small>Doktor: {app.doktorIme}</small>
                                                    </div>
                                                    <span className={`badge bg-${app.status === 'potvrđen' ? 'success' : 'warning'}`}>{app.status}</span>
                                                </li>
                                            ))
                                        ) : (
                                            <li className="list-group-item">Nema nadolazećih pregleda.</li>
                                        )}
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {activeTab === 'myAppointments' && (
                    <div>
                        <h3>Moji pregledi</h3>
                        <Button variant="primary" className="mb-3" onClick={() => openAppointmentModal()}>
                            Zakaži novi pregled
                        </Button>
                        {appointments.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Doktor</th>
                                        <th>Datum</th>
                                        <th>Vrijeme</th>
                                        <th>Razlog</th>
                                        <th>Status</th>
                                        <th>Akcije</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {appointments.map(app => (
                                        <tr key={app.id}>
                                            <td>{app.doktorIme}</td>
                                            <td>{app.datumPregleda}</td>
                                            <td>{app.vrijemePregleda}</td>
                                            <td>{app.komentarPacijenta}</td>
                                            <td><span className={`badge bg-${app.status === 'obavljen' ? 'success' : app.status === 'zakazan' ? 'warning' : 'danger'}`}>{app.status}</span></td>
                                            <td>
                                                {app.status === 'zakazan' || app.status === 'potvrđen' ? (
                                                    <Button variant="danger" size="sm" onClick={() => handleCancelAppointment(app.id)}>Otkaži</Button>
                                                ) : app.status === 'obavljen' ? (
                                                    <Button variant="success" size="sm" onClick={() => openRatingModal(app.doktorID, app.doktorIme)}>Ocijeni</Button>
                                                ) : (
                                                    <small>Nema dostupnih akcija</small>
                                                )}
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>Nema pronađene historije pregleda. Zakažite svoj prvi pregled!</p>
                        )}
                    </div>
                )}

                {activeTab === 'medicalHistory' && (
                    <div>
                        <h3>Moja medicinska historija</h3>
                        {medicalHistory.length > 0 ? (
                            <ul className="list-group">
                                {medicalHistory.map((record, index) => (
                                    <li key={record.id} className="list-group-item">
                                        <strong>Datum:</strong> {record.date} <br />
                                        <strong>Dijagnoza:</strong> {record.diagnosis} <br />
                                        <strong>Liječenje:</strong> {record.treatment} <br />
                                        <strong>Doktor:</strong> {record.doctorName} <br />
                                        <strong>Napomene:</strong> {record.notes || 'N/A'}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>Nema pronađenih zapisa medicinske historije.</p>
                        )}
                    </div>
                )}

                {activeTab === 'documents' && (
                    <div>
                        <h3>Moji dokumenti</h3>
                        <Form.Group controlId="formFile" className="mb-3">
                            <Form.Label>Prenesi novi dokument</Form.Label>
                            <Form.Control type="file" onChange={handleDocumentUpload} />
                        </Form.Group>

                        {documents.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Naziv dokumenta</th>
                                        <th>Tip</th>
                                        <th>Datum prijenosa</th>
                                        <th>Pristup</th>
                                        <th>Akcije</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {documents.map(doc => (
                                        <tr key={doc.id}>
                                            <td>{doc.fileName}</td>
                                            <td>{doc.documentType}</td>
                                            <td>{new Date(doc.uploadDate).toLocaleDateString()}</td>
                                            <td><span className={`badge bg-${doc.pristup === 'JAVNA' ? 'success' : 'secondary'}`}>{doc.pristup}</span></td>
                                            <td>
                                                <Button variant="success" size="sm" href={`${OBAVIJESTI_DOKUMENTI_API_PREFIX}/api/dokumentacija/download/${doc.id}`} target="_blank">Preuzmi</Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>Nema pronađenih dokumenata.</p>
                        )}
                    </div>
                )}

                {activeTab === 'billing' && (
                    <div>
                        <h3>Naplata i plaćanja</h3>
                        {bills.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>ID računa</th>
                                        <th>Datum izdavanja</th>
                                        <th>Iznos</th>
                                        <th>Status</th>
                                        <th>Opis</th>
                                        <th>Izdao</th>
                                        <th>Akcije</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {bills.map(bill => (
                                        <tr key={bill.id}>
                                            <td>{bill.id}</td>
                                            <td>{new Date(bill.billDate).toLocaleDateString()}</td>
                                            <td>{bill.amount ? bill.amount.toFixed(2) : 'N/A'} KM</td>
                                            <td><span className={`badge bg-${bill.status === 'PLACEN' ? 'success' : 'warning'}`}>{bill.status}</span></td>
                                            <td>{bill.opis}</td>
                                            <td>{bill.doktorIme}</td>
                                            <td>
                                                {bill.status === 'NEPLACEN' && (
                                                    <Button variant="success" size="sm" className="me-2">Plati sada</Button>
                                                )}
                                                <Button variant="info" size="sm">Pogledaj detalje</Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>Nema pronađenih zapisa o naplati.</p>
                        )}
                    </div>
                )}

                {activeTab === 'messages' && (
                    <div>
                        <h3>Moje poruke</h3>
                        <Button variant="primary" className="mb-3" onClick={() => setNewMessage({ recipientId: '', subject: '', content: '' })}>Sastavi novu poruku</Button>

                        {messages.length > 0 ? (
                            <ul className="list-group">
                                {messages.map(msg => (
                                    <li key={msg.id} className="list-group-item d-flex justify-content-between align-items-center">
                                        <div>
                                            <strong>Od: {msg.senderIme || (msg.senderType === 'DOKTOR' ? `Doktor (${msg.senderId})` : 'Ja')}</strong> <br />
                                            <strong>Naslov:</strong> {msg.subject} <br />
                                            <small>{new Date(msg.timestamp).toLocaleString()}</small>
                                        </div>
                                        <Button variant="info" size="sm" onClick={() => handleViewMessage(msg)}>Pogledaj poruku</Button>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>Nema pronađenih poruka.</p>
                        )}

                        <hr className="my-4" />

                        <h4>Sastavi novu poruku</h4>
                        <Form>
                            <Form.Group className="mb-3">
                                <Form.Label>Primalac (Doktor)</Form.Label>
                                <Form.Control
                                    as="select"
                                    name="recipientId"
                                    value={newMessage.recipientId}
                                    onChange={handleNewMessageChange}
                                    required
                                >
                                    <option value="">-- Odaberite doktora --</option>
                                    {doctors.map(doctor => (
                                        <option key={doctor.id} value={doctor.id}>{doctor.firstName} {doctor.lastName}</option>
                                    ))}
                                </Form.Control>
                            </Form.Group>
                            <Form.Group className="mb-3">
                                <Form.Label>Naslov</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="subject"
                                    value={newMessage.subject}
                                    onChange={handleNewMessageChange}
                                    required
                                />
                            </Form.Group>
                            <Form.Group className="mb-3">
                                <Form.Label>Sadržaj</Form.Label>
                                <Form.Control
                                    as="textarea"
                                    rows={3}
                                    name="content"
                                    value={newMessage.content}
                                    onChange={handleNewMessageChange}
                                    required
                                />
                            </Form.Group>
                            <Button variant="primary" onClick={handleSendMessage}>Pošalji poruku</Button>
                        </Form>
                    </div>
                )}

                {activeTab === 'doctorSearch' && (
                    <div>
                        <h3>Pronađi doktora</h3>
                        <p>Pretražite doktore po imenu ili specijalnosti kako biste pronašli pravu njegu za vas.</p>
                        <Form.Group className="mb-3">
                            <Form.Control type="text" placeholder="Pretraži po imenu ili specijalnosti..." />
                        </Form.Group>
                        {doctors.length > 0 ? (
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Ime</th>
                                        <th>Specijalnost</th>
                                        <th>Kontakt</th>
                                        <th>Ocjena</th>
                                        <th>Akcije</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {doctors.map(doctor => (
                                        <tr key={doctor.id}>
                                            <td>{doctor.firstName} {doctor.lastName}</td>
                                            <td>{doctor.specialty || 'Opšti praktičar'}</td>
                                            <td>{doctor.email} | {doctor.phone}</td>
                                            <td>{doctor.ocjena ? doctor.ocjena.toFixed(1) : 'N/A'}</td>
                                            <td>
                                                <Button variant="primary" size="sm" onClick={() => {
                                                    // Proslijeđujemo doctor.id u openAppointmentModal za automatsko učitavanje termina
                                                    openAppointmentModal(null, doctor.id);
                                                }}>Zakaži pregled</Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>Nema pronađenih doktora.</p>
                        )}
                    </div>
                )}

                {activeTab === 'profileSettings' && (
                    <div>
                        <h3>Postavke profila</h3>
                        <Form>
                            <Form.Group className="mb-3" controlId="formFirstName">
                                <Form.Label>Ime</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="firstName"
                                    value={patientProfile.firstName || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formLastName">
                                <Form.Label>Prezime</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="lastName"
                                    value={patientProfile.lastName || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formEmail">
                                <Form.Label>Email</Form.Label>
                                <Form.Control
                                    type="email"
                                    name="email"
                                    value={patientProfile.email || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formPhone">
                                <Form.Label>Telefon</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="phone"
                                    value={patientProfile.phone || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formAddress">
                                <Form.Label>Adresa</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="adresa"
                                    value={patientProfile.adresa || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formDatumRodjenja">
                                <Form.Label>Datum rođenja</Form.Label>
                                <Form.Control
                                    type="date"
                                    name="datumRodjenja"
                                    value={patientProfile.datumRodjenja || ''}
                                    onChange={handleProfileChange}
                                />
                            </Form.Group>
                            <Button variant="primary" onClick={handleSaveProfile}>
                                Sačuvaj profil
                            </Button>
                        </Form>
                    </div>
                )}
            </div>

            {/* Appointment Modal */}
            <Modal show={showAppointmentModal} onHide={closeAppointmentModal}>
                <Modal.Header closeButton>
                    <Modal.Title>{editingAppointment ? 'Uredi pregled' : 'Zakaži novi pregled'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3" controlId="formDoctor">
                            <Form.Label>Odaberite doktora</Form.Label>
                            <Form.Control
                                as="select"
                                name="doctorId"
                                value={newAppointment.doctorId}
                                onChange={handleNewAppointmentChange}
                                required
                            >
                                <option value="">-- Odaberite doktora --</option>
                                {doctors.map(doctor => (
                                    <option key={doctor.id} value={doctor.id}>{doctor.firstName} {doctor.lastName}</option>
                                ))}
                            </Form.Control>
                        </Form.Group>

                        {newAppointment.doctorId && (
                            <Form.Group className="mb-3" controlId="formAvailableTerm">
                                <Form.Label>Odaberite slobodan termin</Form.Label>
                                {loading ? (
                                    <Spinner animation="border" size="sm" />
                                ) : allDoctorAvailableTerms.length > 0 ? (
                                    <Form.Control
                                        as="select"
                                        name="terminId"
                                        value={newAppointment.terminId}
                                        onChange={handleNewAppointmentChange}
                                        required
                                    >
                                        <option value="">-- Odaberite datum i vrijeme --</option>
                                        {allDoctorAvailableTerms.map(term => (
                                            <option key={term.terminID} value={term.terminID}>{term.display}</option>
                                        ))}
                                    </Form.Control>
                                ) : (
                                    <Alert variant="info">Nema dostupnih termina za ovog doktora.</Alert>
                                )}
                            </Form.Group>
                        )}

                        {/* Polja za datum i vrijeme koja će se automatski popuniti nakon odabira termina (mogu biti samo za prikaz) */}
                        {newAppointment.terminId && (
                            <>
                                <Form.Group className="mb-3" controlId="formAppointmentDateDisplay">
                                    <Form.Label>Odabrani datum</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={newAppointment.appointmentDate}
                                        readOnly
                                        disabled
                                    />
                                </Form.Group>
                                <Form.Group className="mb-3" controlId="formAppointmentTimeDisplay">
                                    <Form.Label>Odabrano vrijeme</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={newAppointment.appointmentTime ? newAppointment.appointmentTime.substring(0, 5) : ''}
                                        readOnly
                                        disabled
                                    />
                                </Form.Group>
                            </>
                        )}

                        <Form.Group className="mb-3" controlId="formReason">
                            <Form.Label>Razlog za pregled</Form.Label>
                            <Form.Control
                                as="textarea"
                                rows={3}
                                name="reason"
                                value={newAppointment.reason}
                                onChange={handleNewAppointmentChange}
                                placeholder="npr. Opšti pregled, specifični simptomi..."
                                required
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeAppointmentModal}>
                        Zatvori
                    </Button>
                    <Button
                        variant="primary"
                        onClick={handleScheduleAppointment}
                        disabled={!newAppointment.doctorId || !newAppointment.terminId || !newAppointment.reason || loading}
                    >
                        {editingAppointment ? 'Ažuriraj pregled' : 'Zakaži pregled'}
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* Message Detail Modal */}
            <Modal show={!!selectedMessage} onHide={closeMessageModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Poruka: {selectedMessage?.subject}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {selectedMessage && (
                        <>
                            <p><strong>Od:</strong> {selectedMessage.senderIme || (selectedMessage.senderType === 'DOKTOR' ? `Doktor (${selectedMessage.senderId})` : 'Ja')}</p>
                            <p><strong>Datum:</strong> {new Date(selectedMessage.timestamp).toLocaleString()}</p>
                            <hr />
                            <p>{selectedMessage.content}</p>

                            {selectedMessage.replies && selectedMessage.replies.length > 0 && (
                                <div className="mt-4">
                                    <h5>Odgovori:</h5>
                                    {selectedMessage.replies.map((reply, index) => (
                                        <div key={index} className="border p-2 mb-2 rounded bg-light">
                                            <strong>{reply.senderIme || (reply.senderType === 'DOKTOR' ? `Doktor (${reply.senderId})` : 'Ja')}:</strong> {reply.content}
                                            <br />
                                            <small>{new Date(reply.timestamp).toLocaleString()}</small>
                                        </div>
                                    ))}
                                </div>
                            )}

                            <Form.Group className="mt-4">
                                <Form.Label>Odgovor</Form.Label>
                                <Form.Control
                                    as="textarea"
                                    rows={2}
                                    value={replyMessage.content}
                                    onChange={handleReplyMessageChange}
                                    placeholder="Upišite vaš odgovor ovdje..."
                                />
                            </Form.Group>
                            <Button variant="primary" className="mt-2" onClick={() => handleReply(selectedMessage.id)}>Pošalji odgovor</Button>
                        </>
                    )}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeMessageModal}>
                        Zatvori
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* Rating Modal */}
            <Modal show={showRatingModal} onHide={closeRatingModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Ocijeni doktora: {selectedDoctorForRating?.name}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3" controlId="formRating">
                            <Form.Label>Ocjena (1-5)</Form.Label>
                            <Form.Control
                                as="select"
                                name="ocjena"
                                value={ratingData.ocjena}
                                onChange={handleRatingChange}
                                required
                            >
                                <option value="1">1 - Vrlo loše</option>
                                <option value="2">2 - Loše</option>
                                <option value="3">3 - Prosječno</option>
                                <option value="4">4 - Dobro</option>
                                <option value="5">5 - Izvrsno</option>
                            </Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formRatingComment">
                            <Form.Label>Komentar (opcionalno)</Form.Label>
                            <Form.Control
                                as="textarea"
                                rows={3}
                                name="komentar"
                                value={ratingData.komentar}
                                onChange={handleRatingChange}
                                placeholder="Napišite komentar o svom iskustvu..."
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeRatingModal}>
                        Zatvori
                    </Button>
                    <Button variant="primary" onClick={handleSubmitRating}>
                        Pošalji ocjenu
                    </Button>
                </Modal.Footer>
            </Modal>

        </div>
    );
}

export default PatientDashboard;
