import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios'; // Uvezi axios
import hospitalImage from '../images/hospital.jpg';

const GATEWAY_BASE_URL = 'http://localhost:8081';
const AUTH_API_PREFIX = `${GATEWAY_BASE_URL}/auth`;

function ContactUsPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [submitMessage, setSubmitMessage] = useState('');
  const [submitMessageType, setSubmitMessageType] = useState('success');

  const contactInfo = {
    address: 'Zmaja od Bosne, 71000 Sarajevo, Bosna i Hercegovina',
    phone: '+387 33 123 456',
    email: 'info@winxcare.com', // Ovaj email će biti primalac poruka sa forme
    workingHours: 'Ponedjeljak - Petak: 08:00 - 17:00'
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitMessage(''); // Clear previous message
    setSubmitMessageType('success');

    try {
      const payload = {
        name,
        email,
        subject,
        message,
      };

      // Šaljemo podatke na novi /auth/contact endpoint
      const response = await axios.post(`${AUTH_API_PREFIX}/contact`, payload);

      setSubmitMessage(response.data); // Poruka od backenda (npr. "Vaša poruka je uspješno poslana.")
      setSubmitMessageType('success');
      setName('');
      setEmail('');
      setSubject('');
      setMessage('');
    } catch (error) {
      console.error('Greška prilikom slanja kontakt forme:', error.response?.data || error.message);
      setSubmitMessage(`Greška prilikom slanja poruke: ${error.response?.data || error.message}`);
      setSubmitMessageType('danger');
    }
  };

  return (
    <div className="container my-5">
      <div className="card shadow-lg p-4">
        <div className="card-body">
          <h2 className="card-title text-center mb-4 text-danger">Kontaktirajte nas</h2>
          <p className="text-center text-muted mb-5">
            Imate pitanje ili vam je potrebna pomoć? Slobodno nam se obratite putem forme ispod.
          </p>

          <div className="row mb-5 align-items-center">
            <div className="col-md-6">
              <img src={hospitalImage} alt="WinxCare Clinic" className="img-fluid rounded shadow-sm mb-3 mb-md-0" />
            </div>
            <div className="col-md-6">
              <h4 className="text-danger mb-3">Naše informacije</h4>
              <p className="text-muted mb-1"><strong>Adresa:</strong> {contactInfo.address}</p>
              <p className="text-muted mb-1"><strong>Telefon:</strong> {contactInfo.phone}</p>
              <p className="text-muted mb-1"><strong>Email:</strong> {contactInfo.email}</p>
              <p className="text-muted mb-1"><strong>Radno Vrijeme:</strong> {contactInfo.workingHours}</p>
            </div>
          </div>

          <hr className="my-5" />

          <h3 className="text-center mb-4 text-dark">Pošaljite nam poruku</h3>
          {submitMessage && (
            <div className={`alert alert-${submitMessageType} text-center mb-4`} role="alert">
              {submitMessage}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="nameInput" className="form-label">Vaše ime</label>
              <input
                type="text"
                className="form-control"
                id="nameInput"
                placeholder="Puno ime"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="emailInput" className="form-label">Email adresa</label>
              <input
                type="email"
                className="form-control"
                id="emailInput"
                placeholder="ime@primjer.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="subjectInput" className="form-label">Naslov</label>
              <input
                type="text"
                className="form-control"
                id="subjectInput"
                placeholder="Naslov vaše poruke"
                value={subject}
                onChange={(e) => setSubject(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="messageTextarea" className="form-label">Vaša poruka</label>
              <textarea
                className="form-control"
                id="messageTextarea"
                rows="5"
                placeholder="Ovdje ukucajte svoju poruku..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              ></textarea>
            </div>
            <div className="d-grid gap-2">
              <button type="submit" className="btn btn-danger btn-lg">Pošalji poruku</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default ContactUsPage;
