import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/LoginPage.css'; // Dodajte CSS fajl za login stranicu ako imate

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { login, hasRole } = useAuth(); // Dohvati login funkciju i hasRole iz AuthContext-a

  const GATEWAY_BASE_URL = 'http://localhost:8081';
  const AUTH_API_LOGIN = `${GATEWAY_BASE_URL}/auth/login`;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post(AUTH_API_LOGIN, { email, password });
      const { token, roles, doktorId, pacijentId, ime, prezime } = response.data; // Pretpostavljam da backend šalje doktorId/pacijentId i ime/prezime

      const fullName = `${ime} ${prezime}`;
      let idToStore = null;

      if (roles && roles.includes('ROLE_DOKTOR') && doktorId) {
        idToStore = doktorId;
      } else if (roles && roles.includes('ROLE_PATIENT') && pacijentId) {
        idToStore = pacijentId;
      }

      // Pozovi login funkciju iz AuthContext-a sa svim potrebnim podacima
      login(token, roles, email, fullName, idToStore);

      // Redirekcija na osnovu uloge nakon uspješne prijave
      if (roles && roles.includes('ROLE_ADMIN')) {
        navigate('/admin-dashboard');
      } else if (roles && roles.includes('ROLE_DOKTOR')) {
        navigate('/doctor-dashboard'); // OBAVEZNO: Navigacija na fiksnu rutu bez ID-a
      } else if (roles && roles.includes('ROLE_PATIENT')) {
        navigate('/patient-dashboard');
      } else {
        navigate('/'); // Default redirekcija za nepoznate uloge
      }

    } catch (err) {
      console.error("Login error:", err);
      if (err.response) {
        setError(err.response.data.message || 'Neuspješna prijava. Provjerite email i lozinku.');
      } else {
        setError('Nešto je pošlo po zlu. Molimo pokušajte ponovo.');
      }
    }
  };

  return (
    <div className="login-page-container">
      <div className="login-form-card">
        <h2>Prijavi se</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="emailInput" className="form-label">Email adresa</label>
            <input
              type="email"
              className="form-control"
              id="emailInput"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="passwordInput" className="form-label">Lozinka</label>
            <input
              type="password"
              className="form-control"
              id="passwordInput"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary w-100">Prijavi se</button>
          <div className="mt-3 text-center">
            <p>Nemaš račun? <Link to="/sign-up">Registruj se</Link></p>
            <p><Link to="/forgot-password">Zaboravili ste lozinku?</Link></p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
